INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7944, 'nvi', '1 Samuel', '1sm', 28, 1, 'Naqueles dias os filisteus reuniram suas tropas para lutar contra Israel. Aquis disse a Davi: "Você deve saber que você e seus soldados me acompanharão no exército".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7945, 'nvi', '1 Samuel', '1sm', 28, 2, 'Disse Davi a Aquis: "Então tu saberás o que teu servo é capaz de fazer". Aquis respondeu-lhe: "Então, o colocarei como minha guarda pessoal permanente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7946, 'nvi', '1 Samuel', '1sm', 28, 3, 'Samuel já havia morrido, e todo o Israel o havia pranteado e sepultado em Ramá, sua cidade natal. Saul havia expulsado do país os médiuns e os espíritas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7947, 'nvi', '1 Samuel', '1sm', 28, 4, 'Depois que os filisteus se reuniram, vieram e acamparam em Suném, enquanto Saul reunia todos os israelitas e acampava em Gilboa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7948, 'nvi', '1 Samuel', '1sm', 28, 5, 'Quando Saul viu o acampamento filisteu, teve medo; ficou apavorado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7949, 'nvi', '1 Samuel', '1sm', 28, 6, 'Ele consultou o Senhor, mas este não lhe respondeu nem por sonhos nem por Urim nem por profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7950, 'nvi', '1 Samuel', '1sm', 28, 7, 'Então Saul disse aos seus auxiliares: "Procurem uma mulher que invoca espíritos, para que eu a consulte". Eles disseram: "Existe uma em En-Dor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7951, 'nvi', '1 Samuel', '1sm', 28, 8, 'Saul então se disfarçou, vestindo outras roupas, e foi à noite, com dois homens, até a casa da mulher. Ele disse a ela: "Invoque um espírito para mim, fazendo subir aquele cujo nome eu disser".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7952, 'nvi', '1 Samuel', '1sm', 28, 9, 'A mulher, porém, lhe disse: "Certamente você sabe o que Saul fez. Ele eliminou os médiuns e os espíritas da terra de Israel. Por que você está preparando uma armadilha contra mim que me levará à morte? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7953, 'nvi', '1 Samuel', '1sm', 28, 10, 'Saul jurou-lhe pelo Senhor: "Juro pelo nome do Senhor que você não será punida por isso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7954, 'nvi', '1 Samuel', '1sm', 28, 11, '"Quem devo fazer subir? ", perguntou a mulher. Ele respondeu: "Samuel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7955, 'nvi', '1 Samuel', '1sm', 28, 12, 'Quando a mulher viu Samuel, gritou e disse a Saul: "Por que me enganaste? Tu mesmo és Saul! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7956, 'nvi', '1 Samuel', '1sm', 28, 13, 'O rei lhe disse: "Não tenha medo. O que você está vendo? " A mulher disse a Saul: "Vejo um ser que sobe do chão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7957, 'nvi', '1 Samuel', '1sm', 28, 14, 'Ele perguntou: "Qual a aparência dele? " E disse ela: "Um ancião vestindo um manto está subindo". Então Saul ficou sabendo que era Samuel, inclinou-se e prostrou-se, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7958, 'nvi', '1 Samuel', '1sm', 28, 15, 'Samuel perguntou a Saul: "Por que você me perturbou, fazendo-me subir? " Respondeu Saul: "Estou muito angustiado. Os filisteus estão me atacando e Deus se afastou de mim. Ele já não responde nem por profetas nem por sonhos; por isso o chamei para dizer-me o que fazer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7959, 'nvi', '1 Samuel', '1sm', 28, 16, 'Disse Samuel: "Por que você me chamou, já que o Senhor se afastou de você e se tornou seu inimigo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7960, 'nvi', '1 Samuel', '1sm', 28, 17, 'O Senhor fez o que predisse por meu intermédio: rasgou de suas mãos o reino e o deu a seu próximo, a Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7961, 'nvi', '1 Samuel', '1sm', 28, 18, 'Porque você não obedeceu ao Senhor nem executou a grande ira dele contra os amalequitas, ele lhe faz isso hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7962, 'nvi', '1 Samuel', '1sm', 28, 19, 'O Senhor entregará você e o povo de Israel nas mãos dos filisteus, e amanhã você e seus filhos estarão comigo. O Senhor também entregará o exército de Israel nas mãos dos filisteus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7963, 'nvi', '1 Samuel', '1sm', 28, 20, 'Na mesma hora Saul caiu estendido no chão, aterrorizado pelas palavras de Samuel. Suas forças haviam se esgotado, pois ele tinha passado todo aquele dia e toda aquela noite sem comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7964, 'nvi', '1 Samuel', '1sm', 28, 21, 'Quando a mulher se aproximou de Saul e viu que ele estava profundamente perturbado, disse: "Olha, tua serva te obedeceu. Arrisquei minha vida e fiz o que me ordenaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7965, 'nvi', '1 Samuel', '1sm', 28, 22, 'Agora, por favor, ouve tua serva e come um pouco para que tenhas forças para seguir teu caminho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7966, 'nvi', '1 Samuel', '1sm', 28, 23, 'Ele recusou e disse: "Não vou comer". Seus homens, porém, insistiram com ele, e a mulher também; e ele os atendeu. Ele se levantou do chão e sentou-se na cama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7967, 'nvi', '1 Samuel', '1sm', 28, 24, 'A mulher matou depressa um bezerro gordo que tinha em casa; apanhou um pouco de farinha, amassou-a e assou pão sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7968, 'nvi', '1 Samuel', '1sm', 28, 25, 'Então ela serviu a Saul e a seus homens, e eles comeram. E naquela mesma noite eles partiram.');
