INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7277, 'nvi', '1 Samuel', '1sm', 3, 1, 'O menino Samuel ministrava perante o Senhor, sob a direção de Eli; naqueles dias raramente o Senhor falava, e as visões não eram freqüentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7278, 'nvi', '1 Samuel', '1sm', 3, 2, 'Certa noite, Eli, cujos olhos estavam ficando tão fracos que já não conseguia mais enxergar, estava deitado em seu lugar de costume.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7279, 'nvi', '1 Samuel', '1sm', 3, 3, 'A lâmpada de Deus ainda não havia se apagado, e Samuel estava deitado no santuário do Senhor, onde se encontrava a arca de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7280, 'nvi', '1 Samuel', '1sm', 3, 4, 'Então o Senhor chamou Samuel. Samuel respondeu: "Estou aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7281, 'nvi', '1 Samuel', '1sm', 3, 5, 'E correu até Eli e disse: "Estou aqui; o senhor me chamou? " Eli, porém, disse: "Não o chamei; volte e deite-se". Então, ele foi e se deitou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7282, 'nvi', '1 Samuel', '1sm', 3, 6, 'De novo o Senhor chamou: "Samuel! " E Samuel se levantou e foi até Eli e disse: "Estou aqui; o senhor me chamou? " Disse Eli: "Meu filho, não o chamei; volte e deite-se".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7283, 'nvi', '1 Samuel', '1sm', 3, 7, 'Ora, Samuel ainda não conhecia o Senhor. A palavra do Senhor ainda não lhe havia sido revelada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7284, 'nvi', '1 Samuel', '1sm', 3, 8, 'O Senhor chamou Samuel pela terceira vez. Ele se levantou, foi até Eli e disse: "Estou aqui; o senhor me chamou? " Então Eli percebeu que o Senhor estava chamando o menino');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7285, 'nvi', '1 Samuel', '1sm', 3, 9, 'e lhe disse: "Vá e deite-se; se ele chamá-lo, diga: ‘Fala, Senhor, pois o teu servo está ouvindo’ ". Então Samuel foi se deitar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7286, 'nvi', '1 Samuel', '1sm', 3, 10, 'O Senhor voltou a chamá-lo como nas outras vezes: "Samuel, Samuel! " Então Samuel disse: "Fala, pois o teu servo está ouvindo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7287, 'nvi', '1 Samuel', '1sm', 3, 11, 'E o Senhor disse a Samuel: "Vou realizar em Israel algo que fará tinir os ouvidos de todos os que ficarem sabendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7288, 'nvi', '1 Samuel', '1sm', 3, 12, 'Nessa ocasião executarei contra Eli tudo o que falei contra sua família, do começo ao fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7289, 'nvi', '1 Samuel', '1sm', 3, 13, 'Pois eu lhe disse que julgaria sua família para sempre, por causa do pecado dos seus filhos, do qual ele tinha consciência; seus filhos se fizeram desprezíveis, e ele não os repreendeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7290, 'nvi', '1 Samuel', '1sm', 3, 14, 'Por isso jurei à família de Eli: ‘Jamais se fará propiciação pela culpa da família de Eli mediante sacrifício ou oferta’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7291, 'nvi', '1 Samuel', '1sm', 3, 15, 'Samuel ficou deitado até de manhã e então abriu as portas da casa do Senhor. Ele teve medo de contar a visão a Eli,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7292, 'nvi', '1 Samuel', '1sm', 3, 16, 'mas este o chamou e disse: "Samuel, meu filho". "Estou aqui", respondeu Samuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7293, 'nvi', '1 Samuel', '1sm', 3, 17, 'Eli perguntou: "O que o Senhor lhe disse? Não esconda de mim. Deus o castigue, e o faça com muita severidade, se você esconder de mim qualquer coisa que ele lhe falou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7294, 'nvi', '1 Samuel', '1sm', 3, 18, 'Então, Samuel lhe contou tudo, e nada escondeu. Então Eli disse: "Ele é o Senhor; que faça o que lhe parecer melhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7295, 'nvi', '1 Samuel', '1sm', 3, 19, 'O Senhor estava com Samuel enquanto este crescia, e fazia com que todas as suas palavras se cumprissem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7296, 'nvi', '1 Samuel', '1sm', 3, 20, 'Todo o Israel, de Dã até Berseba, reconhecia que Samuel estava confirmado como profeta do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7297, 'nvi', '1 Samuel', '1sm', 3, 21, 'O Senhor continuou aparecendo em Siló, onde havia se revelado a Samuel por meio de sua palavra.');
