INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7370, 'nvi', '1 Samuel', '1sm', 8, 1, 'Quando envelheceu, Samuel nomeou seus filhos como líderes de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7371, 'nvi', '1 Samuel', '1sm', 8, 2, 'Seu filho mais velho chamava-se Joel, o segundo, Abias. Eles eram líderes em Berseba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7372, 'nvi', '1 Samuel', '1sm', 8, 3, 'Mas os filhos dele não andaram em seus caminhos. Eles se tornaram gananciosos, aceitaram suborno e perverteram a justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7373, 'nvi', '1 Samuel', '1sm', 8, 4, 'Por isso, todas as autoridades de Israel reuniram-se e foram falar com Samuel, em Ramá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7374, 'nvi', '1 Samuel', '1sm', 8, 5, 'E disseram-lhe: "Tu já estás idoso, e teus filhos não andam em teus caminhos; escolhe agora um rei para que nos lidere, à semelhança das outras nações".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7375, 'nvi', '1 Samuel', '1sm', 8, 6, 'Quando, porém, disseram: "Dá-nos um rei para que nos lidere", isto desagradou a Samuel; então ele orou ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7376, 'nvi', '1 Samuel', '1sm', 8, 7, 'E o Senhor lhe respondeu: "Atenda a tudo o que o povo está lhe pedindo; não foi a você que rejeitaram; foi a mim que rejeitaram como rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7377, 'nvi', '1 Samuel', '1sm', 8, 8, 'Assim como fizeram comigo desde o dia em que os tirei do Egito, até hoje, abandonando-me e prestando culto a outros deuses, também estão fazendo com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7378, 'nvi', '1 Samuel', '1sm', 8, 9, 'Agora atenda-os; mas advirta-os solenemente e diga-lhes que direitos reivindicará o rei que os governará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7379, 'nvi', '1 Samuel', '1sm', 8, 10, 'Samuel transmitiu todas as palavras do Senhor ao povo, que estava lhe pedindo um rei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7380, 'nvi', '1 Samuel', '1sm', 8, 11, 'dizendo: "Isto é o que o rei que reinará sobre vocês reivindicará como seu direito: ele tomará os filhos de vocês para servi-lo em seus carros de guerra e em sua cavalaria, e para correr à frente dos seus carros de guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7381, 'nvi', '1 Samuel', '1sm', 8, 12, 'Colocará alguns como comandantes de mil e outros como comandantes de cinqüenta. Ele os fará arar as terras dele, fazer a colheita, e fabricar armas de guerra e equipamentos para os seus carros de guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7382, 'nvi', '1 Samuel', '1sm', 8, 13, 'Tomará as filhas de vocês para serem perfumistas, cozinheiras e padeiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7383, 'nvi', '1 Samuel', '1sm', 8, 14, 'Tomará de vocês o melhor das plantações, das vinhas e dos olivais, e o dará aos criados dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7384, 'nvi', '1 Samuel', '1sm', 8, 15, 'Tomará um décimo dos cereais e da colheita das uvas e o dará a seus oficiais e a seus criados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7385, 'nvi', '1 Samuel', '1sm', 8, 16, 'Também tomará de vocês para seu uso particular os servos e as servas, o melhor do gado e dos jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7386, 'nvi', '1 Samuel', '1sm', 8, 17, 'E tomará de vocês um décimo dos rebanhos, e vocês mesmos se tornarão escravos dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7387, 'nvi', '1 Samuel', '1sm', 8, 18, 'Naquele dia, vocês clamarão por causa do rei que vocês mesmos escolheram, e o Senhor não os ouvirá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7388, 'nvi', '1 Samuel', '1sm', 8, 19, 'Todavia, o povo recusou-se a ouvir Samuel, e disseram: "Não! Queremos ter um rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7389, 'nvi', '1 Samuel', '1sm', 8, 20, 'Seremos como todas as outras nações; um rei nos governará, e sairá à nossa frente para combater em nossas batalhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7390, 'nvi', '1 Samuel', '1sm', 8, 21, 'Depois de ter ouvido tudo o que o povo disse, Samuel o repetiu perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7391, 'nvi', '1 Samuel', '1sm', 8, 22, 'E o Senhor respondeu: "Atenda-os e dê-lhes um rei". Então Samuel disse aos homens de Israel: "Volte cada um para sua cidade".');
