INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7392, 'nvi', '1 Samuel', '1sm', 9, 1, 'Havia um homem de Benjamim, rico e influente, chamado Quis, filho de Abiel, neto de Zeror, bisneto de Becorate e trineto de Afia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7393, 'nvi', '1 Samuel', '1sm', 9, 2, 'Ele tinha um filho chamado Saul, jovem de boa aparência, sem igual entre os israelitas; os mais altos batiam nos seus ombros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7394, 'nvi', '1 Samuel', '1sm', 9, 3, 'E aconteceu que jumentas de Quis, pai de Saul, extraviaram-se. E ele disse a Saul: "Chame um dos servos e vá procurar as jumentas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7395, 'nvi', '1 Samuel', '1sm', 9, 4, 'Eles atravessaram os montes de Efraim e a região de Salisa, mas não as encontraram. Prosseguindo, entraram no distrito de Saalim, mas as jumentas não estavam lá. Então atravessaram o território de Benjamim, e mesmo assim não as encontrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7396, 'nvi', '1 Samuel', '1sm', 9, 5, 'Chegando ao distrito de Zufe, disse Saul ao seu servo: "Vamos voltar, ou meu pai deixará de pensar nas jumentas para começar a preocupar-se conosco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7397, 'nvi', '1 Samuel', '1sm', 9, 6, 'O servo, contudo, respondeu: "Nesta cidade mora um homem de Deus que é muito respeitado. Tudo o que ele diz acontece. Vamos falar com ele. Talvez ele nos aponte o caminho a seguir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7398, 'nvi', '1 Samuel', '1sm', 9, 7, 'Saul disse a seu servo: "Se formos, o que poderemos lhe dar? A comida de nossas sacos de viagem acabou. Não temos nenhum presente para levar ao homem de Deus. O que temos para oferecer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7399, 'nvi', '1 Samuel', '1sm', 9, 8, 'O servo lhe respondeu: "Tenho três gramas de prata. Darei isto ao homem de Deus para que ele nos aponte o caminho a seguir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7400, 'nvi', '1 Samuel', '1sm', 9, 9, '( Antigamente em Israel, quando alguém ia consultar a Deus, dizia: "Vamos ao vidente", pois o profeta de hoje era chamado vidente. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7401, 'nvi', '1 Samuel', '1sm', 9, 10, 'E Saul concordou: "Muito bem, vamos! " Assim, foram em direção à cidade onde estava o homem de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7402, 'nvi', '1 Samuel', '1sm', 9, 11, 'Ao subirem a colina para chegar à cidade, encontraram algumas jovens que estavam saindo para buscar água e perguntaram a elas: "O vidente está na cidade? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7403, 'nvi', '1 Samuel', '1sm', 9, 12, 'Elas responderam: "Sim. Ele está ali adiante. Apressem-se; ele chegou hoje à nossa cidade, porque o povo vai oferecer um sacrifício no altar no monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7404, 'nvi', '1 Samuel', '1sm', 9, 13, 'Assim que entrarem na cidade, vocês o encontrarão antes que suba ao altar no monte para comer. O povo não começará a comer antes que ele chegue, pois ele deve abençoar o sacrifício; depois disso, os convidados irão comer. Subam agora e vocês logo o encontrarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7405, 'nvi', '1 Samuel', '1sm', 9, 14, 'Eles foram à cidade e, ao entrarem, Samuel vinha na direção deles a caminho do altar no monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7406, 'nvi', '1 Samuel', '1sm', 9, 15, 'No dia anterior à chegada de Saul, o Senhor havia revelado isto a Samuel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7407, 'nvi', '1 Samuel', '1sm', 9, 16, '"Amanhã, por volta desta hora, enviarei a você um homem da terra de Benjamim. Unja-o como líder sobre meu povo Israel; ele libertará o meu povo das mãos dos filisteus. Atentei para o meu povo, pois seu clamor chegou a mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7408, 'nvi', '1 Samuel', '1sm', 9, 17, 'Quando Samuel viu Saul, o Senhor lhe disse: "Este é o homem de quem lhe falei; ele governará o meu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7409, 'nvi', '1 Samuel', '1sm', 9, 18, 'Saul aproximou-se de Samuel na entrada da cidade e lhe perguntou: "Por favor, pode me dizer onde é a casa do vidente? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7410, 'nvi', '1 Samuel', '1sm', 9, 19, 'Respondeu Samuel: "Eu sou o vidente. Vá na minha frente para o altar, pois hoje você comerá comigo. Amanhã cedo eu lhe contarei tudo o que você quer saber e o deixarei ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7411, 'nvi', '1 Samuel', '1sm', 9, 20, 'Quanto às jumentas que você perdeu há três dias, não se preocupe com elas; já foram encontradas. E a quem pertencerá tudo o que é precioso em Israel, senão a você e toda a família de seu pai? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7412, 'nvi', '1 Samuel', '1sm', 9, 21, 'Saul respondeu: "Acaso não sou eu um benjamita, da menor das tribos de Israel, e não é o meu clã o mais insignificante de todos os clãs da tribo de Benjamim? Por que então estás me dizendo tudo isso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7413, 'nvi', '1 Samuel', '1sm', 9, 22, 'Então Samuel levou a Saul e seu servo para a sala e deu a eles o lugar de honra entre os convidados, cerca de trinta pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7414, 'nvi', '1 Samuel', '1sm', 9, 23, 'E disse ao cozinheiro: "Traga-me a porção de carne que lhe entreguei e mandei reservar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7415, 'nvi', '1 Samuel', '1sm', 9, 24, 'Então o cozinheiro pegou a coxa do animal com o que estava sobre ele e colocou tudo diante de Saul. E disse Samuel: "Aqui está o que lhe foi reservado. Coma, pois desde o momento em que eu disse: ‘Tenho convidados’, ela lhe foi separada para esta ocasião". E Saul comeu com Samuel naquele dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7416, 'nvi', '1 Samuel', '1sm', 9, 25, 'Depois de ter descido do altar no monte para a cidade, Samuel conversou com Saul no terraço de sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7417, 'nvi', '1 Samuel', '1sm', 9, 26, 'Ao romper do dia, quando se levantaram, Samuel chamou Saul no terraço e disse: "Levante-se, e eu o acompanharei, e depois você seguirá viagem". Saul se levantou e saiu junto com Samuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7418, 'nvi', '1 Samuel', '1sm', 9, 27, 'Enquanto desciam para a saída da cidade, Samuel disse a Saul: "Diga ao servo que vá na frente". O servo foi. Samuel prosseguiu: "Fique você aqui um instante, para que eu lhe dê uma mensagem da parte de Deus".');
