INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29698, 'nvi', '1 Timóteo', '1tm', 1, 1, 'Paulo, apóstolo de Cristo Jesus, por ordem de Deus, nosso Salvador, e de Cristo Jesus, a nossa esperança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29699, 'nvi', '1 Timóteo', '1tm', 1, 2, 'a Timóteo, meu verdadeiro filho na fé: Graça, misericórdia e paz da parte de Deus Pai e de Cristo Jesus, o nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29700, 'nvi', '1 Timóteo', '1tm', 1, 3, 'Partindo eu para a Macedônia, roguei-lhe que permanecesse em Éfeso para ordenar a certas pessoas que não mais ensinem doutrinas falsas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29701, 'nvi', '1 Timóteo', '1tm', 1, 4, 'e que deixem de dar atenção a mitos e genealogias intermináveis, que causam controvérsias em vez de promoverem a obra de Deus, que é pela fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29702, 'nvi', '1 Timóteo', '1tm', 1, 5, 'O objetivo desta instrução é o amor que procede de um coração puro, de uma boa consciência e de uma fé sincera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29703, 'nvi', '1 Timóteo', '1tm', 1, 6, 'Alguns se desviaram dessas coisas, voltando-se para discussões inúteis,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29704, 'nvi', '1 Timóteo', '1tm', 1, 7, 'querendo ser mestres da lei, quando não compreendem nem o que dizem nem as coisas acerca das quais fazem afirmações tão categóricas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29705, 'nvi', '1 Timóteo', '1tm', 1, 8, 'Sabemos que a lei é boa, se alguém a usa de maneira adequada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29706, 'nvi', '1 Timóteo', '1tm', 1, 9, 'Também sabemos que ela não é feita para os justos, mas para os transgressores e insubordinados, para os ímpios e pecadores, para os profanos e irreverentes, para os que matam pai e mãe, para os homicidas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29707, 'nvi', '1 Timóteo', '1tm', 1, 10, 'para os que praticam imoralidade sexual e os homossexuais, para os seqüestradores, para os mentirosos e os que juram falsamente; e para todo aquele que se opõe à sã doutrina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29708, 'nvi', '1 Timóteo', '1tm', 1, 11, 'Essa sã doutrina se vê no glorioso evangelho que me foi confiado, o evangelho do Deus bendito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29709, 'nvi', '1 Timóteo', '1tm', 1, 12, 'Dou graças a Cristo Jesus, nosso Senhor, que me deu forças e me considerou fiel, designando-me para o ministério,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29710, 'nvi', '1 Timóteo', '1tm', 1, 13, 'a mim que anteriormente fui blasfemo, perseguidor e insolente; mas alcancei misericórdia, porque o fiz por ignorância e na minha incredulidade;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29711, 'nvi', '1 Timóteo', '1tm', 1, 14, 'contudo, a graça de nosso Senhor transbordou sobre mim, juntamente com a fé e o amor que estão em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29712, 'nvi', '1 Timóteo', '1tm', 1, 15, 'Esta afirmação é fiel e digna de toda aceitação: Cristo Jesus veio ao mundo para salvar os pecadores, dos quais eu sou o pior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29713, 'nvi', '1 Timóteo', '1tm', 1, 16, 'Mas, por isso mesmo alcancei misericórdia, para que em mim, o pior dos pecadores, Cristo Jesus demonstrasse toda a grandeza da sua paciência, usando-me como um exemplo para aqueles que nele haveriam de crer para a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29714, 'nvi', '1 Timóteo', '1tm', 1, 17, 'Ao Rei eterno, ao Deus único, imortal e invisível, sejam honra e glória para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29715, 'nvi', '1 Timóteo', '1tm', 1, 18, 'Timóteo, meu filho, dou-lhe esta instrução, segundo as profecias já proferidas a seu respeito, para que, seguindo-as, você combata o bom combate,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29716, 'nvi', '1 Timóteo', '1tm', 1, 19, 'mantendo a fé e a boa consciência que alguns rejeitaram e, por isso, naufragaram na fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29717, 'nvi', '1 Timóteo', '1tm', 1, 20, 'Entre eles estão Himeneu e Alexandre, os quais entreguei a Satanás, para que aprendam a não blasfemar.');
