INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29765, 'nvi', '1 Timóteo', '1tm', 5, 1, 'Não repreenda asperamente ao homem idoso, mas exorte-o como se ele fosse seu pai; trate os jovens como a irmãos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29766, 'nvi', '1 Timóteo', '1tm', 5, 2, 'as mulheres idosas, como a mães; e as moças, como a irmãs, com toda a pureza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29767, 'nvi', '1 Timóteo', '1tm', 5, 3, 'Trate adequadamente as viúvas que são realmente necessitadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29768, 'nvi', '1 Timóteo', '1tm', 5, 4, 'Mas se uma viúva tem filhos ou netos, que estes aprendam primeiramente a colocar a sua religião em prática, cuidando de sua própria família e retribuindo o bem recebido de seus pais e avós, pois isso agrada a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29769, 'nvi', '1 Timóteo', '1tm', 5, 5, 'A viúva realmente necessitada e desamparada põe sua esperança em Deus e persiste dia e noite em oração e em súplica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29770, 'nvi', '1 Timóteo', '1tm', 5, 6, 'Mas a que vive para os prazeres, ainda que esteja viva, está morta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29771, 'nvi', '1 Timóteo', '1tm', 5, 7, 'Ordene estas coisas para que sejam irrepreensíveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29772, 'nvi', '1 Timóteo', '1tm', 5, 8, 'Se alguém não cuida de seus parentes, e especialmente dos de sua própria família, negou a fé e é pior que um descrente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29773, 'nvi', '1 Timóteo', '1tm', 5, 9, 'Nenhuma mulher deve ser inscrita na lista de viúvas, a não ser que tenha mais de sessenta anos de idade, tenha sido fiel a seu marido');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29774, 'nvi', '1 Timóteo', '1tm', 5, 10, 'e seja bem conhecida por suas boas obras, tais como criar filhos, ser hospitaleira, lavar os pés dos santos, socorrer os atribulados e dedicar-se a todo tipo de boa obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29775, 'nvi', '1 Timóteo', '1tm', 5, 11, 'Não inclua nessa lista as viúvas mais jovens, pois, quando os seus desejos sensuais superam a sua dedicação a Cristo, querem se casar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29776, 'nvi', '1 Timóteo', '1tm', 5, 12, 'Assim, elas trazem condenação sobre si, por haverem rompido seu primeiro compromisso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29777, 'nvi', '1 Timóteo', '1tm', 5, 13, 'Além disso, aprendem a ficar ociosas, andando de casa em casa; e não se tornam apenas ociosas, mas também fofoqueiras e indiscretas, falando coisas que não devem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29778, 'nvi', '1 Timóteo', '1tm', 5, 14, 'Portanto, aconselho que as viúvas mais jovens se casem, tenham filhos, administrem suas casas e não dêem ao inimigo nenhum motivo para maledicência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29779, 'nvi', '1 Timóteo', '1tm', 5, 15, 'Algumas, na verdade, já se desviaram, para seguir a Satanás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29780, 'nvi', '1 Timóteo', '1tm', 5, 16, 'Se alguma mulher crente tem viúvas em sua família, deve ajudá-las. Não seja a igreja sobrecarregada com elas, a fim de que as viúvas realmente necessitadas sejam auxiliadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29781, 'nvi', '1 Timóteo', '1tm', 5, 17, 'Os presbíteros que lideram bem a igreja são dignos de dupla honra, especialmente aqueles cujo trabalho é a pregação e o ensino,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29782, 'nvi', '1 Timóteo', '1tm', 5, 18, 'pois a Escritura diz: "Não amordace o boi enquanto está debulhando o cereal", e "o trabalhador merece o seu salário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29783, 'nvi', '1 Timóteo', '1tm', 5, 19, 'Não aceite acusação contra um presbítero, se não for apoiada por duas ou três testemunhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29784, 'nvi', '1 Timóteo', '1tm', 5, 20, 'Os que pecarem deverão ser repreendidos em público, para que os demais também temam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29785, 'nvi', '1 Timóteo', '1tm', 5, 21, 'Eu o exorto solenemente, diante de Deus, de Cristo Jesus e dos anjos eleitos, a que procure observar essas instruções sem parcialidade; e não faça nada por favoritismo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29786, 'nvi', '1 Timóteo', '1tm', 5, 22, 'Não se precipite em impor as mãos sobre ninguém e não participe dos pecados dos outros. Conserve-se puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29787, 'nvi', '1 Timóteo', '1tm', 5, 23, 'Não continue a beber somente água; tome também um pouco de vinho, por causa do seu estômago e das suas freqüentes enfermidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29788, 'nvi', '1 Timóteo', '1tm', 5, 24, 'Os pecados de alguns são evidentes, mesmo antes de serem submetidos a julgamento; enquanto que os pecados de outros se manifestam posteriormente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29789, 'nvi', '1 Timóteo', '1tm', 5, 25, 'Da mesma forma, as boas obras são evidentes, e as que não o são não podem permanecer ocultas.');
