INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29790, 'nvi', '1 Timóteo', '1tm', 6, 1, 'Todos os que estão sob o jugo da escravidão devem considerar seus senhores como dignos de todo o respeito, para que o nome de Deus e o nosso ensino não sejam blasfemados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29791, 'nvi', '1 Timóteo', '1tm', 6, 2, 'Os que têm senhores crentes não devem ter por eles menos respeito, pelo fato de serem irmãos; pelo contrário, devem servi-los ainda melhor, porque os que se beneficiam do seu serviço são fiéis e amados. Ensine e recomende essas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29792, 'nvi', '1 Timóteo', '1tm', 6, 3, 'Se alguém ensina falsas doutrinas e não concorda com a sã doutrina de nosso Senhor Jesus Cristo e com o ensino que é segundo a piedade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29793, 'nvi', '1 Timóteo', '1tm', 6, 4, 'é orgulhoso e nada entende. Esse tal mostra um interesse doentio por controvérsias e contendas acerca de palavras, que resultam em inveja, brigas, difamações, suspeitas malignas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29794, 'nvi', '1 Timóteo', '1tm', 6, 5, 'e atritos constantes entre pessoas que têm a mente corrompida e que são privados da verdade, os quais pensam que a piedade é fonte de lucro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29795, 'nvi', '1 Timóteo', '1tm', 6, 6, 'De fato, a piedade com contentamento é grande fonte de lucro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29796, 'nvi', '1 Timóteo', '1tm', 6, 7, 'pois nada trouxemos para este mundo e dele nada podemos levar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29797, 'nvi', '1 Timóteo', '1tm', 6, 8, 'por isso, tendo o que comer e com que vestir-nos, estejamos com isso satisfeitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29798, 'nvi', '1 Timóteo', '1tm', 6, 9, 'Os que querem ficar ricos caem em tentação, em armadilhas e em muitos desejos descontrolados e nocivos, que levam os homens a mergulharem na ruína e na destruição,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29799, 'nvi', '1 Timóteo', '1tm', 6, 10, 'pois o amor ao dinheiro é raiz de todos os males. Algumas pessoas, por cobiçarem o dinheiro, desviaram-se da fé e se atormentaram a si mesmas com muitos sofrimentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29800, 'nvi', '1 Timóteo', '1tm', 6, 11, 'Você, porém, homem de Deus, fuja de tudo isso e busque a justiça, a piedade, a fé, o amor, a perseverança e a mansidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29801, 'nvi', '1 Timóteo', '1tm', 6, 12, 'Combata o bom combate da fé. Tome posse da vida eterna, para a qual você foi chamado e fez a boa confissão na presença de muitas testemunhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29802, 'nvi', '1 Timóteo', '1tm', 6, 13, 'Diante de Deus, que a tudo dá vida, e de Cristo Jesus, que diante de Pôncio Pilatos fez a boa confissão, eu lhe recomendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29803, 'nvi', '1 Timóteo', '1tm', 6, 14, 'Guarde este mandamento imaculado, irrepreensível, até a manifestação de nosso Senhor Jesus Cristo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29804, 'nvi', '1 Timóteo', '1tm', 6, 15, 'a qual Deus fará se cumprir no seu devido tempo. Ele é o bendito e único Soberano, o Rei dos reis e Senhor dos senhores,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29805, 'nvi', '1 Timóteo', '1tm', 6, 16, 'o único que é imortal e habita em luz inacessível, a quem ninguém viu nem pode ver. A ele sejam honra e poder para sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29806, 'nvi', '1 Timóteo', '1tm', 6, 17, 'Ordene aos que são ricos no presente mundo que não sejam arrogantes, nem ponham sua esperança na incerteza da riqueza, mas em Deus, que de tudo nos provê ricamente, para a nossa satisfação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29807, 'nvi', '1 Timóteo', '1tm', 6, 18, 'Ordene-lhes que pratiquem o bem, sejam ricos em boas obras, generosos e prontos para repartir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29808, 'nvi', '1 Timóteo', '1tm', 6, 19, 'Dessa forma, eles acumularão um tesouro para si mesmos, um firme fundamento para a era que há de vir, e assim alcançarão a verdadeira vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29809, 'nvi', '1 Timóteo', '1tm', 6, 20, 'Timóteo, guarde o que lhe foi confiado. Evite as conversas inúteis e profanas e as idéias contraditórias do que é falsamente chamado conhecimento;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29810, 'nvi', '1 Timóteo', '1tm', 6, 21, 'professando-o, alguns desviaram-se da fé. A graça seja com vocês.');
