INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29572, 'nvi', '1 Tessalonicenses', '1ts', 2, 1, 'Irmãos, vocês mesmos sabem que a visita que lhes fizemos não foi inútil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29573, 'nvi', '1 Tessalonicenses', '1ts', 2, 2, 'Apesar de termos sido maltratados e insultados em Filipos, como vocês sabem, com a ajuda de nosso Deus tivemos coragem de anunciar-lhes o evangelho de Deus, em meio a muita luta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29574, 'nvi', '1 Tessalonicenses', '1ts', 2, 3, 'Pois nossa exortação não tem origem no erro nem em motivos impuros, nem temos intenção de enganá-los;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29575, 'nvi', '1 Tessalonicenses', '1ts', 2, 4, 'pelo contrário, como homens aprovados por Deus, a ponto de nos ter sido confiado por ele o evangelho, não falamos para agradar a pessoas, mas a Deus, que prova os nossos corações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29576, 'nvi', '1 Tessalonicenses', '1ts', 2, 5, 'Vocês bem sabem a nossa linguagem nunca foi de bajulação nem de pretexto para ganância; Deus é testemunha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29577, 'nvi', '1 Tessalonicenses', '1ts', 2, 6, 'Nem buscamos reconhecimento humano, quer de vocês quer de outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29578, 'nvi', '1 Tessalonicenses', '1ts', 2, 7, 'Embora, como apóstolos de Cristo, pudéssemos ter sido um peso, tornamo-nos bondosos entre vocês, como uma mãe que cuida dos próprios filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29579, 'nvi', '1 Tessalonicenses', '1ts', 2, 8, 'Sentindo, assim, tanta afeição por vocês, decidimos dar-lhes não somente o evangelho de Deus, mas também a nossa própria vida, porque vocês se tornaram muito amados por nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29580, 'nvi', '1 Tessalonicenses', '1ts', 2, 9, 'Irmãos, certamente vocês se lembram do nosso trabalho esgotante e da nossa fadiga; trabalhamos noite e dia para não sermos pesados a ninguém, enquanto lhes pregávamos o evangelho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29581, 'nvi', '1 Tessalonicenses', '1ts', 2, 10, 'Tanto vocês como Deus são testemunhas de como nos portamos de maneira santa, justa e irrepreensível entre vocês, os que crêem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29582, 'nvi', '1 Tessalonicenses', '1ts', 2, 11, 'Pois vocês sabem que tratamos cada um como um pai trata seus filhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29583, 'nvi', '1 Tessalonicenses', '1ts', 2, 12, 'exortando, consolando e dando testemunho, para que vocês vivam de maneira digna de Deus, que os chamou para o seu Reino e glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29584, 'nvi', '1 Tessalonicenses', '1ts', 2, 13, 'Também agradecemos a Deus sem cessar, pois, ao receberem de nossa parte a palavra de Deus, vocês a aceitaram não como palavra de homens, mas segundo verdadeiramente é, como palavra de Deus, que atua com eficácia em vocês, os que crêem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29585, 'nvi', '1 Tessalonicenses', '1ts', 2, 14, 'Porque vocês, irmãos, tornaram-se imitadores das igrejas de Deus em Cristo Jesus que estão na Judéia. Vocês sofreram da parte dos seus próprios conterrâneos as mesmas coisas que aquelas igrejas sofreram da parte dos judeus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29586, 'nvi', '1 Tessalonicenses', '1ts', 2, 15, 'que mataram o Senhor Jesus e os profetas, e também nos perseguiram. Eles desagradam a Deus e são hostis a todos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29587, 'nvi', '1 Tessalonicenses', '1ts', 2, 16, 'esforçando-se para nos impedir que falemos aos gentios, e estes sejam salvos. Dessa forma, vão sempre completando a medida dos seus pecados. Sobre eles, finalmente, veio a ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29588, 'nvi', '1 Tessalonicenses', '1ts', 2, 17, 'Nós, porém, irmãos, privados da companhia de vocês por breve tempo, em pessoa, mas não no coração, esforçamo-nos ainda mais para vê-los pessoalmente, pela saudade que temos de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29589, 'nvi', '1 Tessalonicenses', '1ts', 2, 18, 'Quisemos visitá-los. Eu mesmo, Paulo o quis, e não apenas uma vez, mas duas; Satanás, porém, nos impediu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29590, 'nvi', '1 Tessalonicenses', '1ts', 2, 19, 'Pois quem é a nossa esperança, alegria ou coroa em que nos gloriamos perante o Senhor Jesus na sua vinda? Não são vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29591, 'nvi', '1 Tessalonicenses', '1ts', 2, 20, 'De fato, vocês são a nossa glória e a nossa alegria.');
