INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29623, 'nvi', '1 Tessalonicenses', '1ts', 5, 1, 'Irmãos, quanto aos tempos e épocas, não precisamos escrever-lhes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29624, 'nvi', '1 Tessalonicenses', '1ts', 5, 2, 'pois vocês mesmos sabem perfeitamente que o dia do Senhor virá como ladrão à noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29625, 'nvi', '1 Tessalonicenses', '1ts', 5, 3, 'Quando disserem: "Paz e segurança", então, de repente, a destruição virá sobre eles, como dores à mulher grávida; e de modo nenhum escaparão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29626, 'nvi', '1 Tessalonicenses', '1ts', 5, 4, 'Mas vocês, irmãos, não estão nas trevas, para que esse dia os surpreenda como ladrão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29627, 'nvi', '1 Tessalonicenses', '1ts', 5, 5, 'Vocês todos são filhos da luz, filhos do dia. Não somos da noite nem das trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29628, 'nvi', '1 Tessalonicenses', '1ts', 5, 6, 'Portanto, não durmamos como os demais, mas estejamos atentos e sejamos sóbrios;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29629, 'nvi', '1 Tessalonicenses', '1ts', 5, 7, 'pois os que dormem, dormem de noite, e os que se embriagam, embriagam-se de noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29630, 'nvi', '1 Tessalonicenses', '1ts', 5, 8, 'Nós, porém, que somos do dia, sejamos sóbrios, vestindo a couraça da fé e do amor e o capacete da esperança da salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29631, 'nvi', '1 Tessalonicenses', '1ts', 5, 9, 'Porque Deus não nos destinou para a ira, mas para recebermos a salvação por meio de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29632, 'nvi', '1 Tessalonicenses', '1ts', 5, 10, 'Ele morreu por nós para que, quer estejamos acordados quer dormindo, vivamos unidos a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29633, 'nvi', '1 Tessalonicenses', '1ts', 5, 11, 'Por isso, exortem-se e edifiquem-se uns aos outros, como de fato vocês estão fazendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29634, 'nvi', '1 Tessalonicenses', '1ts', 5, 12, 'Agora lhes pedimos, irmãos, que tenham consideração para com os que se esforçam no trabalho entre vocês, que os lideram no Senhor e os aconselham.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29635, 'nvi', '1 Tessalonicenses', '1ts', 5, 13, 'Tenham-nos na mais alta estima, com amor, por causa do trabalho deles. Vivam em paz uns com os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29636, 'nvi', '1 Tessalonicenses', '1ts', 5, 14, 'Exortamos vocês, irmãos, a que advirtam os ociosos, confortem os desanimados, auxiliem os fracos, sejam pacientes para com todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29637, 'nvi', '1 Tessalonicenses', '1ts', 5, 15, 'Tenham cuidado para que ninguém retribua o mal com o mal, mas sejam sempre bondosos uns para com os outros e para com todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29638, 'nvi', '1 Tessalonicenses', '1ts', 5, 16, 'Alegrem-se sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29639, 'nvi', '1 Tessalonicenses', '1ts', 5, 17, 'Orem continuamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29640, 'nvi', '1 Tessalonicenses', '1ts', 5, 18, 'Dêem graças em todas as circunstâncias, pois esta é a vontade de Deus para vocês em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29641, 'nvi', '1 Tessalonicenses', '1ts', 5, 19, 'Não apaguem o Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29642, 'nvi', '1 Tessalonicenses', '1ts', 5, 20, 'Não tratem com desprezo as profecias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29643, 'nvi', '1 Tessalonicenses', '1ts', 5, 21, 'mas ponham à prova todas as coisas e fiquem com o que é bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29644, 'nvi', '1 Tessalonicenses', '1ts', 5, 22, 'Afastem-se de toda forma de mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29645, 'nvi', '1 Tessalonicenses', '1ts', 5, 23, 'Que o próprio Deus da paz os santifique inteiramente. Que todo o espírito, alma e corpo de vocês seja conservado irrepreensível na vinda de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29646, 'nvi', '1 Tessalonicenses', '1ts', 5, 24, 'Aquele que os chama é fiel, e fará isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29647, 'nvi', '1 Tessalonicenses', '1ts', 5, 25, 'Irmãos, orem por nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29648, 'nvi', '1 Tessalonicenses', '1ts', 5, 26, 'Saúdem todos os irmãos com beijo santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29649, 'nvi', '1 Tessalonicenses', '1ts', 5, 27, 'Responsabilizo-os diante do Senhor para que esta carta seja lida a todos os irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29650, 'nvi', '1 Tessalonicenses', '1ts', 5, 28, 'A graça de nosso Senhor Jesus Cristo seja com vocês.');
