INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28802, 'nvi', '2 Coríntios', '2co', 1, 1, 'Paulo, apóstolo de Cristo Jesus pela vontade de Deus, e o irmão Timóteo, à igreja de Deus que está em Corinto, juntamente com todos os santos de toda a Acaia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28803, 'nvi', '2 Coríntios', '2co', 1, 2, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28804, 'nvi', '2 Coríntios', '2co', 1, 3, 'Bendito seja o Deus e Pai de nosso Senhor Jesus Cristo, Pai das misericórdias e Deus de toda consolação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28805, 'nvi', '2 Coríntios', '2co', 1, 4, 'que nos consola em todas as nossas tribulações, para que, com a consolação que recebemos de Deus, possamos consolar os que estão passando por tribulações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28806, 'nvi', '2 Coríntios', '2co', 1, 5, 'Pois assim como os sofrimentos de Cristo transbordam sobre nós, também por meio de Cristo transborda a nossa consolação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28807, 'nvi', '2 Coríntios', '2co', 1, 6, 'Se somos atribulados, é para consolação e salvação de vocês; se somos consolados, é para consolação de vocês, a qual lhes dá paciência para suportarem os mesmos sofrimentos que nós estamos padecendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28808, 'nvi', '2 Coríntios', '2co', 1, 7, 'E a nossa esperança em relação a vocês está firme, porque sabemos que, da mesma forma como vocês participam dos nossos sofrimentos, participam também da nossa consolação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28809, 'nvi', '2 Coríntios', '2co', 1, 8, 'Irmãos, não queremos que vocês desconheçam as tribulações que sofremos na província da Ásia, as quais foram muito além da nossa capacidade de suportar, a ponto de perdermos a esperança da própria vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28810, 'nvi', '2 Coríntios', '2co', 1, 9, 'De fato, já tínhamos sobre nós a sentença de morte, para que não confiássemos em nós mesmos, mas em Deus, que ressuscita os mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28811, 'nvi', '2 Coríntios', '2co', 1, 10, 'Ele nos livrou e continuará nos livrando de tal perigo de morte. Nele temos colocado a nossa esperança de que continuará a livrar-nos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28812, 'nvi', '2 Coríntios', '2co', 1, 11, 'enquanto vocês nos ajudam com as suas orações. Assim muitos darão graças por nossa causa, pelo favor a nós concedido em resposta às orações de muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28813, 'nvi', '2 Coríntios', '2co', 1, 12, 'Este é o nosso orgulho: A nossa consciência dá testemunho de que nos temos conduzido no mundo, especialmente em nosso relacionamento com vocês, com santidade e sinceridade provenientes de Deus, não de acordo com a sabedoria do mundo, mas de acordo com a graça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28814, 'nvi', '2 Coríntios', '2co', 1, 13, 'Pois nada lhes escrevemos que vocês não sejam capazes de ler ou entender. E espero que,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28815, 'nvi', '2 Coríntios', '2co', 1, 14, 'assim como vocês nos entenderam em parte, venham a entender plenamente que podem orgulhar-se de nós, assim como nos orgulharemos de vocês no dia do Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28816, 'nvi', '2 Coríntios', '2co', 1, 15, 'Confiando nisso, e para que vocês fossem duplamente beneficiados, planejava primeiro visitá-los');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28817, 'nvi', '2 Coríntios', '2co', 1, 16, 'em minha ida à Macedônia e voltar a vocês vindo de lá, para que me ajudassem em minha viagem para a Judéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28818, 'nvi', '2 Coríntios', '2co', 1, 17, 'Quando planejei isso, será que o fiz levianamente? Ou será que faço meus planos de modo mundano, dizendo ao mesmo tempo "sim" e "não"?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28819, 'nvi', '2 Coríntios', '2co', 1, 18, 'Todavia, como Deus é fiel, nossa mensagem a vocês não é "sim" e "não",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28820, 'nvi', '2 Coríntios', '2co', 1, 19, 'pois o Filho de Deus, Jesus Cristo, pregado entre vocês por mim e também por Silvano e Timóteo, não foi "sim" e "não", mas nele sempre houve "sim";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28821, 'nvi', '2 Coríntios', '2co', 1, 20, 'pois quantas forem as promessas feitas por Deus, tantas têm em Cristo o "sim". Por isso, por meio dele, o "Amém" é pronunciado por nós para a glória de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28822, 'nvi', '2 Coríntios', '2co', 1, 21, 'Ora, é Deus que faz que nós e vocês permaneçamos firmes em Cristo. Ele nos ungiu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28823, 'nvi', '2 Coríntios', '2co', 1, 22, 'nos selou como sua propriedade e pôs o seu Espírito em nossos corações como garantia do que está por vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28824, 'nvi', '2 Coríntios', '2co', 1, 23, 'Invoco a Deus como testemunha de que foi a fim de poupá-los que não voltei a Corinto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28825, 'nvi', '2 Coríntios', '2co', 1, 24, 'Não que tenhamos domínio sobre a sua fé, mas cooperamos com vocês para que tenham alegria, pois é pela fé que vocês permanecem firmes.');
