INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28861, 'nvi', '2 Coríntios', '2co', 4, 1, 'Portanto, visto que temos este ministério pela misericórdia que nos foi dada, não desanimamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28862, 'nvi', '2 Coríntios', '2co', 4, 2, 'Antes, renunciamos aos procedimentos secretos e vergonhosos; não usamos de engano nem torcemos a palavra de Deus. Pelo contrário, mediante a clara exposição da verdade, recomendamo-nos à consciência de todos, diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28863, 'nvi', '2 Coríntios', '2co', 4, 3, 'Mas se o nosso evangelho está encoberto, para os que estão perecendo é que está encoberto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28864, 'nvi', '2 Coríntios', '2co', 4, 4, 'O deus desta era cegou o entendimento dos descrentes, para que não vejam a luz do evangelho da glória de Cristo, que é a imagem de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28865, 'nvi', '2 Coríntios', '2co', 4, 5, 'Pois não nos pregamos a nós mesmos, mas a Jesus Cristo, o Senhor, e a nós como escravos de vocês, por amor de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28866, 'nvi', '2 Coríntios', '2co', 4, 6, 'Pois Deus que disse: "Das trevas resplandeça a luz", ele mesmo brilhou em nossos corações, para iluminação do conhecimento da glória de Deus na face de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28867, 'nvi', '2 Coríntios', '2co', 4, 7, 'Mas temos esse tesouro em vasos de barro, para mostrar que este poder que a tudo excede provém de Deus, e não de nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28868, 'nvi', '2 Coríntios', '2co', 4, 8, 'De todos os lados somos pressionados, mas não desanimados; ficamos perplexos, mas não desesperados;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28869, 'nvi', '2 Coríntios', '2co', 4, 9, 'somos perseguidos, mas não abandonados; abatidos, mas não destruídos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28870, 'nvi', '2 Coríntios', '2co', 4, 10, 'Trazemos sempre em nosso corpo o morrer de Jesus, para que a vida de Jesus também seja revelada em nosso corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28871, 'nvi', '2 Coríntios', '2co', 4, 11, 'Pois nós, que estamos vivos, somos sempre entregues à morte por amor a Jesus, para que a sua vida também se manifeste em nosso corpo mortal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28872, 'nvi', '2 Coríntios', '2co', 4, 12, 'De modo que em nós atua a morte; mas em vocês, a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28873, 'nvi', '2 Coríntios', '2co', 4, 13, 'Está escrito: "Cri, por isso falei". Com esse mesmo espírito de fé nós também cremos e, por isso, falamos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28874, 'nvi', '2 Coríntios', '2co', 4, 14, 'porque sabemos que aquele que ressuscitou ao Senhor Jesus dentre os mortos, também nos ressuscitará com Jesus e nos apresentará com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28875, 'nvi', '2 Coríntios', '2co', 4, 15, 'Tudo isso é para o bem de vocês, para que a graça, que está alcançando um número cada vez maior de pessoas, faça que transbordem as ações de graças para a glória de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28876, 'nvi', '2 Coríntios', '2co', 4, 16, 'Por isso não desanimamos. Embora exteriormente estejamos a desgastar-nos, interiormente estamos sendo renovados dia após dia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28877, 'nvi', '2 Coríntios', '2co', 4, 17, 'pois os nossos sofrimentos leves e momentâneos estão produzindo para nós uma glória eterna que pesa mais do que todos eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28878, 'nvi', '2 Coríntios', '2co', 4, 18, 'Assim, fixamos os olhos, não naquilo que se vê, mas no que não se vê, pois o que se vê é transitório, mas o que não se vê é eterno.');
