INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28934, 'nvi', '2 Coríntios', '2co', 8, 1, 'Agora, irmãos, queremos que vocês tomem conhecimento da graça que Deus concedeu às igrejas da Macedônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28935, 'nvi', '2 Coríntios', '2co', 8, 2, 'No meio da mais severa tribulação, a grande alegria e a extrema pobreza deles transbordaram em rica generosidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28936, 'nvi', '2 Coríntios', '2co', 8, 3, 'Pois dou testemunho de que eles deram tudo quanto podiam, e até além do que podiam. Por iniciativa própria');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28937, 'nvi', '2 Coríntios', '2co', 8, 4, 'eles nos suplicaram insistentemente o privilégio de participar da assistência aos santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28938, 'nvi', '2 Coríntios', '2co', 8, 5, 'E não somente fizeram o que esperávamos, mas entregaram-se primeiramente a si mesmos ao Senhor e, depois, a nós, pela vontade de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28939, 'nvi', '2 Coríntios', '2co', 8, 6, 'Assim, recomendamos a Tito, visto que ele já havia começado, que completasse esse ato de graça da parte de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28940, 'nvi', '2 Coríntios', '2co', 8, 7, 'Todavia, assim como vocês se destacam em tudo: na fé, na palavra, no conhecimento, na dedicação completa e no amor que vocês têm por nós, destaquem-se também neste privilégio de contribuir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28941, 'nvi', '2 Coríntios', '2co', 8, 8, 'Não lhes estou dando uma ordem, mas quero verificar a sinceridade do amor de vocês, comparando-o com a dedicação dos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28942, 'nvi', '2 Coríntios', '2co', 8, 9, 'Pois vocês conhecem a graça de nosso Senhor Jesus Cristo que, sendo rico, se fez pobre por amor de vocês, para que por meio de sua pobreza vocês se tornassem ricos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28943, 'nvi', '2 Coríntios', '2co', 8, 10, 'Este é meu conselho: convém que vocês contribuam, já que desde o ano passado vocês foram os primeiros, não somente a contribuir, mas também a propor esse plano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28944, 'nvi', '2 Coríntios', '2co', 8, 11, 'Agora, completem a obra, para que a forte disposição de realizá-la seja igualada pelo zelo em concluí-la, de acordo com os bens que vocês possuem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28945, 'nvi', '2 Coríntios', '2co', 8, 12, 'Porque, se há prontidão, a contribuição é aceitável de acordo com aquilo que alguém tem, e não de acordo com o que não tem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28946, 'nvi', '2 Coríntios', '2co', 8, 13, 'Nosso desejo não é que outros sejam aliviados enquanto vocês são sobrecarregados, mas que haja igualdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28947, 'nvi', '2 Coríntios', '2co', 8, 14, 'No presente momento, a fartura de vocês suprirá a necessidade deles, para que, por sua vez, a fartura deles supra a necessidade de vocês. Então haverá igualdade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28948, 'nvi', '2 Coríntios', '2co', 8, 15, 'como está escrito: "Quem tinha recolhido muito não teve demais, e não faltou a quem tinha recolhido pouco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28949, 'nvi', '2 Coríntios', '2co', 8, 16, 'Agradeço a Deus, que pôs no coração de Tito o mesmo cuidado que tenho por vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28950, 'nvi', '2 Coríntios', '2co', 8, 17, 'pois Tito não apenas aceitou o nosso pedido, mas está indo até vocês, com muito entusiasmo e por iniciativa própria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28951, 'nvi', '2 Coríntios', '2co', 8, 18, 'Com ele estamos enviando o irmão que é recomendado por todas as igrejas por seu serviço no evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28952, 'nvi', '2 Coríntios', '2co', 8, 19, 'Não só por isso, mas ele também foi escolhido pelas igrejas para nos acompanhar quando formos ministrar esta doação, o que fazemos para honrar o próprio Senhor e mostrar a nossa disposição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28953, 'nvi', '2 Coríntios', '2co', 8, 20, 'Queremos evitar que alguém nos critique quanto ao nosso modo de ministrar essa generosa oferta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28954, 'nvi', '2 Coríntios', '2co', 8, 21, 'pois estamos tendo o cuidado de fazer o que é correto, não apenas aos olhos do Senhor, mas também aos olhos dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28955, 'nvi', '2 Coríntios', '2co', 8, 22, 'Além disso, estamos enviando com eles o nosso irmão que muitas vezes e de muitas maneiras já nos provou que é muito dedicado, e agora ainda mais, por causa da grande confiança que ele tem em vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28956, 'nvi', '2 Coríntios', '2co', 8, 23, 'Quanto a Tito, ele é meu companheiro e cooperador entre vocês; quanto a nossos irmãos, eles são representantes das igrejas e uma honra para Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (28957, 'nvi', '2 Coríntios', '2co', 8, 24, 'Portanto, diante das demais igrejas, demonstrem a esses irmãos a prova do amor que vocês têm e a razão do orgulho que temos de vocês.');
