INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11397, 'nvi', '2 Crônicas', '2cr', 10, 1, 'Roboão foi a Siquém, onde todos os israelitas tinham se reunido para proclamá-lo rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11398, 'nvi', '2 Crônicas', '2cr', 10, 2, 'Assim que Jeroboão, filho de Nebate, que estava no Egito fugindo do rei Salomão, soube disso, voltou de lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11399, 'nvi', '2 Crônicas', '2cr', 10, 3, 'E mandaram chamá-lo. Então ele e todo o Israel foram ao encontro de Roboão e disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11400, 'nvi', '2 Crônicas', '2cr', 10, 4, '"Teu pai colocou sobre nós um jugo pesado, mas agora diminui o trabalho árduo e este jugo pesado, e nós te serviremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11401, 'nvi', '2 Crônicas', '2cr', 10, 5, 'Roboão respondeu: "Voltem a mim daqui a três dias". Então o povo foi embora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11402, 'nvi', '2 Crônicas', '2cr', 10, 6, 'O rei Roboão perguntou às autoridades que haviam servido ao seu pai Salomão durante a vida dele: "Como vocês me aconselham a responder a este povo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11403, 'nvi', '2 Crônicas', '2cr', 10, 7, 'Eles responderam: "Se hoje fores bom para esse povo, se o agradares e lhe deres resposta favorável, eles sempre serão teus servos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11404, 'nvi', '2 Crônicas', '2cr', 10, 8, 'Roboão, contudo, rejeitou o conselho que as autoridades de Israel lhe tinham dado e consultou os jovens que haviam crescido com ele e o estavam servindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11405, 'nvi', '2 Crônicas', '2cr', 10, 9, 'Perguntou-lhes: "Qual é o conselho de vocês? Como devemos responder a este povo que me diz: ‘Diminui o jugo que teu pai colocou sobre nós’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11406, 'nvi', '2 Crônicas', '2cr', 10, 10, 'Os jovens que haviam crescido com ele responderam: "A este povo que te disse: ‘Teu pai colocou sobre nós um jugo pesado; torna-o mais leve’ — dize: ‘Meu dedo mínimo é mais grosso do que a cintura do meu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11407, 'nvi', '2 Crônicas', '2cr', 10, 11, 'Pois bem, meu pai lhes impôs um jugo pesado; eu o tornarei ainda mais pesado. Meu pai os castigou com simples chicotes; eu os castigarei com chicotes pontiagudos’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11408, 'nvi', '2 Crônicas', '2cr', 10, 12, 'Três dias depois, Jeroboão e todo o povo voltaram a Roboão, segundo a orientação dada pelo rei: "Voltem a mim daqui a três dias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11409, 'nvi', '2 Crônicas', '2cr', 10, 13, 'Mas o rei lhes respondeu asperamente. Rejeitando o conselho das autoridades de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11410, 'nvi', '2 Crônicas', '2cr', 10, 14, 'seguiu o conselho dos jovens e disse: "Meu pai lhes tornou pesado o jugo; eu o tornarei ainda mais pesado. Meu pai os castigou com simples chicotes; eu os castigarei com chicotes pontiagudos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11411, 'nvi', '2 Crônicas', '2cr', 10, 15, 'E o rei não ouviu o povo, pois esta mudança nos acontecimentos vinha da parte de Deus, para que se cumprisse a palavra que o Senhor havia falado a Jeroboão, filho de Nebate, por meio do silonita Aías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11412, 'nvi', '2 Crônicas', '2cr', 10, 16, 'Quando todo o Israel viu que o rei se recusava a ouvi-los, responderam ao rei: "Que temos em comum com Davi? Que temos em comum com o filho de Jessé? Para as suas tendas, ó Israel! Cuide da sua própria casa, ó Davi! " E assim os israelitas foram para as suas casas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11413, 'nvi', '2 Crônicas', '2cr', 10, 17, 'Quanto, porém, aos israelitas que moravam nas cidades de Judá, Roboão continuou como rei deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11414, 'nvi', '2 Crônicas', '2cr', 10, 18, 'O rei Roboão enviou Adonirão, chefe de trabalhos forçados, mas todo o Israel o apedrejou até à morte. O rei, contudo, conseguiu subir em sua carruagem e fugir para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11415, 'nvi', '2 Crônicas', '2cr', 10, 19, 'Desta forma Israel se rebelou contra a dinastia de Davi, e assim permanece até hoje.');
