INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11416, 'nvi', '2 Crônicas', '2cr', 11, 1, 'Quando Roboão chegou em Jerusalém, convocou cento e oitenta mil homens de combate, das tribos de Judá e de Benjamim, para guerrearem contra Israel e recuperarem o reino para Roboão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11417, 'nvi', '2 Crônicas', '2cr', 11, 2, 'Entretanto, veio esta palavra do Senhor a Semaías, homem de Deus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11418, 'nvi', '2 Crônicas', '2cr', 11, 3, '"Diga a Roboão, filho de Salomão, rei de Judá, e a todos os israelitas de Judá e de Benjamim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11419, 'nvi', '2 Crônicas', '2cr', 11, 4, '‘Assim diz o Senhor: Não saiam à guerra contra os seus irmãos. Voltem para casa, todos vocês, pois fui eu que fiz isso’ ". E eles obedeceram à palavra do Senhor e desistiram de marchar contra Jeroboão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11420, 'nvi', '2 Crônicas', '2cr', 11, 5, 'Roboão morou em Jerusalém e reconstruiu algumas cidades para a defesa de Judá. Foram elas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11421, 'nvi', '2 Crônicas', '2cr', 11, 6, 'Belém, Etã, Tecoa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11422, 'nvi', '2 Crônicas', '2cr', 11, 7, 'Bete-Zur, Socó, Adulão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11423, 'nvi', '2 Crônicas', '2cr', 11, 8, 'Gate, Maressa, Zife,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11424, 'nvi', '2 Crônicas', '2cr', 11, 9, 'Adoraim, Láquis, Azeca,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11425, 'nvi', '2 Crônicas', '2cr', 11, 10, 'Zorá, Aijalom e Hebrom. Essas cidades foram fortificadas em Judá e em Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11426, 'nvi', '2 Crônicas', '2cr', 11, 11, 'Ele fortaleceu as suas defesas e nelas colocou comandantes, com suprimentos de alimentos, azeite e vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11427, 'nvi', '2 Crônicas', '2cr', 11, 12, 'Armazenou escudos grandes e lanças em todas as cidades, tornando-as muito fortes. Assim, Judá e Benjamim continuaram sob o seu domínio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11428, 'nvi', '2 Crônicas', '2cr', 11, 13, 'Os sacerdotes e os levitas de todos os distritos de Israel o apoiaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11429, 'nvi', '2 Crônicas', '2cr', 11, 14, 'Os levitas chegaram até a abandonar as suas pastagens e os seus bens, e foram para Judá e para Jerusalém, porque Jeroboão e seus filhos os haviam rejeitado como sacerdotes do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11430, 'nvi', '2 Crônicas', '2cr', 11, 15, 'nomeando seus próprios sacerdotes para os altares idólatras e para os ídolos que haviam feito em forma de bodes e de bezerros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11431, 'nvi', '2 Crônicas', '2cr', 11, 16, 'De todas as tribos de Israel aqueles que estavam realmente dispostos a buscar o Senhor, o Deus de Israel, seguiram os levitas até Jerusalém para oferecerem sacrifícios ao Senhor, ao Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11432, 'nvi', '2 Crônicas', '2cr', 11, 17, 'Eles fortaleceram o reino de Judá e durante três anos apoiaram Roboão, filho de Salomão, andando nos caminhos de Davi e de Salomão durante esse tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11433, 'nvi', '2 Crônicas', '2cr', 11, 18, 'Roboão casou-se com Maalate, filha de Jeremote e neta de Davi. A mãe de Maalate era Abiail, filha de Eliabe e neta de Jessé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11434, 'nvi', '2 Crônicas', '2cr', 11, 19, 'Ela deu-lhe três filhos: Jeús, Semarias e Zaão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11435, 'nvi', '2 Crônicas', '2cr', 11, 20, 'Depois ele casou-se com Maaca, filha de Absalão, a qual lhe deu os filhos Abias, Atai, Ziza e Selomite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11436, 'nvi', '2 Crônicas', '2cr', 11, 21, 'Roboão amava Maaca, filha de Absalão, mais do que a qualquer outra de suas esposas e concubinas. Ao todo ele teve dezoito esposas e sessenta concubinas, vinte e oito filhos e sessenta filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11437, 'nvi', '2 Crônicas', '2cr', 11, 22, 'Roboão nomeou Abias, filho de Maaca, o chefe entre os seus irmãos, com o intuito de fazê-lo rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11438, 'nvi', '2 Crônicas', '2cr', 11, 23, 'Ele agiu com sabedoria, dispersando seus filhos pelos distritos de Judá e de Benjamim, e pelas cidades fortificadas. Garantiu-lhes fartas provisões e lhes conseguiu muitas mulheres.');
