INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11455, 'nvi', '2 Crônicas', '2cr', 13, 1, 'No décimo oitavo ano do reinado de Jeroboão, Abias tornou-se rei de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11456, 'nvi', '2 Crônicas', '2cr', 13, 2, 'e reinou três anos em Jerusalém. O nome de sua mãe era Maaca, filha de Uriel, de Gibeá. E houve guerra entre Abias e Jeroboão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11457, 'nvi', '2 Crônicas', '2cr', 13, 3, 'Abias entrou em combate levando uma força de quatrocentos mil excelentes guerreiros, e Jeroboão foi enfrentá-lo com oitocentos mil excelentes guerreiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11458, 'nvi', '2 Crônicas', '2cr', 13, 4, 'Abias subiu o monte Zemaraim, nos montes de Efraim, e gritou: "Jeroboão e todo o Israel, ouçam-me!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11459, 'nvi', '2 Crônicas', '2cr', 13, 5, 'Vocês não sabem que o Senhor, o Deus de Israel, deu para sempre o reino de Israel a Davi e a seus descendentes mediante uma aliança irrevogável?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11460, 'nvi', '2 Crônicas', '2cr', 13, 6, 'Mesmo assim, Jeroboão, filho de Nebate, servo de Salomão, filho de Davi, rebelou-se contra o seu senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11461, 'nvi', '2 Crônicas', '2cr', 13, 7, 'Alguns homens vadios e imprestáveis juntaram-se a ele e se opuseram a Roboão, filho de Salomão, quando ainda era jovem, indeciso e incapaz de oferecer-lhes resistência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11462, 'nvi', '2 Crônicas', '2cr', 13, 8, '"E agora vocês pretendem resistir ao reino do Senhor, que está nas mãos dos descendentes de Davi. Vocês são de fato uma multidão imensa e têm os bezerros de ouro que Jeroboão fez para serem os seus deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11463, 'nvi', '2 Crônicas', '2cr', 13, 9, 'Mas, não foram vocês que expulsaram os sacerdotes do Senhor, os descendentes de Arão, e os levitas, e escolheram os seus próprios sacerdotes, como fazem os outros povos? Quem quer que se consagre com um novilho e sete carneiros pode tornar-se sacerdote daqueles que não são deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11464, 'nvi', '2 Crônicas', '2cr', 13, 10, '"Quanto a nós, o Senhor é o nosso Deus, e não o abandonamos. Os nossos sacerdotes, que servem ao Senhor auxiliados pelos levitas, são descendentes de Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11465, 'nvi', '2 Crônicas', '2cr', 13, 11, 'Todas as manhãs e todas as tardes eles apresentam holocaustos e incenso aromático ao Senhor, arrumam os pães sobre a mesa cerimonialmente pura e todas as tardes acendem as lâmpadas do candelabro de ouro. Pois nós observamos as exigências do Senhor, o nosso Deus, enquanto que vocês o abandonaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11466, 'nvi', '2 Crônicas', '2cr', 13, 12, 'E vejam bem! Deus está conosco; ele é o nosso chefe. Os sacerdotes dele, com suas cornetas, farão soar o grito de guerra contra vocês. Israelitas, não lutem contra o Senhor, o Deus dos seus antepassados, pois vocês não terão êxito! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11467, 'nvi', '2 Crônicas', '2cr', 13, 13, 'Enquanto isso, Jeroboão tinha mandado tropas para a retaguarda do exército de Judá, de forma que ele estava em frente de Judá e a emboscada estava atrás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11468, 'nvi', '2 Crônicas', '2cr', 13, 14, 'Quando o exército de Judá virou-se e viu que estava sendo atacado pela frente e pela retaguarda, clamou ao Senhor. Os sacerdotes tocaram suas cornetas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11469, 'nvi', '2 Crônicas', '2cr', 13, 15, 'e os homens de Judá deram o grito de guerra. Ao som do grito de guerra, Deus derrotou Jeroboão e todo o Israel diante de Abias e de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11470, 'nvi', '2 Crônicas', '2cr', 13, 16, 'Os israelitas fugiram dos soldados de Judá, e Deus os entregou nas mãos deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11471, 'nvi', '2 Crônicas', '2cr', 13, 17, 'Abias e os seus soldados lhes infligiram grande derrota; quinhentos mil excelentes guerreiros de Israel foram mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11472, 'nvi', '2 Crônicas', '2cr', 13, 18, 'Os israelitas foram subjugados naquela ocasião, e os homens de Judá tiveram força para vencer, pois confiaram no Senhor, o Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11473, 'nvi', '2 Crônicas', '2cr', 13, 19, 'Abias perseguiu Jeroboão e tomou-lhe as cidades de Betel, Jesana e Efrom, com os seus povoados ao redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11474, 'nvi', '2 Crônicas', '2cr', 13, 20, 'Durante o reinado de Abias, Jeroboão não recuperou o seu poder; até que o Senhor o feriu, e ele morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11475, 'nvi', '2 Crônicas', '2cr', 13, 21, 'Abias, ao contrário, fortaleceu-se. Ele se casou com catorze mulheres e teve vinte e dois filhos e dezesseis filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11476, 'nvi', '2 Crônicas', '2cr', 13, 22, 'Os demais acontecimentos do reinado de Abias, o que ele fez e o que disse, estão escritos nos relatos do profeta Ido.');
