INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11492, 'nvi', '2 Crônicas', '2cr', 15, 1, 'O Espírito de Deus veio sobre Azarias, filho de Odede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11493, 'nvi', '2 Crônicas', '2cr', 15, 2, 'Ele saiu para encontrar-se com Asa e lhe disse: "Escutem-me, Asa e todo o povo de Judá e de Benjamim. O Senhor está com vocês quando vocês estão com ele. Se o buscarem, ele deixará que o encontrem, mas, se o abandonarem, ele os abandonará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11494, 'nvi', '2 Crônicas', '2cr', 15, 3, 'Durante muito tempo Israel esteve sem o verdadeiro Deus, sem sacerdote para ensiná-lo e sem a Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11495, 'nvi', '2 Crônicas', '2cr', 15, 4, 'Mas em sua angústia eles se voltaram para o Senhor, o Deus de Israel; buscaram-no, e ele deixou que o encontrassem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11496, 'nvi', '2 Crônicas', '2cr', 15, 5, 'Naqueles dias não era seguro viajar, pois muitos distúrbios afligiam a todos os habitantes do território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11497, 'nvi', '2 Crônicas', '2cr', 15, 6, 'Nações e cidades se destruíam umas às outras, pois Deus as estava afligindo com toda espécie de desgraças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11498, 'nvi', '2 Crônicas', '2cr', 15, 7, 'Mas, vocês devem ser fortes e não se desanimar, pois o trabalho de vocês será recompensado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11499, 'nvi', '2 Crônicas', '2cr', 15, 8, 'Assim que ouviu as palavras e a profecia do profeta Azarias, filho de Odede, o rei Asa encheu-se de coragem. Retirou os ídolos repugnantes de toda a terra de Judá e de Benjamim, e das cidades que havia conquistado nos montes de Efraim, e restaurou o altar do Senhor que estava em frente do pórtico do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11500, 'nvi', '2 Crônicas', '2cr', 15, 9, 'Depois reuniu todo o povo de Judá e de Benjamim; convocou também os que pertenciam a Efraim, a Manassés e a Simeão que viviam entre eles, pois muitos de Israel tinham passado para o lado do rei Asa, ao verem que o Senhor, o seu Deus, estava com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11501, 'nvi', '2 Crônicas', '2cr', 15, 10, 'Eles se reuniram em Jerusalém no terceiro mês do décimo quinto ano do reinado de Asa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11502, 'nvi', '2 Crônicas', '2cr', 15, 11, 'Naquela ocasião sacrificaram ao Senhor setecentos bois e sete mil ovelhas e cabras, do saque que haviam feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11503, 'nvi', '2 Crônicas', '2cr', 15, 12, 'Fizeram um acordo de todo o coração e de toda a alma de buscar o Senhor, o Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11504, 'nvi', '2 Crônicas', '2cr', 15, 13, 'Todo aquele que não buscasse o Senhor, o Deus de Israel, deveria ser morto, gente simples ou importante, homem ou mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11505, 'nvi', '2 Crônicas', '2cr', 15, 14, 'Fizeram esse juramento ao Senhor em voz alta, bradando ao som de cornetas e trombetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11506, 'nvi', '2 Crônicas', '2cr', 15, 15, 'Todo o povo de Judá alegrou-se com o juramento, pois o havia feito de todo o coração. Eles buscaram a Deus com toda disposição; então ele deixou que o encontrassem e lhes concedeu paz em suas fronteiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11507, 'nvi', '2 Crônicas', '2cr', 15, 16, 'O rei Asa chegou até a depor sua avó Maaca da posição de rainha-mãe, pois ela havia feito um poste sagrado repugnante. Asa derrubou o poste, despedaçou-o e queimou-o no vale do Cedrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11508, 'nvi', '2 Crônicas', '2cr', 15, 17, 'Embora os altares idólatras não tenham sido eliminados de Israel, o coração de Asa foi totalmente dedicado ao Senhor durante toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11509, 'nvi', '2 Crônicas', '2cr', 15, 18, 'Ele trouxe para o templo de Deus a prata, o ouro e os utensílios que ele e seu pai haviam consagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11510, 'nvi', '2 Crônicas', '2cr', 15, 19, 'E não houve mais nenhuma guerra até o trigésimo quinto ano do seu reinado.');
