INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11511, 'nvi', '2 Crônicas', '2cr', 16, 1, 'No trigésimo sexto ano do reinado de Asa, Baasa, rei de Israel, invadiu Judá e fortificou Ramá, para que ninguém pudesse entrar nem sair do território de Asa, rei de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11512, 'nvi', '2 Crônicas', '2cr', 16, 2, 'Então Asa ajuntou a prata e o ouro do tesouro do templo do Senhor e do seu próprio palácio e os enviou a Ben-Hadade, rei da Síria, que governava em Damasco, com uma mensagem que dizia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11513, 'nvi', '2 Crônicas', '2cr', 16, 3, '"Façamos um tratado, como fizeram meu pai e o teu. Estou te enviando prata e ouro. Agora, rompe o tratado que tens com Baasa, rei de Israel, para que ele saia do meu país".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11514, 'nvi', '2 Crônicas', '2cr', 16, 4, 'Ben-Hadade aceitou a proposta do rei Asa e ordenou aos comandantes das suas forças que atacassem as cidades de Israel. Eles conquistaram Ijom, Dã, Abel-Maim e todas as cidades-armazéns de Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11515, 'nvi', '2 Crônicas', '2cr', 16, 5, 'Quando Baasa soube disso, abandonou a construção dos muros de Ramá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11516, 'nvi', '2 Crônicas', '2cr', 16, 6, 'Então o rei Asa reuniu todos os homens de Judá, e eles retiraram de Ramá as pedras e a madeira que Baasa estivera usando. Com esse material Asa fortificou Geba e Mispá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11517, 'nvi', '2 Crônicas', '2cr', 16, 7, 'Naquela época, o vidente Hanani foi dizer a Asa, rei de Judá: "Por você ter pedido ajuda ao rei da Síria e não ao Senhor, ao seu Deus, o exército do rei da Síria escapou de suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11518, 'nvi', '2 Crônicas', '2cr', 16, 8, 'Por acaso os etíopes e os líbios não eram um exército poderoso, com uma grande multidão de carros e cavalos? Contudo, quando você pediu ajuda ao Senhor, ele os entregou em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11519, 'nvi', '2 Crônicas', '2cr', 16, 9, 'Pois os olhos do Senhor estão atentos sobre toda a terra para fortalecer aqueles que lhe dedicam totalmente o coração. Nisso você cometeu uma loucura. De agora em diante terás que enfrentar guerras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11520, 'nvi', '2 Crônicas', '2cr', 16, 10, 'Asa irritou-se contra o vidente por causa disso; ficou tão indignado que mandou prendê-lo. Nessa época Asa oprimiu brutalmente alguns do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11521, 'nvi', '2 Crônicas', '2cr', 16, 11, 'Os demais acontecimentos do reinado de Asa, do início ao fim, estão escritos nos registros históricos dos reis de Judá e de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11522, 'nvi', '2 Crônicas', '2cr', 16, 12, 'No trigésimo nono ano de seu reinado, Asa foi atacado por uma doença nos pés. Embora a sua doença fosse grave, não buscou ajuda do Senhor, mas só dos médicos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11523, 'nvi', '2 Crônicas', '2cr', 16, 13, 'Então, no quadragésimo primeiro ano do seu reinado, Asa morreu e descansou com os seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11524, 'nvi', '2 Crônicas', '2cr', 16, 14, 'Sepultaram-no no túmulo que ele havia mandado cavar para si na cidade de Davi. Deitaram-no num leito coberto de especiarias e de vários perfumes de fina mistura, e fizeram uma imensa fogueira em sua honra.');
