INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11525, 'nvi', '2 Crônicas', '2cr', 17, 1, 'Josafá, filho de Asa, foi o seu sucessor e fortaleceu-se contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11526, 'nvi', '2 Crônicas', '2cr', 17, 2, 'Posicionou tropas em todas as cidades fortificadas de Judá e pôs guarnições em Judá e nas cidades de Efraim que seu pai, Asa, tinha conquistado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11527, 'nvi', '2 Crônicas', '2cr', 17, 3, 'O Senhor esteve com Josafá porque, em seus primeiros anos, ele andou nos caminhos que seu pai Davi tinha seguido. Não consultou os baalins,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11528, 'nvi', '2 Crônicas', '2cr', 17, 4, 'mas buscou o Deus de seu pai e obedeceu aos seus mandamentos, e não imitou as práticas de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11529, 'nvi', '2 Crônicas', '2cr', 17, 5, 'O Senhor firmou o reino de Josafá, e todo o Judá lhe trazia presentes, de maneira que teve grande riqueza e honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11530, 'nvi', '2 Crônicas', '2cr', 17, 6, 'Ele seguiu corajosamente os caminhos do Senhor; além disso, retirou de Judá os altares idólatras e os postes sagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11531, 'nvi', '2 Crônicas', '2cr', 17, 7, 'No terceiro ano de seu reinado, ele enviou seus oficiais Bene-Hail, Obadias, Zacarias, Natanael e Micaías para ensinarem nas cidades de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11532, 'nvi', '2 Crônicas', '2cr', 17, 8, 'Com eles foram os levitas Semaías, Netanias, Zebadias, Asael, Semiramote, Jônatas, Adonias, Tobias, Tobe-Adonias e os sacerdotes Elisama e Jeorão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11533, 'nvi', '2 Crônicas', '2cr', 17, 9, 'Eles percorreram todas as cidades do reino de Judá, levando consigo o Livro da Lei do Senhor e ensinando o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11534, 'nvi', '2 Crônicas', '2cr', 17, 10, 'O temor do Senhor caiu sobre todos os reinos ao redor de Judá, de forma que não entraram em guerra contra Josafá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11535, 'nvi', '2 Crônicas', '2cr', 17, 11, 'Alguns filisteus levaram presentes a Josafá, além da prata que lhe deram como tributo, e os árabes levaram-lhe rebanhos: sete mil e setecentos carneiros e sete mil e setecentos bodes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11536, 'nvi', '2 Crônicas', '2cr', 17, 12, 'Josafá foi se tornando cada vez mais poderoso; construiu fortalezas e cidades-armazéns em Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11537, 'nvi', '2 Crônicas', '2cr', 17, 13, 'onde guardava enorme quantidade de suprimentos. Também mantinha em Jerusalém homens de combate experientes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11538, 'nvi', '2 Crônicas', '2cr', 17, 14, 'A lista desses homens, por famílias, era a seguinte: De Judá, líderes de batalhões de 1. 000: o líder Adna, com 300. 000 homens de combate;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11539, 'nvi', '2 Crônicas', '2cr', 17, 15, 'em seguida, o líder Joanã, com 280. 000;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11540, 'nvi', '2 Crônicas', '2cr', 17, 16, 'depois, Amasias, filho de Zicri, que se apresentou voluntariamente para o serviço do Senhor, com 200. 000.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11541, 'nvi', '2 Crônicas', '2cr', 17, 17, 'De Benjamim: Eliada, um guerreiro valente, com 200. 000 homens armados com arcos e escudos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11542, 'nvi', '2 Crônicas', '2cr', 17, 18, 'Jeozabade, com 180. 000 homens armados para a batalha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11543, 'nvi', '2 Crônicas', '2cr', 17, 19, 'Esses eram os homens que serviam o rei, além dos que estavam posicionados nas cidades fortificadas em todo o Judá.');
