INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11589, 'nvi', '2 Crônicas', '2cr', 20, 1, 'Depois disso, os moabitas e os amonitas, com alguns dos meunitas, entraram em guerra contra Josafá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11590, 'nvi', '2 Crônicas', '2cr', 20, 2, 'Então informaram a Josafá: "Um exército enorme vem contra ti de Edom, do outro lado do mar Morto. Já está em Hazazom-Tamar, isto é, En-Gedi".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11591, 'nvi', '2 Crônicas', '2cr', 20, 3, 'Alarmado, Josafá decidiu consultar o Senhor e proclamou um jejum em todo o reino de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11592, 'nvi', '2 Crônicas', '2cr', 20, 4, 'Reuniu-se, pois, o povo, vindo de todas as cidades de Judá para buscar a ajuda do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11593, 'nvi', '2 Crônicas', '2cr', 20, 5, 'Então Josafá levantou-se na assembléia de Judá e de Jerusalém, no templo do Senhor, na frente do pátio novo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11594, 'nvi', '2 Crônicas', '2cr', 20, 6, 'e orou: "Senhor, Deus dos nossos antepassados, não és tu o Deus que está nos céus? Tu governas sobre todos os reinos do mundo. Força e poder estão em tuas mãos, e ninguém pode opor-se a ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11595, 'nvi', '2 Crônicas', '2cr', 20, 7, 'Não és tu o nosso Deus, que expulsaste os habitantes desta terra perante Israel, teu povo, e a deste para sempre aos descendentes de teu amigo Abraão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11596, 'nvi', '2 Crônicas', '2cr', 20, 8, 'Eles a têm habitado e nela construíram um santuário em honra do teu nome, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11597, 'nvi', '2 Crônicas', '2cr', 20, 9, '‘Se alguma desgraça nos atingir, seja o castigo da espada, seja a peste, seja a fome, nós nos colocaremos em tua presença diante deste templo, pois ele leva o teu nome, e clamaremos a ti em nossa angústia, e tu nos ouvirás e nos salvarás’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11598, 'nvi', '2 Crônicas', '2cr', 20, 10, '"Mas agora, aí estão amonitas, moabitas e habitantes dos montes de Seir, cujos territórios não permitiste que Israel invadisse quando vinha do Egito; por isso os israelitas se desviaram deles e não os destruíram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11599, 'nvi', '2 Crônicas', '2cr', 20, 11, 'Vê agora como estão nos retribuindo, ao virem expulsar-nos da terra que nos deste por herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11600, 'nvi', '2 Crônicas', '2cr', 20, 12, 'Ó nosso Deus, não irás tu julgá-los? Pois não temos força para enfrentar esse exército imenso que está nos atacando. Não sabemos o que fazer, mas os nossos olhos se voltam para ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11601, 'nvi', '2 Crônicas', '2cr', 20, 13, 'Todos os homens de Judá, com suas mulheres e seus filhos, até os de colo, estavam ali de pé, diante do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11602, 'nvi', '2 Crônicas', '2cr', 20, 14, 'Então o Espírito do Senhor veio sobre Jaaziel, filho de Zacarias, neto de Benaia, bisneto de Jeiel e trineto de Matanias, levita e descendente de Asafe, no meio da assembléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11603, 'nvi', '2 Crônicas', '2cr', 20, 15, 'Ele disse: "Escutem, todos os que vivem em Judá e em Jerusalém e o rei Josafá! Assim lhes diz o Senhor: ‘Não tenham medo nem fiquem desanimados por causa desse exército enorme. Pois a batalha não é de vocês, mas de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11604, 'nvi', '2 Crônicas', '2cr', 20, 16, 'Amanhã, desçam contra eles. Eles virão pela subida de Ziz, e vocês os encontrarão no fim do vale, em frente do deserto de Jeruel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11605, 'nvi', '2 Crônicas', '2cr', 20, 17, 'Vocês não precisarão lutar nessa batalha. Tomem suas posições; permaneçam firmes e vejam o livramento que o Senhor lhes dará, ó Judá, ó Jerusalém. Não tenham medo nem se desanimem. Saiam para enfrentá-los amanhã, e o Senhor estará com vocês’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11606, 'nvi', '2 Crônicas', '2cr', 20, 18, 'Josafá prostrou-se, rosto em terra, e todo o povo de Judá e de Jerusalém prostrou-se em adoração perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11607, 'nvi', '2 Crônicas', '2cr', 20, 19, 'Então os levitas descendentes dos coatitas e dos coreítas levantaram-se e louvaram o Senhor, o Deus de Israel, em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11608, 'nvi', '2 Crônicas', '2cr', 20, 20, 'De madrugada partiram para o deserto de Tecoa. Quando estavam saindo, Josafá lhes disse: "Escutem-me, Judá e povo de Jerusalém! Tenham fé no Senhor, o seu Deus, e vocês serão sustentados; tenham fé nos profetas dele e vocês terão a vitória".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11609, 'nvi', '2 Crônicas', '2cr', 20, 21, 'Depois de consultar o povo, Josafá nomeou alguns homens para cantarem ao Senhor e o louvarem pelo esplendor de sua santidade, indo à frente do exército, cantando: "Dêem graças ao Senhor, pois o seu amor dura para sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11610, 'nvi', '2 Crônicas', '2cr', 20, 22, 'Quando começaram a cantar e a entoar louvores, o Senhor preparou emboscadas contra os homens de Amom, de Moabe e dos montes de Seir que estavam invadindo Judá, e eles foram derrotados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11611, 'nvi', '2 Crônicas', '2cr', 20, 23, 'Os amonitas e os moabitas atacaram os dos montes de Seir para destruí-los e aniquilá-los. Depois de massacrarem os homens de Seir, destruiram-se uns aos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11612, 'nvi', '2 Crônicas', '2cr', 20, 24, 'Quando os homens de Judá foram para o lugar de onde se avista o deserto e olharam para o imenso exército, viram somente cadáveres no chão; ninguém havia escapado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11613, 'nvi', '2 Crônicas', '2cr', 20, 25, 'Então, Josafá e os seus soldados foram saquear os cadáveres e encontraram entre eles grande quantidade de equipamentos e roupas, e também objetos de valor; passaram três dias saqueando, mas havia mais do que eram capazes de levar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11614, 'nvi', '2 Crônicas', '2cr', 20, 26, 'No quarto dia eles se reuniram no vale de Beraca, onde louvaram o Senhor. Por isso até hoje ele é chamado vale de Beraca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11615, 'nvi', '2 Crônicas', '2cr', 20, 27, 'Depois sob a liderança de Josafá, todos os homens de Judá e de Jerusalém voltaram alegres para Jerusalém, pois o Senhor lhes enchera de alegria, dando-lhes vitória sobre os seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11616, 'nvi', '2 Crônicas', '2cr', 20, 28, 'Entraram em Jerusalém e foram ao templo do Senhor, ao som de liras, harpas e cornetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11617, 'nvi', '2 Crônicas', '2cr', 20, 29, 'O temor de Deus veio sobre todas as nações, quando souberam como o Senhor havia lutado contra os inimigos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11618, 'nvi', '2 Crônicas', '2cr', 20, 30, 'E o reino de Josafá manteve-se em paz, pois o seu Deus lhe concedeu paz em todas as suas fronteiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11619, 'nvi', '2 Crônicas', '2cr', 20, 31, 'Assim Josafá reinou sobre Judá. Ele tinha trinta e cinco anos de idade quando se tornou rei, e reinou vinte e cinco anos em Jerusalém. O nome da sua mãe era Azuba, filha de Sili.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11620, 'nvi', '2 Crônicas', '2cr', 20, 32, 'Ele andou nos caminhos de Asa, seu pai, e não se desviou deles; fez o que o Senhor aprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11621, 'nvi', '2 Crônicas', '2cr', 20, 33, 'Contudo, não acabou com os altares idólatras, e o povo ainda não havia firmado o coração no Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11622, 'nvi', '2 Crônicas', '2cr', 20, 34, 'Os demais acontecimentos do reinado de Josafá, do início ao fim, estão escritos nos relatos de Jeú, filho de Hanani, e incluídos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11623, 'nvi', '2 Crônicas', '2cr', 20, 35, 'Posteriormente, Josafá, rei de Judá, fez um tratado com Acazias, rei de Israel, que tinha vida ímpia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11624, 'nvi', '2 Crônicas', '2cr', 20, 36, 'Era um tratado para a construção de navios mercantes. Depois de serem construídos os navios em Eziom-Geber,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11625, 'nvi', '2 Crônicas', '2cr', 20, 37, 'Eliézer, filho de Dodava de Maressa, profetizou contra Josafá, dizendo: "Por haver feito um tratado com Acazias, o Senhor destruirá o que você fez". Assim, os navios naufragaram e não puderam sair para negociar.');
