INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11626, 'nvi', '2 Crônicas', '2cr', 21, 1, 'Josafá descansou com seus antepassados e foi sepultado junto deles na cidade de Davi, e seu filho Jeorão foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11627, 'nvi', '2 Crônicas', '2cr', 21, 2, 'Os irmãos de Jeorão, filhos de Josafá, foram Azarias, Jeiel, Zacarias, Azarias, Micael e Sefatias. Todos eles foram filhos de Josafá, rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11628, 'nvi', '2 Crônicas', '2cr', 21, 3, 'Ele deu-lhes muitos presentes de prata e ouro e objetos de valor, bem como cidades fortificadas em Judá, mas o reino deu a Jeorão, porque este era seu filho mais velho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11629, 'nvi', '2 Crônicas', '2cr', 21, 4, 'Logo Jeorão se fortaleceu no reino de seu pai, e matou à espada todos os seus irmãos, juntamente com alguns dos líderes de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11630, 'nvi', '2 Crônicas', '2cr', 21, 5, 'Ele tinha trinta e dois anos de idade quando começou a reinar, e reinou oito anos em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11631, 'nvi', '2 Crônicas', '2cr', 21, 6, 'Andou nos caminhos dos reis de Israel, como a família de Acabe havia feito, pois se casou com uma filha de Acabe. E fez o que o Senhor reprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11632, 'nvi', '2 Crônicas', '2cr', 21, 7, 'Entretanto, por causa da aliança que havia feito com Davi, o Senhor não quis destruir a dinastia dele. Ele havia prometido manter para sempre um descendente de Davi no trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11633, 'nvi', '2 Crônicas', '2cr', 21, 8, 'Nos dias de Jeorão, os edomitas rebelaram-se contra o domínio de Judá, proclamando seu próprio rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11634, 'nvi', '2 Crônicas', '2cr', 21, 9, 'Por isso, Jeorão foi combatê-los com seus líderes e com todos os seus carros de guerra. Os edomitas cercaram a ele e aos chefes de seus carros de guerra, mas ele os atacou de noite e rompeu o cerco inimigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11635, 'nvi', '2 Crônicas', '2cr', 21, 10, 'E até hoje Edom continua independente de Judá. Nessa mesma época, a cidade de Libna também tornou-se independente, pois Jeorão havia abandonado o Senhor, o Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11636, 'nvi', '2 Crônicas', '2cr', 21, 11, 'Ele até construiu altares idólatras nas colinas de Judá, levando o povo de Jerusalém a prostituir-se e Judá a desviar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11637, 'nvi', '2 Crônicas', '2cr', 21, 12, 'Então Jeorão recebeu uma carta do profeta Elias, que dizia: "Assim diz o Senhor, o Deus de Davi, seu antepassado: ‘Você não tem andado nos caminhos de seu pai Josafá nem de Asa, rei de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11638, 'nvi', '2 Crônicas', '2cr', 21, 13, 'Mas sim nos caminhos dos reis de Israel, e levou Judá e o povo de Jerusalém a se prostituírem na idolatria como a família de Acabe. E ainda assassinou seus próprios irmãos, membros da família de seu pai, homens que eram melhores do que você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11639, 'nvi', '2 Crônicas', '2cr', 21, 14, 'Por isso, o Senhor vai ferir terrivelmente seu povo, seus filhos, suas mulheres e tudo o que é seu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11640, 'nvi', '2 Crônicas', '2cr', 21, 15, 'Você ficará muito doente; terá uma enfermidade no ventre, que irá piorar até que saiam os seus intestinos’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11641, 'nvi', '2 Crônicas', '2cr', 21, 16, 'O Senhor despertou contra Jeorão a hostilidade dos filisteus e dos árabes que viviam perto dos etíopes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11642, 'nvi', '2 Crônicas', '2cr', 21, 17, 'Eles atacaram o reino de Judá, invadiram-no e levaram todos os bens que encontraram no palácio do rei, juntamente com suas mulheres e seus filhos. Só ficou Acazias, o filho mais novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11643, 'nvi', '2 Crônicas', '2cr', 21, 18, 'Depois disso tudo, o Senhor afligiu Jeorão com uma doença incurável nos intestinos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11644, 'nvi', '2 Crônicas', '2cr', 21, 19, 'Algum tempo depois, ao fim do segundo ano, tanto se agravou a doença que os seus intestinos saíram, e ele morreu sofrendo dores horríveis. Seu povo não fez nenhuma fogueira em sua homenagem, como havia feito para os seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11645, 'nvi', '2 Crônicas', '2cr', 21, 20, 'Jeorão tinha trinta e dois anos de idade quando começou a reinar, e reinou oito anos em Jerusalém. Morreu sem que ninguém o lamentasse, e foi sepultado na cidade de Davi, mas não nos túmulos dos reis.');
