INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11658, 'nvi', '2 Crônicas', '2cr', 23, 1, 'No sétimo ano Joiada encorajou-se e fez um acordo com os líderes dos batalhões de cem: Azarias, filho de Jeroão, Ismael, filho de Joanã, Azarias, filho de Obede, Maaséias, filho de Adaías, e Elisafate, filho de Zicri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11659, 'nvi', '2 Crônicas', '2cr', 23, 2, 'Eles percorreram todo o Judá e reuniram de todas as cidades, os levitas e os chefes das famílias israelitas. Quando chegaram a Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11660, 'nvi', '2 Crônicas', '2cr', 23, 3, 'toda a assembléia fez um acordo com o rei no templo de Deus. Joiada lhes disse: "Reinará o filho do rei, conforme o Senhor prometeu acerca dos descendentes de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11661, 'nvi', '2 Crônicas', '2cr', 23, 4, 'Vocês vão fazer o seguinte: Um terço de vocês, sacerdotes e levitas que entrarão de serviço no sábado, deverá ficar vigiando nas portas do templo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11662, 'nvi', '2 Crônicas', '2cr', 23, 5, 'um terço, no palácio real; e um terço, na porta do Alicerce; e todo o povo estará nos pátios do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11663, 'nvi', '2 Crônicas', '2cr', 23, 6, 'Ninguém deverá entrar no templo do Senhor, exceto os sacerdotes e os levitas de serviço; esses podem entrar porque são consagrados, mas o povo deverá observar o que o Senhor lhes determinou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11664, 'nvi', '2 Crônicas', '2cr', 23, 7, 'Os levitas deverão posicionar-se em torno do rei, todos de armas na mão. Matem todo aquele que entrar no templo. Acompanhem o rei aonde quer que ele for".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11665, 'nvi', '2 Crônicas', '2cr', 23, 8, 'Os levitas e todos os homens de Judá fizeram como o sacerdote Joiada havia ordenado. Cada um levou seus soldados, tanto os que estavam entrando de serviço no sábado como os que estavam saindo, pois o sacerdote Joiada não havia dispensado nenhuma das divisões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11666, 'nvi', '2 Crônicas', '2cr', 23, 9, 'Então ele deu aos líderes dos batalhões de cem, as lanças e os escudos grandes e pequenos que haviam pertencido ao rei Davi, e que estavam no templo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11667, 'nvi', '2 Crônicas', '2cr', 23, 10, 'Posicionou todos os homens, todos de arma na mão, em volta do rei, perto do altar e do templo, desde o lado sul até o lado norte do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11668, 'nvi', '2 Crônicas', '2cr', 23, 11, 'Joiada e seus filhos trouxeram o filho do rei e o coroaram; entregaram-lhe uma cópia da aliança e o proclamaram rei, ungindo-o e gritando: "Viva o rei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11669, 'nvi', '2 Crônicas', '2cr', 23, 12, 'Quando Atalia ouviu o barulho do povo correndo e aclamando o rei, foi ao templo do Senhor, onde estava o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11670, 'nvi', '2 Crônicas', '2cr', 23, 13, 'Lá ela viu o rei à entrada, de pé junto à coluna. Os oficiais e tocadores de cornetas estavam ao lado do rei, e todo o povo se alegrava ao som das cornetas; os músicos com seus instrumentos musicais dirigiam os louvores. Então Atalia rasgou suas vestes e gritou: "Traição! Traição! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11671, 'nvi', '2 Crônicas', '2cr', 23, 14, 'O sacerdote Joiada ordenou aos líderes dos batalhões de cem que estavam no comando das tropas: "Levem-na para fora por entre as fileiras, e matem à espada todo que a seguir". Pois o sacerdote dissera: "Não a matem no templo do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11672, 'nvi', '2 Crônicas', '2cr', 23, 15, 'Então eles a prenderam e levaram à porta dos Cavalos, no terreno do palácio, e lá a mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11673, 'nvi', '2 Crônicas', '2cr', 23, 16, 'E Joiada fez um acordo pelo qual ele, o povo e o rei seriam o povo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11674, 'nvi', '2 Crônicas', '2cr', 23, 17, 'Então todo o povo foi ao templo de Baal e o derrubou. Despedaçaram os altares e os ídolos, e mataram Matã, sacerdote de Baal, em frente dos altares.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11675, 'nvi', '2 Crônicas', '2cr', 23, 18, 'E Joiada confiou a supervisão do templo do Senhor aos sacerdotes levitas, aos quais Davi tinha atribuído tarefas no templo, para apresentarem os holocaustos ao Senhor, conforme está escrito na lei de Moisés, com júbilo e cânticos, segundo as instruções de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11676, 'nvi', '2 Crônicas', '2cr', 23, 19, 'Também pôs guardas nas portas do templo do Senhor para que não entrasse ninguém que de alguma forma estivesse impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11677, 'nvi', '2 Crônicas', '2cr', 23, 20, 'Levou consigo os líderes dos batalhões de cem, os nobres, os governantes do povo e todo o povo e, juntos, conduziram o rei do templo do Senhor ao palácio, passando pela porta superior, e instalaram o rei no trono;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11678, 'nvi', '2 Crônicas', '2cr', 23, 21, 'e todo o povo se alegrou. A cidade acalmou-se depois que Atalia foi morta à espada.');
