INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11829, 'nvi', '2 Crônicas', '2cr', 30, 1, 'Ezequias enviou uma mensagem a todo o Israel e Judá e também escreveu cartas a Efraim e a Manassés, convidando-os para ir ao templo do Senhor em Jerusalém e celebrar a Páscoa do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11830, 'nvi', '2 Crônicas', '2cr', 30, 2, 'O rei, seus oficiais e toda a comunidade de Jerusalém decidiram celebrar a Páscoa no segundo mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11831, 'nvi', '2 Crônicas', '2cr', 30, 3, 'Não tinha sido possível celebrá-la na data prescrita, pois não havia número suficiente de sacerdotes consagrados, e o povo não estava reunido em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11832, 'nvi', '2 Crônicas', '2cr', 30, 4, 'A idéia pareceu boa tanto ao rei quanto a toda a assembléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11833, 'nvi', '2 Crônicas', '2cr', 30, 5, 'Então decidiram fazer uma proclamação em todo o Israel, desde Berseba até Dã, convocando o povo a Jerusalém para celebrar a Páscoa do Senhor, o Deus de Israel. Pois muitos não a celebravam segundo o que estava escrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11834, 'nvi', '2 Crônicas', '2cr', 30, 6, 'Por ordem do rei, mensageiros percorreram Israel e Judá com cartas assinadas pelo rei e pelos seus oficiais, com a seguinte mensagem: "Israelitas, voltem para o Senhor, o Deus de Abraão, de Isaque e de Israel, para que ele se volte para vocês que restaram e escaparam das mãos dos reis da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11835, 'nvi', '2 Crônicas', '2cr', 30, 7, 'Não sejam como seus pais e seus irmãos, que foram infiéis ao Senhor, o Deus dos seus antepassados, de maneira que ele os deixou em ruínas, conforme vocês vêem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11836, 'nvi', '2 Crônicas', '2cr', 30, 8, 'Portanto não sejam obstinados como seus antepassados; submetam-se ao Senhor. Venham ao santuário que ele consagrou para sempre. Sirvam ao Senhor, ao seu Deus, para que o fogo da sua ira se desvie de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11837, 'nvi', '2 Crônicas', '2cr', 30, 9, 'Se vocês voltarem para o Senhor, os que capturaram os seus irmãos e os seus filhos terão misericórdia deles, e eles voltarão a esta terra, pois o Senhor, o seu Deus, é bondoso e compassivo. Ele não os rejeitará, se vocês se voltarem para ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11838, 'nvi', '2 Crônicas', '2cr', 30, 10, 'Os mensageiros foram de cidade em cidade, em Efraim e em Manassés, e até em Zebulom, mas o povo zombou deles e os expôs ao ridículo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11839, 'nvi', '2 Crônicas', '2cr', 30, 11, 'No entanto, alguns homens de Aser, de Manassés e de Zebulom humilharam-se e foram para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11840, 'nvi', '2 Crônicas', '2cr', 30, 12, 'Já em Judá a mão de Deus esteve sobre o povo dando-lhes unidade de pensamento para executarem o que o rei e os seus oficiais haviam ordenado, conforme a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11841, 'nvi', '2 Crônicas', '2cr', 30, 13, 'Uma imensa multidão reuniu-se em Jerusalém no segundo mês, para celebrar a festa dos pães sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11842, 'nvi', '2 Crônicas', '2cr', 30, 14, 'Eles retiraram os altares que havia em Jerusalém e se desfizeram de todos os altares de incenso, atirando-os no vale de Cedrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11843, 'nvi', '2 Crônicas', '2cr', 30, 15, 'Abateram o cordeiro da Páscoa no dia catorze do segundo mês. Os sacerdotes e os levitas, envergonhados, consagraram-se e levaram holocaustos ao templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11844, 'nvi', '2 Crônicas', '2cr', 30, 16, 'E assumiram seus postos, conforme prescrito na Lei de Moisés, homem de Deus. Os sacerdotes aspergiram o sangue que os levitas lhes entregaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11845, 'nvi', '2 Crônicas', '2cr', 30, 17, 'Visto que muitos na multidão não haviam se consagrado, os levitas tiveram que matar cordeiros da Páscoa para todos os que não estavam cerimonialmente puros e que por isso não podiam consagrar os seus cordeiros ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11846, 'nvi', '2 Crônicas', '2cr', 30, 18, 'Embora muitos dos que vieram de Efraim, de Manassés, de Issacar e de Zebulom não se tivessem purificado, assim mesmo comeram a Páscoa, contrariando o que estava escrito. Mas Ezequias orou por eles, dizendo: "Queira o Senhor, que é bondoso, perdoar todo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11847, 'nvi', '2 Crônicas', '2cr', 30, 19, 'aquele que inclina o seu coração para buscar a Deus, o Senhor, o Deus dos seus antepassados, mesmo que não esteja puro de acordo com as regras do santuário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11848, 'nvi', '2 Crônicas', '2cr', 30, 20, 'E o Senhor ouviu a oração de Ezequias e não castigou o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11849, 'nvi', '2 Crônicas', '2cr', 30, 21, 'Os israelitas presentes em Jerusalém celebraram com muita alegria a festa dos pães sem fermento durante sete dias. Diariamente os levitas e os sacerdotes cantavam louvores, ao som dos instrumentos ressonantes do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11850, 'nvi', '2 Crônicas', '2cr', 30, 22, 'Ezequias dirigiu palavras animadoras a todos os levitas, que mostraram boa disposição para com o serviço do Senhor. Durante os sete dias eles comeram suas porções das ofertas, apresentaram sacrifícios de comunhão e louvaram o Senhor, o Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11851, 'nvi', '2 Crônicas', '2cr', 30, 23, 'E toda a assembléia decidiu prolongar a festa por mais sete dias, e a celebraram com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11852, 'nvi', '2 Crônicas', '2cr', 30, 24, 'Ezequias, rei de Judá, forneceu mil novilhos e sete mil ovelhas e bodes para a assembléia, e os líderes, mil novilhos e dez mil ovelhas e bodes. Muitos sacerdotes se consagraram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11853, 'nvi', '2 Crônicas', '2cr', 30, 25, 'e toda a assembléia de Judá se regozijava, juntamente com os sacerdotes, com os levitas e com todos os que se haviam reunido, vindos de Israel, inclusive os estrangeiros que viviam em Israel e em Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11854, 'nvi', '2 Crônicas', '2cr', 30, 26, 'Houve grande alegria em Jerusalém, pois desde os dias de Salomão, filho de Davi, rei de Israel, não havia acontecido algo assim na cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11855, 'nvi', '2 Crônicas', '2cr', 30, 27, 'Os sacerdotes e os levitas levantaram-se para abençoar o povo, e Deus os ouviu; a oração deles chegou aos céus, sua santa habitação.');
