INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11968, 'nvi', '2 Crônicas', '2cr', 35, 1, 'Josias celebrou a Páscoa do Senhor em Jerusalém, e o cordeiro da Páscoa foi abatido no dia catorze do primeiro mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11969, 'nvi', '2 Crônicas', '2cr', 35, 2, 'Ele nomeou os sacerdotes para as suas responsabilidades e os encorajou a dedicar-se ao serviço no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11970, 'nvi', '2 Crônicas', '2cr', 35, 3, 'Ele disse aos levitas que instruíam todo Israel e haviam sido consagrados ao Senhor: "Ponham a arca sagrada no templo construído por Salomão, filho de Davi, rei de Israel. Vocês não precisam mais levá-la de um lado para outro sobre os ombros. Agora sirvam ao Senhor, ao seu Deus, e ao povo dele, Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11971, 'nvi', '2 Crônicas', '2cr', 35, 4, 'Preparem-se por famílias, em suas divisões, de acordo com a orientação escrita por Davi, rei de Israel, e por seu filho Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11972, 'nvi', '2 Crônicas', '2cr', 35, 5, '"Fiquem no lugar santo com um grupo de levitas para cada subdivisão das famílias do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11973, 'nvi', '2 Crônicas', '2cr', 35, 6, 'Abatam os cordeiros da Páscoa, consagrem-se e preparem os cordeiros para os seus irmãos israelitas, fazendo o que o Senhor ordenou por meio de Moisés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11974, 'nvi', '2 Crônicas', '2cr', 35, 7, 'Josias deu a todo o povo que ali estava um total de trinta mil ovelhas e cabritos para as ofertas da Páscoa, além de três mil bois; tudo foi tirado dos bens pessoais do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11975, 'nvi', '2 Crônicas', '2cr', 35, 8, 'Seus oficiais também contribuíram voluntariamente para o povo, para os sacerdotes e para os levitas. Hilquias, Zacarias e Jeiel, os administradores do templo de Deus, deram aos sacerdotes duas mil e seiscentas ovelhas e cabritos e trezentos bois.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11976, 'nvi', '2 Crônicas', '2cr', 35, 9, 'Também Conanias, junto com seus irmãos Semaías, Natanael e com os líderes dos levitas, Hasabias, Jeiel e Jozabade, ofereceram aos levitas cinco mil ovelhas e cabritos e quinhentos bois.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11977, 'nvi', '2 Crônicas', '2cr', 35, 10, 'O serviço foi organizado e os sacerdotes assumiram os seus lugares com os levitas em seus turnos, conforme o rei ordenara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11978, 'nvi', '2 Crônicas', '2cr', 35, 11, 'Os cordeiros da Páscoa foram abatidos, e os sacerdotes aspergiram o sangue que lhes fora entregue, enquanto os levitas tiravam a pele dos animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11979, 'nvi', '2 Crônicas', '2cr', 35, 12, 'Eles separaram também os holocaustos para dá-los aos grupos das famílias do povo, para que essas os oferecessem ao Senhor, conforme está escrito no livro de Moisés; e fizeram o mesmo com os bois.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11980, 'nvi', '2 Crônicas', '2cr', 35, 13, 'Assaram os animais da Páscoa sobre o fogo, conforme prescrito, cozinharam as ofertas sagradas em potes, caldeirões e panelas, e serviram rapidamente a todo o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11981, 'nvi', '2 Crônicas', '2cr', 35, 14, 'Depois disso, os levitas prepararam a parte deles e a dos sacerdotes, pois estes, descendentes de Arão, ficaram sacrificando os holocaustos e as porções de gordura até o anoitecer. Foi por isso que os levitas prepararam a parte deles e a dos sacerdotes, descendentes de Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11982, 'nvi', '2 Crônicas', '2cr', 35, 15, 'Os músicos, descendentes de Asafe, estavam nos locais prescritos por Davi, e por Asafe, Hemã e Jedutum, videntes do rei. Os porteiros que guardavam cada porta não precisaram deixar os seus postos, pois os seus colegas levitas prepararam as ofertas para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11983, 'nvi', '2 Crônicas', '2cr', 35, 16, 'Assim, naquele dia, todo o serviço do Senhor foi executado para a celebração da Páscoa e para a apresentação de holocaustos no altar do Senhor, conforme o rei Josias havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11984, 'nvi', '2 Crônicas', '2cr', 35, 17, 'Os israelitas que estavam presentes celebraram a Páscoa naquele dia e durante sete dias celebraram a festa dos pães sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11985, 'nvi', '2 Crônicas', '2cr', 35, 18, 'A Páscoa não havia sido celebrada dessa maneira em Israel desde os dias do profeta Samuel; e nenhum dos reis de Israel havia celebrado uma Páscoa como esta, como o fez Josias, com os sacerdotes, os levitas e todo o Judá e Israel que estavam ali com o povo de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11986, 'nvi', '2 Crônicas', '2cr', 35, 19, 'Esta Páscoa foi celebrada no décimo oitavo ano do reinado de Josias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11987, 'nvi', '2 Crônicas', '2cr', 35, 20, 'Depois de tudo o que Josias fez, e depois de colocar em ordem o templo, Neco, rei do Egito, saiu para lutar em Carquemis, junto ao Eufrates, e Josias marchou para combatê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11988, 'nvi', '2 Crônicas', '2cr', 35, 21, 'Neco, porém, enviou-lhe mensageiros, dizendo: "Não interfiras nisso, ó rei de Judá. Desta vez não estou atacando a ti, mas a outro reino com o qual estou em guerra. Deus me disse que me apressasse; por isso pára de opor-se a Deus, que está comigo; caso contrário ele o destruirá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11989, 'nvi', '2 Crônicas', '2cr', 35, 22, 'Josias, contudo, não quis voltar atrás, e disfarçou-se para enfrentá-lo em combate. Ele não quis ouvir o que Neco lhe disse por ordem de Deus, mas foi combatê-lo na planície de Megido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11990, 'nvi', '2 Crônicas', '2cr', 35, 23, 'E na batalha, flecheiros atingiram o rei Josias, pelo que disse aos seus oficiais: "Tirem-me daqui. Estou gravemente ferido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11991, 'nvi', '2 Crônicas', '2cr', 35, 24, 'Eles o tiraram do seu carro, colocaram-no em outro e o levaram para Jerusalém, onde morreu. Ele foi sepultado nos túmulos dos seus antepassados, e todos os moradores de Judá e de Jerusalém choraram por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11992, 'nvi', '2 Crônicas', '2cr', 35, 25, 'Jeremias compôs um cântico de lamento em homenagem a Josias, e até hoje todos os cantores e cantoras homenageiam Josias com cânticos de lamento. Estes se tornaram uma tradição em Israel e estão escritos na Coletânea das Lamentações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11993, 'nvi', '2 Crônicas', '2cr', 35, 26, 'Os demais acontecimentos do reinado de Josias e os seus atos piedosos, de acordo com o que está escrito na Lei do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11994, 'nvi', '2 Crônicas', '2cr', 35, 27, 'todos os acontecimentos, do início ao fim, estão escritos nos registros históricos dos reis de Israel e de Judá.');
