INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11248, 'nvi', '2 Crônicas', '2cr', 4, 1, 'Salomão também mandou fazer um altar de bronze de nove metros de comprimento, nove metros de largura e quatro metros e meio de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11249, 'nvi', '2 Crônicas', '2cr', 4, 2, 'Fez o tanque de metal fundido, redondo, medindo quatro metros e meio de diâmetro e dois metros e vinte e cinco centímetros de altura. Era preciso um fio de treze metros e meio para medir a sua circunferência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11250, 'nvi', '2 Crônicas', '2cr', 4, 3, 'Abaixo da borda e ao seu redor havia figuras de touro, de cinco em cinco centímetros. Os touros foram fundidos em duas fileiras numa só peça com o tanque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11251, 'nvi', '2 Crônicas', '2cr', 4, 4, 'O tanque ficava sobre doze touros, três voltados para o norte, três para o oeste, três para o sul e três para o leste. Ficava em cima deles, e as pernas traseiras dos touros eram voltadas para o centro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11252, 'nvi', '2 Crônicas', '2cr', 4, 5, 'A espessura do tanque era de quatro dedos, e sua borda era como a borda de um cálice, como uma flor de lírio. Tinha capacidade de sessenta e seis mil litros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11253, 'nvi', '2 Crônicas', '2cr', 4, 6, 'Fez dez pias, colocando cinco no lado sul e cinco no lado norte. Nelas tudo o que era usado nos holocaustos era lavado, enquanto que o tanque servia para os sacerdotes se lavarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11254, 'nvi', '2 Crônicas', '2cr', 4, 7, 'Fez dez candelabros de ouro, de acordo com as especificações, e colocou-os no templo, cinco no lado sul e cinco no lado norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11255, 'nvi', '2 Crônicas', '2cr', 4, 8, 'Fez dez mesas e colocou-as no templo, cinco no lado sul e cinco no lado norte. Também fez cem bacias de ouro para aspersão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11256, 'nvi', '2 Crônicas', '2cr', 4, 9, 'E fez o pátio dos sacerdotes e o pátio principal com suas portas, e revestiu de bronze as suas portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11257, 'nvi', '2 Crônicas', '2cr', 4, 10, 'Pôs o tanque no lado sul, no canto sudeste do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11258, 'nvi', '2 Crônicas', '2cr', 4, 11, 'Também fez os jarros, as pás e as bacias para aspersão. Assim, Hirão terminou o trabalho de que fora encarregado pelo rei Salomão, no templo de Deus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11259, 'nvi', '2 Crônicas', '2cr', 4, 12, 'as duas colunas; os dois capitéis em forma de taça no alto das colunas; os dois conjuntos de correntes que decoravam os dois capitéis;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11260, 'nvi', '2 Crônicas', '2cr', 4, 13, 'as quatrocentas romãs para os dois conjuntos de correntes, sendo duas fileiras de romãs para cada conjunto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11261, 'nvi', '2 Crônicas', '2cr', 4, 14, 'os dez carrinhos com as suas dez pias;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11262, 'nvi', '2 Crônicas', '2cr', 4, 15, 'o tanque e os doze touros debaixo dele;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11263, 'nvi', '2 Crônicas', '2cr', 4, 16, 'e os jarros, as pás, os garfos de carne e todos os utensílios afins. Todos esses utensílios que Hirão-Abi fez a pedido do rei Salomão, para o templo do Senhor, eram de bronze polido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11264, 'nvi', '2 Crônicas', '2cr', 4, 17, 'Foi na planície do Jordão, entre Sucote e Zeredá, que o rei os mandou fundir, em moldes de barro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11265, 'nvi', '2 Crônicas', '2cr', 4, 18, 'Salomão os fez em tão grande quantidade, a ponto de não se poder determinar o peso do bronze utilizado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11266, 'nvi', '2 Crônicas', '2cr', 4, 19, 'Além desses, Salomão mandou fazer também todos estes outros utensílios para o templo de Deus: o altar de ouro; as mesas sobre as quais ficavam os pães da Presença;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11267, 'nvi', '2 Crônicas', '2cr', 4, 20, 'os candelabros de ouro puro com suas lâmpadas, para iluminarem diante do santuário interno, conforme determinado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11268, 'nvi', '2 Crônicas', '2cr', 4, 21, 'as flores, as lâmpadas e as tenazes de ouro maciço;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11269, 'nvi', '2 Crônicas', '2cr', 4, 22, 'os cortadores de pavio, as bacias para aspersão, as tigelas, os incensários de ouro puro e as portas de ouro do templo: tanto as portas da sala interna, o Lugar Santíssimo, quanto as portas do átrio principal.');
