INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11366, 'nvi', '2 Crônicas', '2cr', 9, 1, 'A rainha de Sabá soube da fama de Salomão, e foi a Jerusalém para pô-lo à prova com perguntas difíceis. Quando chegou, acompanhada de uma enorme caravana, com camelos carregados de especiarias, grande quantidade de ouro e pedras preciosas, foi até Salomão e lhe fez todas as perguntas que tinha em mente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11367, 'nvi', '2 Crônicas', '2cr', 9, 2, 'Salomão respondeu a todas; nenhuma lhe foi tão difícil que não pudesse responder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11368, 'nvi', '2 Crônicas', '2cr', 9, 3, 'Vendo a sabedoria de Salomão, bem como o palácio que ele havia construído,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11369, 'nvi', '2 Crônicas', '2cr', 9, 4, 'o que era servido em sua mesa, o lugar de seus oficiais, os criados e copeiros, todos uniformizados, e os holocaustos que ele fazia no templo do Senhor, ela ficou impressionada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11370, 'nvi', '2 Crônicas', '2cr', 9, 5, 'Disse ela então ao rei: "Tudo o que ouvi em meu país acerca de tuas realizações e de tua sabedoria era verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11371, 'nvi', '2 Crônicas', '2cr', 9, 6, 'Mas eu não acreditava no que diziam, até ver com os meus próprios olhos. Na realidade, não me contaram nem a metade da grandeza de tua sabedoria; tu ultrapassas em muito o que ouvi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11372, 'nvi', '2 Crônicas', '2cr', 9, 7, 'Como devem ser felizes os homens da tua corte, que continuamente estão diante de ti e ouvem a tua sabedoria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11373, 'nvi', '2 Crônicas', '2cr', 9, 8, 'Bendito seja o Senhor, o teu Deus, que se agradou de ti e te colocou no trono dele para reinar pelo Senhor, pelo teu Deus. Por causa do amor de teu Deus para com Israel e do seu desejo de preservá-lo para sempre, ele te fez rei, para manter a justiça e a retidão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11374, 'nvi', '2 Crônicas', '2cr', 9, 9, 'E ela deu ao rei quatro toneladas e duzentos quilos de ouro e grande quantidade de especiarias e de pedras preciosas. Nunca se viu tantas especiarias tais como aquelas que a rainha de Sabá deu ao rei Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11375, 'nvi', '2 Crônicas', '2cr', 9, 10, '( Os marinheiros de Hirão e de Salomão trouxeram ouro de Ofir, e também madeira de junípero e pedras preciosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11376, 'nvi', '2 Crônicas', '2cr', 9, 11, 'O rei utilizou a madeira para fazer a escadaria do templo do Senhor e a do palácio real, além de harpas e liras para os músicos. Nunca se havia visto algo semelhante em Judá. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11377, 'nvi', '2 Crônicas', '2cr', 9, 12, 'O rei Salomão deu à rainha de Sabá tudo o que ela desejou e pediu; muito mais do que ela lhe tinha trazido. Então ela e seus servos voltaram para o seu país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11378, 'nvi', '2 Crônicas', '2cr', 9, 13, 'O peso do ouro que Salomão recebia anualmente era de vinte e três toneladas e trezentos quilos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11379, 'nvi', '2 Crônicas', '2cr', 9, 14, 'fora o que os mercadores e os comerciantes traziam. Também todos os reis da Arábia e os governadores do país traziam ouro e prata para Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11380, 'nvi', '2 Crônicas', '2cr', 9, 15, 'O rei Salomão fez duzentos escudos grandes de ouro batido, utilizando três quilos e seiscentos gramas de ouro em cada um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11381, 'nvi', '2 Crônicas', '2cr', 9, 16, 'Também fez trezentos escudos pequenos de ouro batido, com um quilo e oitocentos gramas de ouro em cada um. O rei os colocou no Palácio da Floresta do Líbano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11382, 'nvi', '2 Crônicas', '2cr', 9, 17, 'O rei mandou fazer ainda um grande trono de marfim revestido de ouro puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11383, 'nvi', '2 Crônicas', '2cr', 9, 18, 'O trono tinha seis degraus, e um estrado de ouro fixo nele. Nos dois lados do assento havia braços, com um leão junto a cada braço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11384, 'nvi', '2 Crônicas', '2cr', 9, 19, 'Doze leões ficavam nos seis degraus, um de cada lado. Nada igual havia sido feito em nenhum outro reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11385, 'nvi', '2 Crônicas', '2cr', 9, 20, 'Todas as taças do rei Salomão eram de ouro, bem como todos os utensílios do Palácio da Floresta do Líbano. Não havia nada de prata, pois a prata quase não tinha valor nos dias de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11386, 'nvi', '2 Crônicas', '2cr', 9, 21, 'O rei tinha uma frota de navios mercantes tripulados por marinheiros do rei Hirão. Cada três anos a frota voltava, trazendo ouro, prata, marfim, macacos e pavões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11387, 'nvi', '2 Crônicas', '2cr', 9, 22, 'O rei Salomão era o mais rico e o mais sábio de todos os reis da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11388, 'nvi', '2 Crônicas', '2cr', 9, 23, 'Esses pediam audiência a Salomão, para ouvirem a sabedoria que Deus lhe tinha dado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11389, 'nvi', '2 Crônicas', '2cr', 9, 24, 'Ano após ano, todos os que vinham traziam algum presente: utensílios de prata e de ouro, mantos, armas e especiarias, cavalos e mulas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11390, 'nvi', '2 Crônicas', '2cr', 9, 25, 'Salomão possuía quatro mil estábulos para cavalos e carros, e doze mil cavalos, dos quais mantinha uma parte nas guarnições de algumas cidades e a outra perto dele, em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11391, 'nvi', '2 Crônicas', '2cr', 9, 26, 'Ele dominava sobre todos os reis desde o Eufrates até a terra dos filisteus, junto à fronteira do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11392, 'nvi', '2 Crônicas', '2cr', 9, 27, 'O rei tornou a prata tão comum em Jerusalém quanto as pedras, e o cedro tão numeroso quanto as figueiras bravas da Sefelá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11393, 'nvi', '2 Crônicas', '2cr', 9, 28, 'Os cavalos de Salomão eram importados do Egito e de todos os outros países.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11394, 'nvi', '2 Crônicas', '2cr', 9, 29, 'Os demais acontecimentos do reinado de Salomão, desde o início até o fim, estão escritos nos relatos do profeta Natã, nas profecias do silonita Aías e nas visões do vidente Ido acerca de Jeroboão, filho de Nebate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11395, 'nvi', '2 Crônicas', '2cr', 9, 30, 'Salomão reinou quarenta anos em Jerusalém, sobre todo o Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (11396, 'nvi', '2 Crônicas', '2cr', 9, 31, 'Então descansou com os seus antepassados e foi sepultado na cidade de Davi, seu pai. E o seu filho Roboão foi o seu sucessor.');
