INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30481, 'nvi', '2 Pedro', '2pe', 1, 1, 'Simão Pedro, servo e apóstolo de Jesus Cristo, àqueles que, mediante a justiça de nosso Deus e Salvador Jesus Cristo, receberam conosco uma fé igualmente valiosa:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30482, 'nvi', '2 Pedro', '2pe', 1, 2, 'Graça e paz lhes sejam multiplicadas, pelo pleno conhecimento de Deus e de Jesus, o nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30483, 'nvi', '2 Pedro', '2pe', 1, 3, 'Seu divino poder nos deu todas as coisas de que necessitamos para a vida e para a piedade, por meio do pleno conhecimento daquele que nos chamou para a sua própria glória e virtude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30484, 'nvi', '2 Pedro', '2pe', 1, 4, 'Por intermédio destas ele nos deu as suas grandiosas e preciosas promessas, para que por elas vocês se tornassem participantes da natureza divina e fugissem da corrupção que há no mundo, causada pela cobiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30485, 'nvi', '2 Pedro', '2pe', 1, 5, 'Por isso mesmo, empenhem-se para acrescentar à sua fé a virtude; à virtude o conhecimento;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30486, 'nvi', '2 Pedro', '2pe', 1, 6, 'ao conhecimento o domínio próprio; ao domínio próprio a perseverança; à perseverança a piedade;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30487, 'nvi', '2 Pedro', '2pe', 1, 7, 'à piedade a fraternidade; e à fraternidade o amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30488, 'nvi', '2 Pedro', '2pe', 1, 8, 'Porque, se essas qualidades existirem e estiverem crescendo em suas vidas, elas impedirão que vocês, no pleno conhecimento de nosso Senhor Jesus Cristo, sejam inoperantes e improdutivos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30489, 'nvi', '2 Pedro', '2pe', 1, 9, 'Todavia, se alguém não as tem, está cego, só vê o que está perto, esquecendo-se da purificação dos seus antigos pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30490, 'nvi', '2 Pedro', '2pe', 1, 10, 'Portanto, irmãos, empenhem-se ainda mais para consolidar o chamado e a eleição de vocês, pois se agirem dessa forma, jamais tropeçarão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30491, 'nvi', '2 Pedro', '2pe', 1, 11, 'e assim vocês estarão ricamente providos quando entrarem no Reino eterno de nosso Senhor e Salvador Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30492, 'nvi', '2 Pedro', '2pe', 1, 12, 'Por isso, sempre terei o cuidado de lembrar-lhes estas coisas, se bem que vocês já as sabem e estão solidamente firmados na verdade que receberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30493, 'nvi', '2 Pedro', '2pe', 1, 13, 'Considero importante, enquanto estiver no tabernáculo deste corpo, despertar a memória de vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30494, 'nvi', '2 Pedro', '2pe', 1, 14, 'porque sei que em breve deixarei este tabernáculo, como o nosso Senhor Jesus Cristo já me revelou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30495, 'nvi', '2 Pedro', '2pe', 1, 15, 'Eu me empenharei para que, também depois da minha partida, vocês sejam sempre capazes de lembrar-se destas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30496, 'nvi', '2 Pedro', '2pe', 1, 16, 'De fato, não seguimos fábulas engenhosamente inventadas, quando lhes falamos a respeito do poder e da vinda de nosso Senhor Jesus Cristo; pelo contrário, nós fomos testemunhas oculares da sua majestade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30497, 'nvi', '2 Pedro', '2pe', 1, 17, 'Ele recebeu honra e glória da parte de Deus Pai, quando da suprema glória lhe foi dirigida a voz que disse: "Este é o meu filho amado, em quem me agrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30498, 'nvi', '2 Pedro', '2pe', 1, 18, 'Nós mesmos ouvimos essa voz vinda do céu, quando estávamos com ele no monte santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30499, 'nvi', '2 Pedro', '2pe', 1, 19, 'Assim, temos ainda mais firme a palavra dos profetas, e vocês farão bem se a ela prestarem atenção, como a uma candeia que brilha em lugar escuro, até que o dia clareie e a estrela da alva nasça em seus corações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30500, 'nvi', '2 Pedro', '2pe', 1, 20, 'Antes de mais nada, saibam que nenhuma profecia da Escritura provém de interpretação pessoal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30501, 'nvi', '2 Pedro', '2pe', 1, 21, 'pois jamais a profecia teve origem na vontade humana, mas homens falaram da parte de Deus, impelidos pelo Espírito Santo.');
