INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30502, 'nvi', '2 Pedro', '2pe', 2, 1, 'No passado surgiram falsos profetas no meio do povo, como também surgirão entre vocês falsos mestres. Estes introduzirão secretamente heresias destruidoras, chegando a negar o Soberano que os resgatou, trazendo sobre si mesmos repentina destruição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30503, 'nvi', '2 Pedro', '2pe', 2, 2, 'Muitos seguirão os caminhos vergonhosos desses homens e, por causa deles, será difamado o caminho da verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30504, 'nvi', '2 Pedro', '2pe', 2, 3, 'Em sua cobiça, tais mestres os explorarão com histórias que inventaram. Há muito tempo a sua condenação paira sobre eles, e a sua destruição não tarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30505, 'nvi', '2 Pedro', '2pe', 2, 4, 'Pois Deus não poupou os anjos que pecaram, mas os lançou no inferno, prendendo-os em abismos tenebrosos a fim de serem reservados para o juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30506, 'nvi', '2 Pedro', '2pe', 2, 5, 'Ele não poupou o mundo antigo quando trouxe o dilúvio sobre aquele povo ímpio, mas preservou Noé, pregador da justiça, e mais sete pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30507, 'nvi', '2 Pedro', '2pe', 2, 6, 'Também condenou as cidades de Sodoma e Gomorra, reduzindo-as a cinzas, tornando-as exemplo do que acontecerá aos ímpios;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30508, 'nvi', '2 Pedro', '2pe', 2, 7, 'mas livrou Ló, homem justo, que se afligia com o procedimento libertino dos que não tinham princípios morais');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30509, 'nvi', '2 Pedro', '2pe', 2, 8, '( pois, vivendo entre eles, todos os dias aquele justo se atormentava em sua alma justa por causa das maldades que via e ouvia ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30510, 'nvi', '2 Pedro', '2pe', 2, 9, 'Vemos, portanto, que o Senhor sabe livrar os piedosos da provação e manter em castigo os ímpios para o dia do juízo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30511, 'nvi', '2 Pedro', '2pe', 2, 10, 'especialmente os que seguem os desejos impuros da carne e desprezam a autoridade. Insolentes e arrogantes, tais homens não têm medo de difamar os seres celestiais;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30512, 'nvi', '2 Pedro', '2pe', 2, 11, 'contudo, nem os anjos, embora sendo maiores em força e poder, fazem acusações injuriosas contra aqueles seres na presença do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30513, 'nvi', '2 Pedro', '2pe', 2, 12, 'Mas eles difamam o que desconhecem e são como criaturas irracionais, guiadas pelo instinto, nascidas para serem capturadas e destruídas; serão corrompidos pela sua própria corrupção!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30514, 'nvi', '2 Pedro', '2pe', 2, 13, 'Eles receberão retribuição pela injustiça que causaram. Consideram prazer entregar-se à devassidão em plena luz do dia. São nódoas e manchas, regalando-se em seus prazeres, quando participam das festas de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30515, 'nvi', '2 Pedro', '2pe', 2, 14, 'Tendo os olhos cheios de adultério, nunca param de pecar, iludem os instáveis e têm o coração exercitado na ganância. Malditos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30516, 'nvi', '2 Pedro', '2pe', 2, 15, 'Eles abandonaram o caminho reto e se desviaram, seguindo o caminho de Balaão, filho de Beor, que amou o salário da injustiça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30517, 'nvi', '2 Pedro', '2pe', 2, 16, 'mas em sua transgressão foi repreendido por uma jumenta, um animal mudo, que falou com voz humana e refreou a insensatez do profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30518, 'nvi', '2 Pedro', '2pe', 2, 17, 'Esses homens são fontes sem água e névoas impelidas pela tempestade. A escuridão das trevas lhes está reservada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30519, 'nvi', '2 Pedro', '2pe', 2, 18, 'pois eles, com palavras de vaidosa arrogância e provocando os desejos libertinos da carne, seduzem os que estão quase conseguindo fugir daqueles que vivem no erro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30520, 'nvi', '2 Pedro', '2pe', 2, 19, 'Prometendo-lhes liberdade, eles mesmos são escravos da corrupção, pois o homem é escravo daquilo que o domina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30521, 'nvi', '2 Pedro', '2pe', 2, 20, 'Se, tendo escapado das contaminações do mundo por meio do conhecimento de nosso Senhor e Salvador Jesus Cristo, encontram-se novamente nelas enredados e por elas dominados, estão em pior estado do que no princípio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30522, 'nvi', '2 Pedro', '2pe', 2, 21, 'Teria sido melhor que não tivessem conhecido o caminho da justiça, do que, depois de o terem conhecido, voltarem as costas para o santo mandamento que lhes foi transmitido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30523, 'nvi', '2 Pedro', '2pe', 2, 22, 'Confirma-se neles que é verdadeiro o provérbio: "O cão voltou ao seu vômito" e ainda: "A porca lavada voltou a revolver-se na lama".');
