INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30524, 'nvi', '2 Pedro', '2pe', 3, 1, 'Amados, esta é agora a segunda carta que lhes escrevo. Em ambas quero despertar com estas lembranças a sua mente sincera para que vocês se lembrem');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30525, 'nvi', '2 Pedro', '2pe', 3, 2, 'das palavras proferidas no passado pelos santos profetas, e do mandamento de nosso Senhor e Salvador que os apóstolos de vocês lhes ensinaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30526, 'nvi', '2 Pedro', '2pe', 3, 3, 'Antes de tudo saibam que, nos últimos dias, surgirão escarnecedores zombando e seguindo suas próprias paixões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30527, 'nvi', '2 Pedro', '2pe', 3, 4, 'Eles dirão: "O que houve com a promessa da sua vinda? Desde que os antepassados morreram, tudo continua como desde o princípio da criação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30528, 'nvi', '2 Pedro', '2pe', 3, 5, 'Mas eles deliberadamente se esquecem de que há muito tempo, pela palavra de Deus, existiam céus e terra, esta formada da água e pela água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30529, 'nvi', '2 Pedro', '2pe', 3, 6, 'E pela água o mundo daquele tempo foi submerso e destruído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30530, 'nvi', '2 Pedro', '2pe', 3, 7, 'Pela mesma palavra os céus e a terra que agora existem estão reservados para o fogo, guardados para o dia do juízo e para a destruição dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30531, 'nvi', '2 Pedro', '2pe', 3, 8, 'Não se esqueçam disto, amados: para o Senhor um dia é como mil anos, e mil anos como um dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30532, 'nvi', '2 Pedro', '2pe', 3, 9, 'O Senhor não demora em cumprir a sua promessa, como julgam alguns. Pelo contrário, ele é paciente com vocês, não querendo que ninguém pereça, mas que todos cheguem ao arrependimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30533, 'nvi', '2 Pedro', '2pe', 3, 10, 'O dia do Senhor, porém, virá como ladrão. Os céus desaparecerão com um grande estrondo, os elementos serão desfeitos pelo calor, e a terra, e tudo o que nela há, será desnudada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30534, 'nvi', '2 Pedro', '2pe', 3, 11, 'Visto que tudo será assim desfeito, que tipo de pessoas é necessário que vocês sejam? Vivam de maneira santa e piedosa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30535, 'nvi', '2 Pedro', '2pe', 3, 12, 'esperando o dia de Deus e apressando a sua vinda. Naquele dia os céus serão desfeitos pelo fogo, e os elementos se derreterão pelo calor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30536, 'nvi', '2 Pedro', '2pe', 3, 13, 'Todavia, de acordo com a sua promessa, esperamos novos céus e nova terra, onde habita a justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30537, 'nvi', '2 Pedro', '2pe', 3, 14, 'Portanto, amados, enquanto esperam estas coisas, empenhem-se para serem encontrados por ele em paz, imaculados e inculpáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30538, 'nvi', '2 Pedro', '2pe', 3, 15, 'Tenham em mente que a paciência de nosso Senhor significa salvação, como também o nosso amado irmão Paulo lhes escreveu, com a sabedoria que Deus lhe deu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30539, 'nvi', '2 Pedro', '2pe', 3, 16, 'Ele escreve da mesma forma em todas as suas cartas, falando nelas destes assuntos. Suas cartas contêm algumas coisas difíceis de entender, as quais os ignorantes e instáveis torcem, como também o fazem com as demais Escrituras, para a própria destruição deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30540, 'nvi', '2 Pedro', '2pe', 3, 17, 'Portanto, amados, sabendo disso, guardem-se para que não sejam levados pelo erro dos que não têm princípios morais, nem percam a sua firmeza e caiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30541, 'nvi', '2 Pedro', '2pe', 3, 18, 'Cresçam, porém, na graça e no conhecimento de nosso Senhor e Salvador Jesus Cristo. A ele seja a glória, agora e para sempre! Amém.');
