INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9831, 'nvi', '2 Reis', '2rs', 11, 1, 'Quando Atalia, mãe de Acazias, soube que seu filho estava morto, mandou matar toda a família real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9832, 'nvi', '2 Reis', '2rs', 11, 2, 'Mas Jeoseba, filha do rei Jeorão e irmã de Acazias, pegou Joás, um dos filhos do rei que iam ser assassinados, e o colocou num quarto, junto com a sua ama, para escondê-lo de Atalia; assim ele não foi morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9833, 'nvi', '2 Reis', '2rs', 11, 3, 'Seis anos ele ficou escondido com ela no templo do Senhor, enquanto Atalia governava o país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9834, 'nvi', '2 Reis', '2rs', 11, 4, 'No sétimo ano, o sacerdote Joiada mandou chamar à sua presença no templo do Senhor os líderes dos batalhões de cem dos cários e dos guardas. E fez um acordo com eles, fazendo-os jurar no templo do Senhor. Então lhes mostrou o filho do rei');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9835, 'nvi', '2 Reis', '2rs', 11, 5, 'e lhes ordenou: "Vocês vão fazer o seguinte: Quando entrarem de serviço no sábado, uma companhia ficará de guarda no palácio real,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9836, 'nvi', '2 Reis', '2rs', 11, 6, 'outra, na porta de Sur e a terceira, na porta atrás das outras companhias. Elas montarão guarda no templo por turnos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9837, 'nvi', '2 Reis', '2rs', 11, 7, 'As outras duas companhias que normalmente não estão de serviço no sábado ficarão de guarda no templo, para proteger o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9838, 'nvi', '2 Reis', '2rs', 11, 8, 'Posicionem-se ao redor do rei, de armas na mão. Matem todo o que se aproximar de suas fileiras. Acompanhem o rei aonde quer que ele for".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9839, 'nvi', '2 Reis', '2rs', 11, 9, 'Os líderes dos batalhões de cem fizeram como o sacerdote Joiada havia ordenado. Cada um levou seus soldados, tanto os que estavam entrando de serviço no sábado como os que estavam saindo, ao sacerdote Joiada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9840, 'nvi', '2 Reis', '2rs', 11, 10, 'Então ele deu aos líderes dos batalhões de cem, as lanças e os escudos que haviam pertencido ao rei Davi, e que estavam no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9841, 'nvi', '2 Reis', '2rs', 11, 11, 'Os guardas, todos de arma na mão, posicionaram-se em volta do rei, perto do altar e do templo, desde o lado sul até o lado norte do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9842, 'nvi', '2 Reis', '2rs', 11, 12, 'Depois Joiada trouxe para fora o filho do rei, colocou nele a coroa e entregou-lhe uma cópia da aliança. Então o proclamaram rei ungindo-o, e o povo aplaudia e gritava: "Viva o rei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9843, 'nvi', '2 Reis', '2rs', 11, 13, 'Quando Atalia ouviu o barulho dos guardas e do povo, foi ao templo do Senhor, onde estava o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9844, 'nvi', '2 Reis', '2rs', 11, 14, 'Lá ela viu o rei, conforme o costume, de pé junto à coluna. Os oficiais e os tocadores de corneta estavam ao lado do rei, e todo o povo se alegrava ao som das cornetas. Então, Atalia rasgou suas vestes e gritou: "Traição! Traição! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9845, 'nvi', '2 Reis', '2rs', 11, 15, 'O sacerdote Joiada ordenou aos líderes dos batalhões de cem que estavam no comando das tropas: "Levem-na para fora por entre as fileiras, e matem à espada todo que a seguir". Pois o sacerdote dissera: "Ela não será morta no templo do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9846, 'nvi', '2 Reis', '2rs', 11, 16, 'Então eles a prenderam e a levaram ao lugar onde os cavalos entram no terreno do palácio, e lá a mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9847, 'nvi', '2 Reis', '2rs', 11, 17, 'E Joiada fez uma aliança entre o Senhor, o rei e o povo, para que fossem o povo do Senhor. Também fez um acordo entre o rei e o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9848, 'nvi', '2 Reis', '2rs', 11, 18, 'Então todo o povo foi ao templo de Baal e o derrubou. Despedaçaram os altares e os ídolos, e mataram Matã, sacerdote de Baal, em frente dos altares. E o sacerdote Joiada colocou guardas no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9849, 'nvi', '2 Reis', '2rs', 11, 19, 'Levou consigo os líderes dos batalhões de cem dos cários, os guardas e todo o povo e, juntos, conduziram o rei do templo ao palácio, passando pela porta da guarda. O rei então ocupou seu lugar no trono real,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9850, 'nvi', '2 Reis', '2rs', 11, 20, 'e todo o povo se alegrou. E a cidade acalmou-se depois que Atalia foi morta à espada no palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9851, 'nvi', '2 Reis', '2rs', 11, 21, 'Joás tinha sete anos de idade quando começou a reinar.');
