INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9927, 'nvi', '2 Reis', '2rs', 15, 1, 'No vigésimo sétimo ano do reinado de Jeroboão, rei de Israel, Azarias, filho de Amazias, rei de Judá, começou a reinar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9928, 'nvi', '2 Reis', '2rs', 15, 2, 'Tinha dezesseis anos de idade quando se tornou rei, e reinou cinqüenta e dois anos em Jerusalém. Sua mãe era de Jerusalém e chamava-se Jecolias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9929, 'nvi', '2 Reis', '2rs', 15, 3, 'Ele fez o que o Senhor aprova, tal como o seu pai Amazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9930, 'nvi', '2 Reis', '2rs', 15, 4, 'Contudo, os altares idólatras não foram derrubados; o povo continuava a oferecer sacrifícios e a queimar incenso neles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9931, 'nvi', '2 Reis', '2rs', 15, 5, 'O Senhor feriu o rei com lepra, até o dia de sua morte. Durante todo esse tempo morou numa casa separada. Jotão, filho do rei, tomava conta do palácio e governava o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9932, 'nvi', '2 Reis', '2rs', 15, 6, 'Os demais acontecimentos do reinado de Azarias e todas as suas realizações, estão escritos nos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9933, 'nvi', '2 Reis', '2rs', 15, 7, 'Azarias descansou com os seus antepassados e foi sepultado junto a eles na cidade de Davi. Seu filho Jotão foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9934, 'nvi', '2 Reis', '2rs', 15, 8, 'No trigésimo oitavo ano do reinado de Azarias, rei de Judá, Zacarias, filho de Jeroboão, tornou-se rei de Israel em Samaria, e reinou seis meses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9935, 'nvi', '2 Reis', '2rs', 15, 9, 'Ele fez o que o Senhor reprova, como seus antepassados haviam feito. Não se desviou dos pecados que Jeroboão, filho de Nebate, levara Israel a cometer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9936, 'nvi', '2 Reis', '2rs', 15, 10, 'Salum, filho de Jabes, conspirou contra Zacarias. Ele o atacou na frente do povo, assassinou-o e foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9937, 'nvi', '2 Reis', '2rs', 15, 11, 'Os demais acontecimentos do reinado de Zacarias estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9938, 'nvi', '2 Reis', '2rs', 15, 12, 'Assim se cumpriu a palavra do Senhor anunciada a Jeú: "Seus descendentes ocuparão o trono de Israel até a quarta geração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9939, 'nvi', '2 Reis', '2rs', 15, 13, 'Salum, filho de Jabes, começou a reinar no trigésimo oitavo ano do reinado de Uzias, rei de Judá, e reinou um mês em Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9940, 'nvi', '2 Reis', '2rs', 15, 14, 'Então Menaém, filho de Gadi, foi de Tirza a Samaria e atacou a Salum, filho de Jabes, assassinou-o e foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9941, 'nvi', '2 Reis', '2rs', 15, 15, 'Os demais acontecimentos do reinado de Salum e a conspiração que liderou estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9942, 'nvi', '2 Reis', '2rs', 15, 16, 'Naquela ocasião, Menaém, partindo de Tirza, atacou Tifsa e todos que estavam na cidade e seus arredores, porque eles se recusaram a abrir as portas da cidade. Saqueou Tifsa e rasgou ao meio todas as mulheres grávidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9943, 'nvi', '2 Reis', '2rs', 15, 17, 'No trigésimo nono ano do reinado de Azarias, rei de Judá, Menaém, filho de Gadi, tornou-se rei de Israel, e reinou dez anos em Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9944, 'nvi', '2 Reis', '2rs', 15, 18, 'Ele fez o que o Senhor reprova. Durante todo o seu reinado não se desviou dos pecados que Jeroboão, filho de Nebate, levara Israel a cometer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9945, 'nvi', '2 Reis', '2rs', 15, 19, 'Então Pul, rei da Assíria, invadiu o país, e Menaém lhe deu trinta e cinco toneladas de prata para obter seu apoio e manter-se no trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9946, 'nvi', '2 Reis', '2rs', 15, 20, 'Menaém cobrou essa quantia de Israel. Todos os homens de posses tiveram de contribuir com seiscentos gramas de prata no pagamento ao rei da Assíria. Então ele interrompeu a invasão e foi embora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9947, 'nvi', '2 Reis', '2rs', 15, 21, 'Os demais acontecimentos do reinado de Menaém e todas as suas realizações, estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9948, 'nvi', '2 Reis', '2rs', 15, 22, 'Menaém descansou com seus antepassados, e seu filho Pecaías foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9949, 'nvi', '2 Reis', '2rs', 15, 23, 'No qüinquagésimo ano do reinado de Azarias, rei de Judá, Pecaías, filho de Menaém, tornou-se rei de Israel em Samaria, e reinou dois anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9950, 'nvi', '2 Reis', '2rs', 15, 24, 'Pecaías fez o que o Senhor reprova. Não se desviou dos pecados que Jeroboão, filho de Nebate, levara Israel a cometer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9951, 'nvi', '2 Reis', '2rs', 15, 25, 'Um de seus principais oficiais, Peca, filho de Remalias, conspirou contra ele. Levando consigo cinqüenta homens de Gileade, assassinou Pecaías juntamente com Argobe e Arié, na cidadela do palácio real em Samaria. Assim Peca matou Pecaías e foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9952, 'nvi', '2 Reis', '2rs', 15, 26, 'Os demais acontecimentos do reinado de Pecaías e todas as suas realizações estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9953, 'nvi', '2 Reis', '2rs', 15, 27, 'No qüinquagésimo segundo ano do reinado de Azarias, rei de Judá, Peca, filho de Remalias, tornou-se rei de Israel em Samaria, e reinou vinte anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9954, 'nvi', '2 Reis', '2rs', 15, 28, 'Ele fez o que o Senhor reprova. Não se desviou dos pecados que Jeroboão, filho de Nebate, levara Israel a cometer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9955, 'nvi', '2 Reis', '2rs', 15, 29, 'Durante seu reinado, Tiglate-Pileser, rei da Assíria, invadiu e conquistou Ijom, Abel-Bete-Maaca, Janoa, Quedes e Hazor. Tomou Gileade e a Galiléia, inclusive toda a terra de Naftali, e deportou o povo para a Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9956, 'nvi', '2 Reis', '2rs', 15, 30, 'Então Oséias, filho de Elá, conspirou contra Peca, filho de Remalias. Ele o atacou e o assassinou, tornando-se seu sucessor no vigésimo ano do reinado de Jotão, filho de Uzias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9957, 'nvi', '2 Reis', '2rs', 15, 31, 'Os demais acontecimentos do reinado de Peca e todas as suas realizações, estão escritos nos registros históricos dos reis de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9958, 'nvi', '2 Reis', '2rs', 15, 32, 'No segundo ano do reinado de Peca, filho de Remalias, rei de Israel, Jotão, filho de Uzias, rei de Judá, começou a reinar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9959, 'nvi', '2 Reis', '2rs', 15, 33, 'Ele tinha vinte e cinco anos de idade quando começou a reinar, e reinou dezesseis anos em Jerusalém. O nome da sua mãe era Jerusa, filha de Zadoque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9960, 'nvi', '2 Reis', '2rs', 15, 34, 'Ele fez o que o Senhor aprova, tal como seu pai Uzias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9961, 'nvi', '2 Reis', '2rs', 15, 35, 'Contudo, os altares idólatras não foram derrubados; o povo continuou a oferecer sacrifícios e a queimar incenso neles. Jotão reconstruiu a porta superior do templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9962, 'nvi', '2 Reis', '2rs', 15, 36, 'Os demais acontecimentos do reinado de Jotão e as suas realizações, estão escritos nos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9963, 'nvi', '2 Reis', '2rs', 15, 37, '( Naqueles dias o Senhor começou a enviar Rezim, rei da Síria, e Peca, filho de Remalias, contra Judá. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9964, 'nvi', '2 Reis', '2rs', 15, 38, 'Jotão descansou com os seus antepassados e foi sepultado junto a eles na cidade de Davi, cidade de seu pai. Seu filho Acaz foi o seu sucessor.');
