INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10026, 'nvi', '2 Reis', '2rs', 18, 1, 'No terceiro ano do reinado de Oséias, filho de Elá, rei de Israel, Ezequias, filho de Acaz, rei de Judá, começou a reinar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10027, 'nvi', '2 Reis', '2rs', 18, 2, 'Ele tinha vinte e cinco anos de idade quando começou a reinar, e reinou vinte e nove anos em Jerusalém. O nome de sua mãe era Abia, filha de Zacarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10028, 'nvi', '2 Reis', '2rs', 18, 3, 'Ele fez o que o Senhor aprova, tal como tinha feito Davi, seu predecessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10029, 'nvi', '2 Reis', '2rs', 18, 4, 'Removeu os altares idólatras, quebrou as colunas sagradas e derrubou os postes sagrados. Despedaçou a serpente de bronze que Moisés havia feito, pois até àquela época os israelitas lhe queimavam incenso. Ela era chamada Neustã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10030, 'nvi', '2 Reis', '2rs', 18, 5, 'Ezequias confiava no Senhor, o Deus de Israel. Nunca houve ninguém como ele entre todos os reis de Judá, nem antes nem depois dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10031, 'nvi', '2 Reis', '2rs', 18, 6, 'Ele se apegou ao Senhor e não deixou de segui-lo; obedeceu aos mandamentos que o Senhor tinha dado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10032, 'nvi', '2 Reis', '2rs', 18, 7, 'E o Senhor estava com ele; era bem sucedido em tudo o que fazia. Rebelou-se contra o rei da Assíria e deixou de submeter-se a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10033, 'nvi', '2 Reis', '2rs', 18, 8, 'Desde a torre de sentinela até à cidade fortificada, ele derrotou os filisteus, até Gaza e o seu território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10034, 'nvi', '2 Reis', '2rs', 18, 9, 'No quarto ano do reinado do rei Ezequias, o sétimo ano do reinado de Oséias, filho de Elá, rei de Israel, Salmaneser, rei da Assíria, marchou contra Samaria e a cercou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10035, 'nvi', '2 Reis', '2rs', 18, 10, 'Ao fim de três anos, os assírios a tomaram. Assim a cidade foi conquistada no sexto ano do reinado de Ezequias, o nono ano do reinado de Oséias, rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10036, 'nvi', '2 Reis', '2rs', 18, 11, 'O rei assírio deportou os israelitas para a Assíria e os estabeleceu em Hala, em Gozã do rio Tabor e nas cidades dos medos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10037, 'nvi', '2 Reis', '2rs', 18, 12, 'Isto aconteceu porque os israelitas não obedeceram ao Senhor seu Deus, mas violaram a sua aliança: tudo o que Moisés, o servo do Senhor, tinha ordenado. Não o ouviram nem lhe obedeceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10038, 'nvi', '2 Reis', '2rs', 18, 13, 'No décimo quarto ano do reinado do rei Ezequias, Senaqueribe, rei da Assíria, atacou todas as cidades fortificadas de Judá e as conquistou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10039, 'nvi', '2 Reis', '2rs', 18, 14, 'Então Ezequias, rei de Judá, enviou esta mensagem ao rei da Assíria, em Láquis: "Cometi um erro. Pára de atacar-me, e eu pagarei tudo que exigires". O rei da Assíria cobrou de Ezequias, rei de Judá, dez toneladas e meia de prata e uma tonelada e cinqüenta quilos de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10040, 'nvi', '2 Reis', '2rs', 18, 15, 'Assim, Ezequias lhes deu toda a prata que se encontrou no templo e na tesouraria do palácio real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10041, 'nvi', '2 Reis', '2rs', 18, 16, 'Nessa ocasião Ezequias, rei de Judá, retirou o ouro com que havia coberto as portas e batentes do templo do Senhor, e o deu ao rei da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10042, 'nvi', '2 Reis', '2rs', 18, 17, 'De Láquis o rei da Assíria enviou ao rei Ezequias, em Jerusalém, seu general, seu oficial principal e seu comandante de campo com um grande exército. Eles subiram a Jerusalém e pararam no aqueduto do açude superior, na estrada que leva ao campo do Lavandeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10043, 'nvi', '2 Reis', '2rs', 18, 18, 'Eles chamaram pelo rei; e o administrador do palácio, Eliaquim, filho de Hilquias, o secretário Sebna e o arquivista real Joá, filho de Asafe, foram ao seu encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10044, 'nvi', '2 Reis', '2rs', 18, 19, 'O comandante de campo lhes disse: "Digam isto a Ezequias: Assim diz o grande rei, o rei da Assíria: Em que você baseia sua confiança?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10045, 'nvi', '2 Reis', '2rs', 18, 20, 'Você pensa que meras palavras já são estratégia e poderio militar. Em quem você está confiando para se rebelar contra mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10046, 'nvi', '2 Reis', '2rs', 18, 21, 'Você está confiando no Egito, aquele caniço quebrado, que espeta e perfura a mão do homem que nele se apóia! Assim o faraó, rei do Egito, retribui a quem confia nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10047, 'nvi', '2 Reis', '2rs', 18, 22, 'Mas, se vocês me disserem: "Estamos confiando no Senhor nosso Deus"; não é ele aquele cujos santuários e altares Ezequias removeu, dizendo a Judá e Jerusalém: "Vocês devem adorar diante deste altar em Jerusalém? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10048, 'nvi', '2 Reis', '2rs', 18, 23, 'Aceite, pois, agora, o desafio do meu senhor, o rei da Assíria: Eu lhe darei dois mil cavalos; se você tiver cavaleiros para eles!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10049, 'nvi', '2 Reis', '2rs', 18, 24, 'Como você pode derrotar o mais insignificante guerreiro do meu senhor? Você confia no Egito para lhe dar carros de guerra e cavaleiros?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10050, 'nvi', '2 Reis', '2rs', 18, 25, 'Além disso, será que vim atacar e destruir este local sem uma palavra da parte do Senhor? O próprio Senhor me disse que marchasse contra este país e o destruísse".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10051, 'nvi', '2 Reis', '2rs', 18, 26, 'Então Eliaquim, filho de Hilquias, Sebna e Joá disseram ao comandante de campo: "Por favor, fala com teus servos em aramaico, porque entendemos essa língua. Não fales em hebraico, pois assim o povo que está sobre os muros entenderá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10052, 'nvi', '2 Reis', '2rs', 18, 27, 'O comandante, porém, respondeu: "Será que meu senhor enviou-me para dizer essas coisas, somente para o seu senhor e para você e não para os que estão sentados no muro, que, como vocês, terão de comer as próprias fezes e beber a própria urina? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10053, 'nvi', '2 Reis', '2rs', 18, 28, 'Então o comandante levantou-se e gritou em hebraico: "Ouçam a palavra do grande rei, o rei da Assíria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10054, 'nvi', '2 Reis', '2rs', 18, 29, 'Assim diz o rei: Não deixem que Ezequias os engane. Ele não poderá livrá-los de minha mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10055, 'nvi', '2 Reis', '2rs', 18, 30, 'Não deixem Ezequias convencê-los a confiar no Senhor, quando diz: ‘Com certeza o Senhor nos livrará; esta cidade não será entregue nas mãos do rei da Assíria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10056, 'nvi', '2 Reis', '2rs', 18, 31, 'Não dêem ouvidos a Ezequias. Assim diz o rei da Assíria: Façam paz comigo e rendam-se. Então cada um de vocês comerá de sua própria videira e de sua própria figueira e beberá água de sua própria cisterna,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10057, 'nvi', '2 Reis', '2rs', 18, 32, 'até que eu venha e os leve para uma terra igual à de vocês, terra de cereais, de vinho, terra de pão e de vinhas, terra de oliveiras e de mel. Escolham a vida e não a morte! Não dêem ouvidos a Ezequias, pois ele os está iludindo, quando diz: ‘O Senhor nos livrará’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10058, 'nvi', '2 Reis', '2rs', 18, 33, 'Será que o deus de alguma nação conseguiu livrar sua terra das mãos do rei da Assíria?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10059, 'nvi', '2 Reis', '2rs', 18, 34, 'Onde estão os deuses de Hamate e de Arpade? Onde estão os deuses de Sefarvaim, Hena e Iva? Acaso livraram Samaria das minhas mãos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10060, 'nvi', '2 Reis', '2rs', 18, 35, 'Qual dentre todos os deuses dessas nações conseguiu livrar sua terra do meu poder? Como então o Senhor poderá livrar Jerusalém das minhas mãos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10061, 'nvi', '2 Reis', '2rs', 18, 36, 'Mas o povo permaneceu calado e nada disse em resposta, pois o rei tinha ordenado: "Não lhe respondam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10062, 'nvi', '2 Reis', '2rs', 18, 37, 'Então o administrador do palácio, Eliaquim, filho de Hilquias, o secretário Sebna e o arquivista real Joá, filho de Asafe, retornaram com as vestes rasgadas a Ezequias e lhe relataram o que o comandante de campo tinha dito.');
