INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10063, 'nvi', '2 Reis', '2rs', 19, 1, 'Ao ouvir o relato, o rei Ezequias rasgou as suas vestes, pôs roupas de luto e entrou no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10064, 'nvi', '2 Reis', '2rs', 19, 2, 'Ele enviou o administrador do palácio, Eliaquim, o secretário Sebna e os sacerdotes principais, todos vestidos com pano de saco, ao profeta Isaías, filho de Amoz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10065, 'nvi', '2 Reis', '2rs', 19, 3, 'Eles lhe disseram: "Assim diz Ezequias: Hoje é dia de angústia, de repreensão e de humilhação; estamos como a mulher que está para dar à luz filhos, mas não tem forças para fazê-los nascer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10066, 'nvi', '2 Reis', '2rs', 19, 4, 'Talvez o Senhor seu Deus ouça todas as palavras do comandante de campo, a quem o senhor dele, o rei da Assíria, enviou para zombar do Deus vivo. E que o Senhor seu Deus o repreenda pelas palavras que ouviu. Portanto, suplique a Deus pelo remanescente que ainda sobrevive".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10067, 'nvi', '2 Reis', '2rs', 19, 5, 'Quando os oficiais do rei Ezequias chegaram a Isaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10068, 'nvi', '2 Reis', '2rs', 19, 6, 'este lhes disse: "Digam a seu senhor: ‘Assim diz o Senhor: Não tenha medo das palavras que você ouviu, das blasfêmias que os servos do rei da Assíria lançaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10069, 'nvi', '2 Reis', '2rs', 19, 7, 'Ouça! Eu o farei tomar a decisão de retornar ao seu próprio país, quando ele ouvir certa notícia. E lá o farei morrer à espada’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10070, 'nvi', '2 Reis', '2rs', 19, 8, 'Quando o comandante de campo soube que o rei da Assíria havia partido de Láquis, retirou-se e encontrou o rei lutando contra Libna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10071, 'nvi', '2 Reis', '2rs', 19, 9, 'Ora, Senaqueribe fora informado de que Tiraca, rei etíope do Egito, estava vindo lutar contra ele, de modo que mandou novamente mensageiros a Ezequias com este recado:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10072, 'nvi', '2 Reis', '2rs', 19, 10, '"Digam a Ezequias, rei de Judá: Não deixe que o Deus no qual você confia o engane, quando diz: ‘Jerusalém não cairá nas mãos do rei da Assíria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10073, 'nvi', '2 Reis', '2rs', 19, 11, 'Com certeza você ouviu o que os reis da Assíria têm feito a todas as nações, como as destruíram por completo. E você haveria de livrar-se?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10074, 'nvi', '2 Reis', '2rs', 19, 12, 'Acaso os deuses das nações que foram destruídas por meus antepassados as livraram: os deuses de Gozã, Harã, Rezefe e do povo de Éden, que estava em Telassar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10075, 'nvi', '2 Reis', '2rs', 19, 13, 'Onde estão o rei de Hamate, o rei de Arpade, o rei da cidade de Sevarfaim, de Hena ou de Iva? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10076, 'nvi', '2 Reis', '2rs', 19, 14, 'Ezequias recebeu a carta das mãos dos mensageiros e a leu. Então subiu ao templo do Senhor e estendeu-a perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10077, 'nvi', '2 Reis', '2rs', 19, 15, 'E Ezequias orou ao Senhor: "Senhor, Deus de Israel, que reina em teu trono, entre os querubins, só tu és Deus sobre todos os reinos da terra. Tu criaste os céus e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10078, 'nvi', '2 Reis', '2rs', 19, 16, 'Dá ouvidos, Senhor, e vê; ouve as palavras que Senaqueribe enviou para insultar o Deus vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10079, 'nvi', '2 Reis', '2rs', 19, 17, 'É verdade, Senhor, que os reis assírios fizeram de todas essas nações e seus territórios um deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10080, 'nvi', '2 Reis', '2rs', 19, 18, 'Atiraram os deuses delas no fogo e os destruíram, pois não eram deuses; eram apenas madeira e pedra moldadas por mãos humanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10081, 'nvi', '2 Reis', '2rs', 19, 19, 'Agora, Senhor nosso Deus, salva-nos das mãos dele, para que todos os reinos da terra saibam que só tu, Senhor, és Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10082, 'nvi', '2 Reis', '2rs', 19, 20, 'Então Isaías, filho de Amoz, enviou uma mensagem a Ezequias: "Assim diz o Senhor, o Deus de Israel: Ouvi a sua oração acerca de Senaqueribe, o rei da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10083, 'nvi', '2 Reis', '2rs', 19, 21, 'Esta é a palavra que o Senhor falou contra ele: ‘A virgem, a filha de Sião, o despreza e zomba de você. A filha de Jerusalém meneia a cabeça enquanto você foge.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10084, 'nvi', '2 Reis', '2rs', 19, 22, 'De quem você zombou e contra quem blasfemou? Contra quem você levantou a voz e contra quem ergueu o seu olhar arrogante? Contra o Santo de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10085, 'nvi', '2 Reis', '2rs', 19, 23, 'Sim, você insultou o Senhor por meio dos seus mensageiros. E declarou: "Com carros sem conta subi, aos pontos mais elevados e às inacessíveis alturas do Líbano. Derrubei os seus mais altos cedros, os seus melhores pinheiros. Entrei em suas regiões mais remotas, e nas suas mais densas florestas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10086, 'nvi', '2 Reis', '2rs', 19, 24, 'Em terras estrangeiras cavei poços e bebi água. Com as solas de meus pés sequei todos os rios do Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10087, 'nvi', '2 Reis', '2rs', 19, 25, 'Você não percebe que há muito tempo eu já havia determinado tudo isso. Desde a antigüidade planejei o que agora faço acontecer, que você deixaria cidades fortificadas em ruínas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10088, 'nvi', '2 Reis', '2rs', 19, 26, 'Seus habitantes, sem forças, desanimam-se envergonhados. São como pastagens, como brotos tenros e verdes, como ervas no telhado, queimadas antes de crescer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10089, 'nvi', '2 Reis', '2rs', 19, 27, 'Eu, porém, sei onde você está, sei quando você sai e quando retorna; e como você se enfurece contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10090, 'nvi', '2 Reis', '2rs', 19, 28, 'Sim, contra mim você se enfureceu e o seu atrevimento chegou aos meus ouvidos. Por isso porei o meu anzol em seu nariz e o meu freio em sua boca, e o farei voltar pelo caminho por onde veio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10091, 'nvi', '2 Reis', '2rs', 19, 29, 'A você, Ezequias, darei este sinal: Neste ano vocês comerão do que crescer por si, e no próximo o que daquilo brotar. Mas no terceiro ano semeiem e colham, plantem vinhas e comam o seu fruto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10092, 'nvi', '2 Reis', '2rs', 19, 30, 'Mais uma vez, um remanescente da tribo de Judá sobreviverá, lançará raízes na terra e se encherão de frutos os seus ramos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10093, 'nvi', '2 Reis', '2rs', 19, 31, 'De Jerusalém sairão sobreviventes, e um remanescente do monte Sião. O zelo do Senhor dos Exércitos o executará’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10094, 'nvi', '2 Reis', '2rs', 19, 32, 'Portanto, assim diz o Senhor acerca do rei da Assíria: ‘Ele não invadirá esta cidade nem disparará contra ela uma só flecha. Não a enfrentará com escudo nem construirá rampas de cerco contra ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10095, 'nvi', '2 Reis', '2rs', 19, 33, 'Pelo caminho por onde veio voltará; não invadirá esta cidade’, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10096, 'nvi', '2 Reis', '2rs', 19, 34, '‘Eu a defenderei e a salvarei, por amor de mim mesmo e do meu servo Davi’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10097, 'nvi', '2 Reis', '2rs', 19, 35, 'Naquela noite o anjo do Senhor saiu e matou cento e oitenta e cinco mil homens no acampamento assírio. Quando o povo se levantou na manhã seguinte, o lugar estava repleto de cadáveres!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10098, 'nvi', '2 Reis', '2rs', 19, 36, 'Assim Senaqueribe, rei da Assíria, desmontou o acampamento e foi embora. Voltou para Nínive e lá ficou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10099, 'nvi', '2 Reis', '2rs', 19, 37, 'Certo dia, enquanto ele estava adorando no templo de seu deus Nisroque, seus filhos Adrameleque e Sarezer mataram-no à espada e fugiram para a terra de Ararate. Seu filho Esar-Hadom foi o seu sucessor.');
