INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9553, 'nvi', '2 Reis', '2rs', 2, 1, 'Quando o Senhor levou Elias aos céus num redemoinho aconteceu o seguinte: Elias e Eliseu saíram de Gilgal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9554, 'nvi', '2 Reis', '2rs', 2, 2, 'e no caminho disse-lhe Elias: "Fique aqui, pois o Senhor me enviou a Betel". Eliseu, porém, disse: "Juro pelo nome do Senhor e por tua vida, que não te deixarei ir só". Então foram a Betel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9555, 'nvi', '2 Reis', '2rs', 2, 3, 'Em Betel os discípulos dos profetas foram falar com Eliseu e perguntaram: "Você sabe que hoje o Senhor vai levar para os céus o seu mestre, separando-o de você? " Respondeu Eliseu: "Sim, eu sei, mas não falem nisso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9556, 'nvi', '2 Reis', '2rs', 2, 4, 'Então Elias lhe disse: "Fique aqui, Eliseu, pois o Senhor me enviou a Jericó". Ele respondeu: "Juro pelo nome do Senhor e por tua vida, que não te deixarei ir só". Desceram então a Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9557, 'nvi', '2 Reis', '2rs', 2, 5, 'Em Jericó os discípulos dos profetas foram falar com Eliseu e lhe perguntaram: "Você sabe que hoje o Senhor vai levar para os céus o seu mestre, separando-o de você? " Respondeu Eliseu: "Sim, eu sei, mas não falem nisso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9558, 'nvi', '2 Reis', '2rs', 2, 6, 'Em seguida Elias lhe disse: "Fique aqui, pois o Senhor me enviou ao rio Jordão". Ele respondeu: "Juro pelo nome do Senhor e por tua vida, que não te deixarei ir só! " Então partiram juntos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9559, 'nvi', '2 Reis', '2rs', 2, 7, 'Cinqüenta discípulos dos profetas os acompanharam e ficaram olhando a distância, quando Elias e Eliseu pararam à margem do Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9560, 'nvi', '2 Reis', '2rs', 2, 8, 'Então Elias tirou o manto, enrolou-o e com ele bateu nas águas. As águas se dividiram, e os dois atravessaram a seco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9561, 'nvi', '2 Reis', '2rs', 2, 9, 'Depois de atravessar, Elias disse a Eliseu: "O que posso fazer por você antes que eu seja levado para longe de você? " Respondeu Eliseu: "Faze de mim o principal herdeiro de teu espírito profético".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9562, 'nvi', '2 Reis', '2rs', 2, 10, 'Disse Elias: "Você fez um pedido difícil; mas, se você me vir quando eu for separado de você, terá o que pediu; do contrário, não será atendido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9563, 'nvi', '2 Reis', '2rs', 2, 11, 'De repente, enquanto caminhavam e conversavam, apareceu um carro de fogo, puxado por cavalos de fogo, que os separou, e Elias foi levado aos céus num redemoinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9564, 'nvi', '2 Reis', '2rs', 2, 12, 'Quando viu isso, Eliseu gritou: "Meu pai! Meu pai! Tu eras como os carros de guerra e os cavaleiros de Israel! " E quando já não podia mais vê-lo, Eliseu pegou as próprias vestes e as rasgou ao meio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9565, 'nvi', '2 Reis', '2rs', 2, 13, 'Depois pegou o manto de Elias, que tinha caído, e voltou para a margem do Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9566, 'nvi', '2 Reis', '2rs', 2, 14, 'Então bateu nas águas do rio com o manto e perguntou: "Onde está agora o Senhor, o Deus de Elias? " Tendo batido nas águas, essas se dividiram e ele atravessou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9567, 'nvi', '2 Reis', '2rs', 2, 15, 'Quando os discípulos dos profetas, vindos de Jericó, viram isso, disseram: "O espírito profético de Elias repousa sobre Eliseu". Então foram ao seu encontro, prostraram-se diante dele e disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9568, 'nvi', '2 Reis', '2rs', 2, 16, '"Olha, nós, teus servos, temos cinqüenta homens fortes. Deixa-os sair à procura do teu mestre. Talvez o Espírito do Senhor o tenha levado e deixado em algum monte ou em algum vale". Respondeu Eliseu: "Não mandem ninguém".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9569, 'nvi', '2 Reis', '2rs', 2, 17, 'Mas eles insistiram até que, constrangido, consentiu: "Podem mandar os homens". E mandaram cinqüenta homens, que procuraram Elias por três dias, mas não o encontraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9570, 'nvi', '2 Reis', '2rs', 2, 18, 'Quando voltaram a Eliseu, que tinha ficado em Jericó, ele lhes falou: "Não lhes disse que não fossem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9571, 'nvi', '2 Reis', '2rs', 2, 19, 'Alguns homens da cidade foram dizer a Eliseu: "Como podes ver, esta cidade está bem localizada, mas a água não é boa e a terra é improdutiva".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9572, 'nvi', '2 Reis', '2rs', 2, 20, 'E disse ele: "Ponham sal numa tigela nova e tragam-na para mim". Quando a levaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9573, 'nvi', '2 Reis', '2rs', 2, 21, 'ele foi à nascente, jogou o sal ali e disse: "Assim diz o Senhor: ‘Purifiquei esta água. Não causará mais mortes nem deixará a terra improdutiva’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9574, 'nvi', '2 Reis', '2rs', 2, 22, 'E até hoje a água permanece pura, conforme a palavra de Eliseu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9575, 'nvi', '2 Reis', '2rs', 2, 23, 'De Jericó Eliseu foi para Betel. No caminho, alguns meninos que vinham da cidade começaram a caçoar dele, gritando: "Suma daqui, careca! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9576, 'nvi', '2 Reis', '2rs', 2, 24, 'Voltando-se, olhou para eles e os amaldiçoou em nome do Senhor. Então, duas ursas saíram do bosque e despedaçaram quarenta e dois meninos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9577, 'nvi', '2 Reis', '2rs', 2, 25, 'De Betel prosseguiu até o monte Carmelo e dali voltou a Samaria.');
