INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10121, 'nvi', '2 Reis', '2rs', 21, 1, 'Manassés tinha doze anos de idade quando começou a reinar, e reinou cinqüenta e cinco anos em Jerusalém. O nome de sua mãe era Hefzibá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10122, 'nvi', '2 Reis', '2rs', 21, 2, 'Ele fez o que o Senhor reprova, imitando as práticas detestáveis das nações que o Senhor havia expulsado de diante dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10123, 'nvi', '2 Reis', '2rs', 21, 3, 'Reconstruiu os altares idólatras que seu pai Ezequias havia demolido; também ergueu altares para Baal e fez um poste sagrado, como fizera Acabe, rei de Israel. Inclinou-se diante de todos os exércitos celestes e lhes prestou culto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10124, 'nvi', '2 Reis', '2rs', 21, 4, 'Construiu altares no templo do Senhor, do qual este tinha dito: "Em Jerusalém porei meu nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10125, 'nvi', '2 Reis', '2rs', 21, 5, 'Nos dois pátios do templo do Senhor ele construiu altares para todos os exércitos celestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10126, 'nvi', '2 Reis', '2rs', 21, 6, 'Chegou a queimar o próprio filho em sacrifício, praticou feitiçaria e adivinhação e consultou médiuns e espíritas. Fez o que o Senhor reprova, provocando-o à ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10127, 'nvi', '2 Reis', '2rs', 21, 7, 'Ele tomou o poste sagrado que havia feito e o pôs no templo, do qual o Senhor tinha dito a Davi e a seu filho Salomão: "Neste templo e em Jerusalém, que escolhi dentre todas as tribos de Israel, porei meu nome para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10128, 'nvi', '2 Reis', '2rs', 21, 8, 'Não farei os pés dos israelitas andarem errantes novamente, longe da terra que dei aos seus antepassados, se tão-somente tiverem o cuidado de fazer tudo o que lhes ordenei e obedecer à toda a Lei que meu servo Moisés lhes deu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10129, 'nvi', '2 Reis', '2rs', 21, 9, 'Mas o povo não quis ouvir. Manassés os desviou, a ponto de fazerem pior do que as nações que o Senhor havia destruído diante dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10130, 'nvi', '2 Reis', '2rs', 21, 10, 'E o Senhor disse por meio dos seus servos, os profetas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10131, 'nvi', '2 Reis', '2rs', 21, 11, '"Manassés, rei de Judá, cometeu esses atos repugnantes. Agiu pior do que os amorreus que o antecederam e também levou Judá a pecar com os ídolos que fizera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10132, 'nvi', '2 Reis', '2rs', 21, 12, 'Portanto, assim diz o Senhor, o Deus de Israel: Causarei uma tal desgraça em Jerusalém e em Judá que os ouvidos de quem ouvir a respeito ficarão zumbindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10133, 'nvi', '2 Reis', '2rs', 21, 13, 'Estenderei sobre Jerusalém o fio de medir utilizado contra Samaria e o fio de prumo usado contra a família de Acabe. Limparei Jerusalém como se lava um prato, lavando-o e virando-o de cabeça para baixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10134, 'nvi', '2 Reis', '2rs', 21, 14, 'Abandonarei o remanescente da minha herança e o entregarei nas mãos de seus inimigos. Serão despojados e saqueados por todos os seus adversários,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10135, 'nvi', '2 Reis', '2rs', 21, 15, 'pois fizeram o que eu reprovo e me provocaram à ira, desde o dia em que seus antepassados saíram do Egito até hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10136, 'nvi', '2 Reis', '2rs', 21, 16, 'Manassés também derramou tanto sangue inocente que encheu Jerusalém de um lado a outro; além disso levou Judá a cometer pecado, a fim de que fizessem o que o Senhor reprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10137, 'nvi', '2 Reis', '2rs', 21, 17, 'Os demais acontecimentos do reinado de Manassés e todas as suas realizações, inclusive o pecado que cometeu, estão escritos no livro dos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10138, 'nvi', '2 Reis', '2rs', 21, 18, 'Manassés descansou com seus antepassados e foi sepultado no jardim do seu palácio, o jardim de Uzá. E seu filho Amom foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10139, 'nvi', '2 Reis', '2rs', 21, 19, 'Amom tinha vinte e dois anos de idade quando começou a reinar, e reinou dois anos em Jerusalém. O nome de sua mãe era Mesulemete, filha de Haruz; ela era de Jotbá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10140, 'nvi', '2 Reis', '2rs', 21, 20, 'Ele fez o que o Senhor reprova, como fizera Manassés, seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10141, 'nvi', '2 Reis', '2rs', 21, 21, 'Imitou o seu pai em tudo; prestou culto aos ídolos aos quais seu pai havia cultuado e inclinou-se diante deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10142, 'nvi', '2 Reis', '2rs', 21, 22, 'Abandonou o Senhor, o Deus de seus antepassados e não andou no caminho do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10143, 'nvi', '2 Reis', '2rs', 21, 23, 'Os oficiais de Amom conspiraram contra ele e o assassinaram em seu palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10144, 'nvi', '2 Reis', '2rs', 21, 24, 'Mas o povo matou todos os que haviam conspirado contra o rei Amom, e a seu filho Josias proclamou rei em seu lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10145, 'nvi', '2 Reis', '2rs', 21, 25, 'Os demais acontecimentos do reinado de Amom e as suas realizações, estão escritos no livro dos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10146, 'nvi', '2 Reis', '2rs', 21, 26, 'Ele foi sepultado em seu túmulo no jardim de Uzá. Seu filho Josias foi o seu sucessor.');
