INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10147, 'nvi', '2 Reis', '2rs', 22, 1, 'Josias tinha oito anos de idade quando começou a reinar, e reinou trinta e um anos em Jerusalém. O nome de sua mãe era Jedida, filha de Adaías; ela era de Bozcate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10148, 'nvi', '2 Reis', '2rs', 22, 2, 'Ele fez o que o Senhor aprova e andou nos caminhos de Davi, seu predecessor, sem desviar-se nem para a direita nem para a esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10149, 'nvi', '2 Reis', '2rs', 22, 3, 'No décimo oitavo ano do seu reinado, o rei Josias enviou o secretário Safã, filho de Azalias e neto de Mesulão, ao templo do Senhor, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10150, 'nvi', '2 Reis', '2rs', 22, 4, '"Vá ao sumo sacerdote Hilquias e mande-o ajuntar a prata que foi trazida ao templo do Senhor, que os guardas das portas recolheram do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10151, 'nvi', '2 Reis', '2rs', 22, 5, 'Eles deverão entregar a prata aos homens nomeados para supervisionar a reforma do templo, para poderem pagar os trabalhadores que fazem os reparos no templo do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10152, 'nvi', '2 Reis', '2rs', 22, 6, 'os carpinteiros, os construtores e os pedreiros. Além disso comprarão madeira e pedras lavradas para os reparos no templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10153, 'nvi', '2 Reis', '2rs', 22, 7, 'Mas eles não precisarão prestar contas da prata que lhes foi confiada, pois estão agindo com honestidade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10154, 'nvi', '2 Reis', '2rs', 22, 8, 'Então o sumo sacerdote Hilquias disse ao secretário Safã: "Encontrei o livro da Lei no templo do Senhor". Ele o entregou a Safã, que o leu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10155, 'nvi', '2 Reis', '2rs', 22, 9, 'O secretário Safã voltou ao rei e lhe informou: "Teus servos entregaram a prata que havia no templo do Senhor e a confiaram aos trabalhadores e supervisores no templo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10156, 'nvi', '2 Reis', '2rs', 22, 10, 'E o secretário Safã acrescentou: "O sacerdote Hilquias entregou-me um livro". E Safã o leu para o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10157, 'nvi', '2 Reis', '2rs', 22, 11, 'Assim que o rei ouviu as palavras do livro da Lei, rasgou suas vestes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10158, 'nvi', '2 Reis', '2rs', 22, 12, 'e deu estas ordens ao sacerdote Hilquias, a Aicam, filho de Safã, a Acbor, filho de Micaías, ao secretário Safã e ao auxiliar real Asaías:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10159, 'nvi', '2 Reis', '2rs', 22, 13, '"Vão consultar o Senhor por mim, pelo povo e por todo Judá acerca do que está escrito neste livro que foi encontrado. A ira do Senhor contra nós deve ser grande, pois nossos antepassados não obedeceram às palavras deste livro, nem agiram de acordo com tudo que nele está escrito a nosso respeito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10160, 'nvi', '2 Reis', '2rs', 22, 14, 'O sacerdote Hilquias, Aicam, Acbor, Safã e Asaías foram falar com a profetisa Hulda, mulher de Salum, filho de Ticvá e neto de Harás, responsável pelo guarda-roupa do templo. Ela morava no bairro novo de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10161, 'nvi', '2 Reis', '2rs', 22, 15, 'Ela lhes disse: "Assim diz o Senhor, o Deus de Israel: ‘Digam ao homem que os enviou a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10162, 'nvi', '2 Reis', '2rs', 22, 16, 'Assim diz o Senhor: Eu vou trazer desgraça sobre este lugar e sobre seus habitantes; tudo o que está escrito no livro que o rei de Judá leu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10163, 'nvi', '2 Reis', '2rs', 22, 17, 'Porque me abandonaram e queimaram incenso a outros deuses, provocando-me à ira por meio de todos os ídolos que as mãos deles têm feito, minha ira arderá contra este lugar e não será apagada’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10164, 'nvi', '2 Reis', '2rs', 22, 18, 'Digam ao rei de Judá, que os enviou para consultar o Senhor: ‘Assim diz o Senhor, o Deus de Israel, acerca das palavras que você ouviu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10165, 'nvi', '2 Reis', '2rs', 22, 19, 'Já que o seu coração se abriu e você se humilhou diante do Senhor, ao ouvir o que falei contra este lugar e contra seus habitantes, que seriam arrasados e amaldiçoados, e porque você rasgou as vestes e chorou na minha presença, eu o ouvi, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10166, 'nvi', '2 Reis', '2rs', 22, 20, 'Portanto, eu o reunirei aos seus antepassados, e você será sepultado em paz. Seus olhos não verão toda a desgraça que eu vou trazer sobre este lugar’ ". Então eles levaram a resposta ao rei.');
