INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10204, 'nvi', '2 Reis', '2rs', 24, 1, 'Durante o reinado de Jeoaquim, Nabucodonosor, rei da Babilônia, invadiu o país, e Jeoaquim tornou-se seu vassalo por três anos. Então ele voltou atrás e rebelou-se contra Nabucodonosor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10205, 'nvi', '2 Reis', '2rs', 24, 2, 'O Senhor enviou contra ele tropas babilônicas, aramaicas, moabitas e amonitas, para destruir Judá, de acordo com a palavra do Senhor proclamada por seus servos, os profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10206, 'nvi', '2 Reis', '2rs', 24, 3, 'Com certeza isto aconteceu a Judá conforme a ordem do Senhor, a fim de removê-los da sua presença, por causa de todos os pecados que Manassés cometeu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10207, 'nvi', '2 Reis', '2rs', 24, 4, 'inclusive o derramamento de sangue inocente. Pois ele havia enchido Jerusalém de sangue inocente, e o Senhor não o quis perdoar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10208, 'nvi', '2 Reis', '2rs', 24, 5, 'Os demais acontecimentos do reinado de Jeoaquim e todas as suas realizações, estão escritos no livro dos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10209, 'nvi', '2 Reis', '2rs', 24, 6, 'Jeoaquim descansou com seus antepassados. Seu filho Joaquim foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10210, 'nvi', '2 Reis', '2rs', 24, 7, 'O rei do Egito não mais se atreveu a sair com seu exército de suas próprias fronteiras, pois o rei da Babilônia havia ocupado todo o território entre o ribeiro do Egito e o rio Eufrates, que antes pertencera ao Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10211, 'nvi', '2 Reis', '2rs', 24, 8, 'Joaquim tinha dezoito anos de idade quando começou a reinar, e reinou três meses em Jerusalém. O nome da sua mãe era Neusta, filha de Elnatã; ela era de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10212, 'nvi', '2 Reis', '2rs', 24, 9, 'Ele fez o que o Senhor reprova, tal como seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10213, 'nvi', '2 Reis', '2rs', 24, 10, 'Naquela ocasião os oficiais de Nabucodonosor, rei da Babilônia, avançaram até Jerusalém e a cercaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10214, 'nvi', '2 Reis', '2rs', 24, 11, 'Enquanto seus oficiais a cercavam o próprio Nabucodonosor veio à cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10215, 'nvi', '2 Reis', '2rs', 24, 12, 'Então Joaquim, rei de Judá, sua mãe, seus conselheiros, seus nobres e seus oficiais se entregaram; todos se renderam a ele. No oitavo ano do reinado do rei da Babilônia, ele levou Joaquim como prisioneiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10216, 'nvi', '2 Reis', '2rs', 24, 13, 'Conforme o Senhor tinha declarado, Nabucodonosor retirou todos os tesouros do templo do Senhor e do palácio real, quebrando todos os utensílios de ouro que Salomão, rei de Israel, fizera para o templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10217, 'nvi', '2 Reis', '2rs', 24, 14, 'Levou para o exílio toda Jerusalém: todos os líderes e os homens de combate, todos os artesãos e artífices. Era um total de dez mil pessoas; só ficaram os mais pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10218, 'nvi', '2 Reis', '2rs', 24, 15, 'Nabucodonosor levou prisioneiro Joaquim para a Babilônia. Também levou de Jerusalém para a Babilônia a mãe do rei, as mulheres do rei e os oficiais do rei e os líderes do país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10219, 'nvi', '2 Reis', '2rs', 24, 16, 'O rei da Babilônia também deportou para a Babilônia toda a força de sete mil homens de combate, homens fortes e preparados para a guerra, e mil artífices e artesãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10220, 'nvi', '2 Reis', '2rs', 24, 17, 'Fez Matanias, tio de Joaquim, reinar em seu lugar, e mudou seu nome para Zedequias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10221, 'nvi', '2 Reis', '2rs', 24, 18, 'Zedequias tinha vinte e um anos de idade quando começou a reinar, e reinou onze anos em Jerusalém. O nome de sua mãe era Hamutal, filha de Jeremias; ela era de Libna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10222, 'nvi', '2 Reis', '2rs', 24, 19, 'Ele fez o que o Senhor reprova, tal como fizera Jeoaquim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10223, 'nvi', '2 Reis', '2rs', 24, 20, 'Por causa da ira do Senhor tudo isso aconteceu a Jerusalém e a Judá; por fim ele os lançou para longe da sua presença. Ora, Zedequias rebelou-se contra o rei da Babilônia.');
