INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10224, 'nvi', '2 Reis', '2rs', 25, 1, 'Então, no nono ano do reinado de Zedequias, no décimo dia do décimo mês, Nabucodonosor, rei da Babilônia, marchou contra Jerusalém com todo o seu exército. Ele acampou em frente da cidade e construiu rampas de ataque ao redor dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10225, 'nvi', '2 Reis', '2rs', 25, 2, 'A cidade foi mantida sob cerco até o décimo primeiro ano do reinado de Zedequias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10226, 'nvi', '2 Reis', '2rs', 25, 3, 'No nono dia do quarto mês, a fome na cidade havia se tornado tão severa que não havia nada para o povo comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10227, 'nvi', '2 Reis', '2rs', 25, 4, 'Então o muro da cidade foi rompido, e todos os soldados fugiram de noite pela porta entre os dois muros próximos ao jardim do rei, embora os babilônios estivessem em torno da cidade. Fugiram na direção da Arabá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10228, 'nvi', '2 Reis', '2rs', 25, 5, 'mas o exército babilônio perseguiu o rei e o alcançou nas planícies de Jericó. Todos os seus soldados o abandonaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10229, 'nvi', '2 Reis', '2rs', 25, 6, 'e ele foi capturado. Foi levado até o rei da Babilônia, em Ribla, onde pronunciaram a sentença contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10230, 'nvi', '2 Reis', '2rs', 25, 7, 'Executaram os filhos de Zedequias na sua frente; depois furaram seus olhos, prenderam-no com algemas de bronze e o levaram para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10231, 'nvi', '2 Reis', '2rs', 25, 8, 'No sétimo dia do quinto mês do décimo nono ano do reinado de Nabucodonosor, rei da Babilônia, Nebuzaradã, comandante da guarda imperial, conselheiro do rei da Babilônia, foi a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10232, 'nvi', '2 Reis', '2rs', 25, 9, 'Incendiou o templo do Senhor, o palácio real, todas as casas de Jerusalém e todos os edifícios importantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10233, 'nvi', '2 Reis', '2rs', 25, 10, 'Todo o exército babilônio, que acompanhava Nebuzaradã derrubou os muros de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10234, 'nvi', '2 Reis', '2rs', 25, 11, 'E ele levou para o exílio o povo que sobrou na cidade, os que passaram para o lado do rei da Babilônia e o restante da população.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10235, 'nvi', '2 Reis', '2rs', 25, 12, 'Mas alguns dos mais pobres do país o comandante deixou para trás, para trabalharem nas vinhas e nos campos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10236, 'nvi', '2 Reis', '2rs', 25, 13, 'Os babilônios destruíram as colunas de bronze, os suportes e o tanque de bronze que estavam no templo do Senhor, e levaram o bronze para a Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10237, 'nvi', '2 Reis', '2rs', 25, 14, 'Também levaram as panelas, as pás, os cortadores de pavio, as vasilhas e todos os utensílios de bronze utilizados no serviço do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10238, 'nvi', '2 Reis', '2rs', 25, 15, 'O comandante da guarda imperial levou os incensários e as bacias de aspersão, tudo o que era feito de ouro puro ou prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10239, 'nvi', '2 Reis', '2rs', 25, 16, 'As duas colunas, o tanque e os suportes, que Salomão fizera para o templo do Senhor, eram mais do que podia ser pesado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10240, 'nvi', '2 Reis', '2rs', 25, 17, 'Cada coluna tinha oito metros e dez centímetros de altura. O capitel de bronze no alto de cada coluna tinha um metro e trinta e cinco centímetros de altura e era decorado com uma fileira de romãs de bronze ao redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10241, 'nvi', '2 Reis', '2rs', 25, 18, 'O comandante da guarda levou como prisioneiros o sumo sacerdote, Seraías, Sofonias, o segundo sacerdote, e os três guardas da porta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10242, 'nvi', '2 Reis', '2rs', 25, 19, 'Dos que ainda estavam na cidade, ele levou o oficial responsável pelos homens de combate e cinco conselheiros reais. Também levou o secretário, principal líder responsável pelo alistamento militar no país e sessenta homens do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10243, 'nvi', '2 Reis', '2rs', 25, 20, 'O comandante Nebuzaradã levou a todos ao rei da Babilônia, em Ribla.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10244, 'nvi', '2 Reis', '2rs', 25, 21, 'Lá, em Ribla, na terra de Hamate, o rei mandou executá-los. Assim Judá foi para o exílio, para longe de sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10245, 'nvi', '2 Reis', '2rs', 25, 22, 'Nabucodonosor, rei da Babilônia, nomeou Gedalias, filho de Aicam e neto de Safã, como governador do povo que havia sido deixado em Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10246, 'nvi', '2 Reis', '2rs', 25, 23, 'Quando Ismael, filho de Netanias, Joanã, filho de Careá, Seraías, filho do netofatita Tanumete, e Jazanias, filho de um maacatita, todos os líderes do exército, souberam que o rei da Babilônia havia nomeado Gedalias como governador, eles e seus soldados foram falar com Gedalias em Mispá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10247, 'nvi', '2 Reis', '2rs', 25, 24, 'Gedalias fez um juramento a esses líderes e a seus soldados, dizendo: "Não tenham medo dos oficiais babilônios. Estabeleçam-se nesta terra e sirvam o rei da Babilônia, e tudo lhes irá bem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10248, 'nvi', '2 Reis', '2rs', 25, 25, 'Mas no sétimo mês, Ismael, filho de Netanias e neto de Elisama, que tinha sangue real, foi com dez homens e assassinou Gedalias e os judeus e os babilônios que estavam com ele em Mispá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10249, 'nvi', '2 Reis', '2rs', 25, 26, 'Então todo o povo, desde as crianças até os velhos, juntamente com os líderes do exército, fugiram para o Egito, com medo dos babilônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10250, 'nvi', '2 Reis', '2rs', 25, 27, 'No trigésimo sétimo ano do exílio de Joaquim, rei de Judá, no ano em que Evil-Merodaque se tornou rei da Babilônia, ele tirou Joaquim da prisão, no dia vinte e sete do décimo segundo mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10251, 'nvi', '2 Reis', '2rs', 25, 28, 'Ele lhe tratou com bondade e deu-lhe o lugar mais honrado entre os outros reis que estavam com ele na Babilônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10252, 'nvi', '2 Reis', '2rs', 25, 29, 'Assim, Joaquim deixou suas vestes de prisão e pelo resto de sua vida comeu à mesa do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (10253, 'nvi', '2 Reis', '2rs', 25, 30, 'E diariamente, enquanto viveu, Joaquim recebeu uma pensão do rei.');
