INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9578, 'nvi', '2 Reis', '2rs', 3, 1, 'Jorão, filho de Acabe, tornou-se rei de Israel em Samaria no décimo oitavo ano de Josafá, rei de Judá, e reinou doze anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9579, 'nvi', '2 Reis', '2rs', 3, 2, 'Fez o que o Senhor reprova, mas não como seu pai e sua mãe, pois derrubou a coluna sagrada de Baal, que seu pai havia feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9580, 'nvi', '2 Reis', '2rs', 3, 3, 'No entanto, persistiu nos pecados que Jeroboão, filho de Nebate, levara Israel a cometer e deles não se afastou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9581, 'nvi', '2 Reis', '2rs', 3, 4, 'Ora, Messa, rei de Moabe, tinha muitos rebanhos e pagava como tributo ao rei de Israel cem mil cordeiros e a lã de cem mil carneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9582, 'nvi', '2 Reis', '2rs', 3, 5, 'Mas, depois que Acabe morreu, o rei de Moabe rebelou-se contra o rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9583, 'nvi', '2 Reis', '2rs', 3, 6, 'E, naquela ocasião, o rei Jorão partiu de Samaria e mobilizou todo Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9584, 'nvi', '2 Reis', '2rs', 3, 7, 'Também enviou esta mensagem a Josafá, rei de Judá: "O rei de Moabe rebelou-se contra mim. Irás acompanhar-me na luta contra Moabe? " Ele respondeu: "Sim, eu irei. Serei teu aliado, os meus soldados e os teus, os meus cavalos e os teus serão um só exército".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9585, 'nvi', '2 Reis', '2rs', 3, 8, 'E perguntou: "Por qual caminho atacaremos? " Respondeu Jorão: "Pelo deserto de Edom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9586, 'nvi', '2 Reis', '2rs', 3, 9, 'Então o rei de Israel partiu com os reis de Judá e de Edom. Depois de uma marcha de sete dias, já havia acabado a água para os homens e para os animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9587, 'nvi', '2 Reis', '2rs', 3, 10, 'Exclamou, então, o rei de Israel: "E agora? Será que o Senhor ajuntou a nós, os três reis, para nos entregar nas mãos de Moabe? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9588, 'nvi', '2 Reis', '2rs', 3, 11, 'Mas Josafá perguntou: "Será que não há aqui profeta do Senhor, para que possamos consultar o Senhor por meio dele? " Um conselheiro do rei de Israel respondeu: "Eliseu, filho de Safate, está aqui. Ele era auxiliar de Elias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9589, 'nvi', '2 Reis', '2rs', 3, 12, 'Josafá prosseguiu: "A palavra do Senhor está com ele". Então o rei de Israel, Josafá e o rei de Edom foram falar com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9590, 'nvi', '2 Reis', '2rs', 3, 13, 'Eliseu disse ao rei de Israel: "Nada tenho a ver com você. Vá consultar os profetas de seu pai e de sua mãe". Mas o rei de Israel insistiu: "Não, pois foi o Senhor que ajuntou a nós, estes três reis, para entregar-nos nas mãos de Moabe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9591, 'nvi', '2 Reis', '2rs', 3, 14, 'Então Eliseu disse: "Juro pelo nome do Senhor dos Exércitos, a quem sirvo, que se não fosse por respeito a Josafá, rei de Judá, eu não olharia para você nem mesmo lhe daria atenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9592, 'nvi', '2 Reis', '2rs', 3, 15, 'Mas agora tragam-me um harpista". Enquanto o harpista estava tocando, o poder do Senhor veio sobre Eliseu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9593, 'nvi', '2 Reis', '2rs', 3, 16, 'e ele disse: "Assim diz o Senhor: Cavem muitas cisternas neste vale.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9594, 'nvi', '2 Reis', '2rs', 3, 17, 'Pois assim diz o Senhor: Vocês não verão vento nem chuva, contudo este vale ficará cheio de água, e vocês, seus rebanhos e seus outros animais beberão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9595, 'nvi', '2 Reis', '2rs', 3, 18, 'Mas para o Senhor isso ainda é pouco; ele também lhes entregará Moabe nas suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9596, 'nvi', '2 Reis', '2rs', 3, 19, 'Vocês destruirão todas as cidades fortificadas e todas as cidades importantes. Derrubarão toda árvore frutífera, taparão todas as fontes e encherão de pedras todas as terras de cultivo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9597, 'nvi', '2 Reis', '2rs', 3, 20, 'No dia seguinte, na hora do sacrifício da manhã, a água veio descendo da direção de Edom e alagou a região.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9598, 'nvi', '2 Reis', '2rs', 3, 21, 'Quando os moabitas ficaram sabendo que os reis tinham vindo para atacá-los, todos os que eram capazes de empunhar armas, do mais jovem ao mais velho, foram convocados e posicionaram-se na fronteira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9599, 'nvi', '2 Reis', '2rs', 3, 22, 'Ao se levantarem na manhã seguinte, o sol refletia na água. Para os moabitas que estavam defronte dela, a água era vermelha como sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9600, 'nvi', '2 Reis', '2rs', 3, 23, 'Então gritaram: "É sangue! Os reis lutaram entre si e se mataram. Agora, ao saque, Moabe! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9601, 'nvi', '2 Reis', '2rs', 3, 24, 'Quando, porém, os moabitas chegaram ao acampamento de Israel, os israelitas os atacaram e os puseram em fuga. Entraram no território de Moabe e o arrasaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9602, 'nvi', '2 Reis', '2rs', 3, 25, 'Destruíram as cidades, e quando passavam por um campo cultivável cada homem atirava uma pedra até que ficasse coberto. Taparam todas as fontes e derrubaram toda árvore frutífera. Só Quir-Haresete ficou com as pedras no lugar, mas homens armados de atiradeiras a cercaram e também a atacaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9603, 'nvi', '2 Reis', '2rs', 3, 26, 'Quando o rei de Moabe viu que estava perdendo a batalha, reuniu setecentos homens armados de espadas para forçar a passagem, para alcançar o rei de Edom, mas fracassou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9604, 'nvi', '2 Reis', '2rs', 3, 27, 'Então pegou seu filho mais velho, que devia sucedê-lo como rei, e o sacrificou sobre o muro da cidade. Isso trouxe grande ira contra Israel, de modo que eles se retiraram e voltaram para sua própria terra.');
