INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9676, 'nvi', '2 Reis', '2rs', 6, 1, 'Os discípulos dos profetas disseram a Eliseu: "Como vês, o lugar onde nos reunimos contigo é pequeno demais para nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9677, 'nvi', '2 Reis', '2rs', 6, 2, 'Por que não vamos ao rio Jordão? Lá cada um de nós poderá cortar um tronco para construirmos ali um lugar de reuniões". Eliseu disse: "Podem ir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9678, 'nvi', '2 Reis', '2rs', 6, 3, 'Então um deles perguntou: "Não gostarias de ir com os teus servos? " "Sim", ele respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9679, 'nvi', '2 Reis', '2rs', 6, 4, 'E foi com eles. Foram ao Jordão e começaram a derrubar árvores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9680, 'nvi', '2 Reis', '2rs', 6, 5, 'Quando um deles estava cortando um tronco, o ferro do machado caiu na água. E ele gritou: "Ah, meu senhor, era emprestado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9681, 'nvi', '2 Reis', '2rs', 6, 6, 'O homem de Deus perguntou: "Onde caiu? " Quando ele lhe mostrou o lugar, Eliseu cortou um galho e o jogou ali, fazendo o ferro flutuar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9682, 'nvi', '2 Reis', '2rs', 6, 7, 'e disse: "Pegue-o". O homem esticou o braço e o pegou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9683, 'nvi', '2 Reis', '2rs', 6, 8, 'Ora, o rei da Síria estava em guerra contra Israel. Depois de deliberar com os seus conselheiros, dizia: "Montarei o meu acampamento em tal lugar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9684, 'nvi', '2 Reis', '2rs', 6, 9, 'Mas o homem de Deus mandava uma mensagem ao rei de Israel: "Evite passar por tal lugar, pois os arameus estão descendo para lá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9685, 'nvi', '2 Reis', '2rs', 6, 10, 'Assim, o rei de Israel investigava o lugar indicado pelo homem de Deus. Repetidas vezes Eliseu alertou o rei, que tomava as devidas precauções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9686, 'nvi', '2 Reis', '2rs', 6, 11, 'Isto enfureceu o rei da Síria, que, convocando seus conselheiros, lhes perguntou: "Vocês não me apontarão qual dos nossos está do lado do rei de Israel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9687, 'nvi', '2 Reis', '2rs', 6, 12, 'Respondeu um dos conselheiros: "Nenhum de nós, majestade. É Eliseu, o profeta que está em Israel, que revela ao rei de Israel até as palavras que tu falas em teu quarto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9688, 'nvi', '2 Reis', '2rs', 6, 13, 'Ordenou o rei: "Descubram onde ele está, para que eu mande capturá-lo". Quando lhe informaram que o profeta estava em Dotã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9689, 'nvi', '2 Reis', '2rs', 6, 14, 'ele enviou para lá uma grande tropa com cavalos e carros de guerra. Eles chegaram de noite e cercaram a cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9690, 'nvi', '2 Reis', '2rs', 6, 15, 'O servo do homem de Deus levantou-se bem cedo pela manhã e, quando saía, viu que uma tropa com cavalos e carros de guerra havia cercado a cidade. Então ele exclamou: "Ah, meu senhor! O que faremos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9691, 'nvi', '2 Reis', '2rs', 6, 16, 'O profeta respondeu: "Não tenha medo. Aqueles que estão conosco são mais numerosos do que eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9692, 'nvi', '2 Reis', '2rs', 6, 17, 'E Eliseu orou: "Senhor, abre os olhos dele para que veja". Então o Senhor abriu os olhos do rapaz, que olhou e viu as colinas cheias de cavalos e carros de fogo ao redor de Eliseu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9693, 'nvi', '2 Reis', '2rs', 6, 18, 'Quando os arameus desceram na direção de Eliseu, ele orou ao Senhor: "Fere estes homens de cegueira". Então ele os feriu de cegueira, conforme Eliseu havia pedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9694, 'nvi', '2 Reis', '2rs', 6, 19, 'Eliseu lhes disse: "Este não é o caminho nem esta é a cidade que procuram. Sigam-me, e eu os levarei ao homem que vocês estão procurando". E os guiou até a cidade de Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9695, 'nvi', '2 Reis', '2rs', 6, 20, 'Assim que entraram na cidade, Eliseu disse: "Senhor, abre os olhos destes homens para que possam ver". Então o Senhor abriu-lhes os olhos, e eles viram que estavam dentro de Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9696, 'nvi', '2 Reis', '2rs', 6, 21, 'Quando o rei de Israel os viu, perguntou a Eliseu: "Devo matá-los, meu pai? Devo matá-los? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9697, 'nvi', '2 Reis', '2rs', 6, 22, 'Ele respondeu: "Não! Costumas matar prisioneiros que capturas com a tua espada e o teu arco? Manda-lhes servir comida e bebida e deixa que voltem ao seu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9698, 'nvi', '2 Reis', '2rs', 6, 23, 'Então preparou-lhes um grande banquete e, terminando eles de comer e beber, mandou-os de volta para o seu senhor. Assim, as tropas da Síria pararam de invadir o território de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9699, 'nvi', '2 Reis', '2rs', 6, 24, 'Algum tempo depois, Ben-Hadade, rei da Síria, mobilizou todo o seu exército e cercou Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9700, 'nvi', '2 Reis', '2rs', 6, 25, 'O cerco durou tanto e causou tamanha fome que uma cabeça de jumento chegou a valer oitenta peças de prata, e uma caneca de esterco de pomba, cinco peças de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9701, 'nvi', '2 Reis', '2rs', 6, 26, 'Um dia, quando o rei de Israel inspecionava os muros da cidade, uma mulher gritou para ele: "Socorro, majestade! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9702, 'nvi', '2 Reis', '2rs', 6, 27, 'O rei respondeu: "Se o Senhor não socorrê-la, como poderei ajudá-la? Acaso há trigo na eira ou vinho no lagar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9703, 'nvi', '2 Reis', '2rs', 6, 28, 'Contudo ele perguntou: "Qual é o problema? " Ela respondeu: "Esta mulher me disse: ‘Vamos comer o seu filho hoje, e amanhã comeremos o meu’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9704, 'nvi', '2 Reis', '2rs', 6, 29, 'Então cozinhamos o meu filho e o comemos. No dia seguinte eu disse a ela que era a vez de comermos o seu filho, mas ela o havia escondido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9705, 'nvi', '2 Reis', '2rs', 6, 30, 'Quando o rei ouviu as palavras da mulher, rasgou as próprias vestes. Como estava sobre os muros, o povo viu que ele estava usando pano de saco por baixo, junto ao corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9706, 'nvi', '2 Reis', '2rs', 6, 31, 'E ele disse: "Deus me castigue com todo rigor, se a cabeça de Eliseu, filho de Safate, continuar hoje sobre seus ombros! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9707, 'nvi', '2 Reis', '2rs', 6, 32, 'Ora, Eliseu estava sentado em sua casa, reunido com as autoridades de Israel. O rei havia mandado um mensageiro à sua frente, mas, antes que ele chegasse, Eliseu disse às autoridades: "Aquele assassino mandou alguém para cortar-me a cabeça? Quando o mensageiro chegar, fechem a porta e mantenham-na trancada. Vocês não estão ouvindo os passos do seu senhor que vem atrás dele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9708, 'nvi', '2 Reis', '2rs', 6, 33, 'Enquanto ainda lhes falava, o mensageiro chegou. Na mesma hora o rei disse: "Esta desgraça vem do Senhor. Por que devo ainda ter esperança no Senhor? "');
