INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9709, 'nvi', '2 Reis', '2rs', 7, 1, 'Eliseu respondeu: "Ouçam a palavra do Senhor! Assim diz o Senhor: Amanhã, por volta desta hora, na porta de Samaria, tanto uma medida de farinha como duas medidas de cevada serão vendidas por uma peça de prata".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9710, 'nvi', '2 Reis', '2rs', 7, 2, 'O oficial, em cujo braço o rei estava se apoiando, disse ao homem de Deus: "Ainda que o Senhor abrisse as comportas do céu, será que isso poderia acontecer? " Mas Eliseu advertiu: "Você o verá com os próprios olhos, mas não comerá coisa alguma! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9711, 'nvi', '2 Reis', '2rs', 7, 3, 'Havia quatro leprosos junto à porta da cidade. Eles disseram uns aos outros: "Por que ficar aqui esperando a morte?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9712, 'nvi', '2 Reis', '2rs', 7, 4, 'Se resolvermos entrar na cidade, morreremos de fome, mas se ficarmos aqui, também morreremos. Vamos, pois, ao acampamento dos arameus para nos render. Se eles nos pouparem, viveremos; se nos matarem, morreremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9713, 'nvi', '2 Reis', '2rs', 7, 5, 'Ao anoitecer, eles foram ao acampamento dos arameus. Quando chegaram às imediações do acampamento, não havia ninguém ali,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9714, 'nvi', '2 Reis', '2rs', 7, 6, 'pois o Senhor tinha feito os arameus ouvirem o ruído de um grande exército com cavalos e carros de guerra, de modo que disseram uns aos outros: "Ouçam, o rei de Israel contratou os reis dos hititas e dos egípcios para nos atacar! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9715, 'nvi', '2 Reis', '2rs', 7, 7, 'Então, para salvar suas vidas, fugiram ao anoitecer, abandonando tendas, cavalos e jumentos, deixando o acampamento como estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9716, 'nvi', '2 Reis', '2rs', 7, 8, 'Tendo chegado às imediações do acampamento os leprosos entraram numa das tendas. Comeram e beberam; pegaram prata, ouro e roupas e saíram para esconder tudo. Depois voltaram e entraram noutra tenda, pegaram o que quiseram e esconderam isso também.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9717, 'nvi', '2 Reis', '2rs', 7, 9, 'Então disseram uns aos outros: "Não estamos agindo certo. Este é um dia de boas notícias, e não podemos ficar calados. Se esperarmos até o amanhecer, seremos castigados. Vamos imediatamente contar tudo no palácio do rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9718, 'nvi', '2 Reis', '2rs', 7, 10, 'Então foram e chamaram as sentinelas da porta da cidade e lhes contaram: "Entramos no acampamento arameu, e não vimos nem ouvimos ninguém. Havia apenas cavalos e jumentos amarrados, e tendas abandonadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9719, 'nvi', '2 Reis', '2rs', 7, 11, 'As sentinelas da porta proclamaram a notícia, e ela foi anunciada dentro do palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9720, 'nvi', '2 Reis', '2rs', 7, 12, 'O rei se levantou de noite e disse aos seus conselheiros: "Eu lhes explicarei o que os arameus planejaram. Como sabem que estamos passando fome, deixaram o acampamento e se esconderam no campo, pensando: ‘Com certeza eles sairão, e então os pegaremos vivos e entraremos na cidade’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9721, 'nvi', '2 Reis', '2rs', 7, 13, 'Um de seus conselheiros respondeu: "Manda que alguns homens apanhem cinco dos cavalos que restam na cidade. O destino desses homens será o mesmo de todos os israelitas que ficarem, sim, como toda esta multidão condenada. Por isso vamos enviá-los para descobrir o que aconteceu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9722, 'nvi', '2 Reis', '2rs', 7, 14, 'Assim que prepararam dois carros de guerra com seus cavalos, o rei os enviou atrás do exército arameu, ordenando aos condutores: "Vão e descubram o que aconteceu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9723, 'nvi', '2 Reis', '2rs', 7, 15, 'Eles seguiram as pegadas do exército até o Jordão e encontraram todo o caminho cheio de roupas e armas que os arameus haviam deixado para trás enquanto fugiam. Os mensageiros voltaram e relataram tudo ao rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9724, 'nvi', '2 Reis', '2rs', 7, 16, 'Então o povo saiu e saqueou o acampamento dos arameus. Assim, tanto uma medida de farinha como duas medidas de cevada passaram a ser vendidas por uma peça de prata, conforme o Senhor tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9725, 'nvi', '2 Reis', '2rs', 7, 17, 'Ora, o rei havia posto o oficial em cujo braço tinha se apoiado como encarregado da porta da cidade, mas quando o povo saiu, atropelou-o junto à porta, e ele morreu, conforme o homem de Deus havia predito quando o rei foi à sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9726, 'nvi', '2 Reis', '2rs', 7, 18, 'Aconteceu conforme o homem de Deus dissera ao rei: "Amanhã, por volta desta hora, na porta de Samaria, tanto uma medida de farinha como duas medidas de cevada serão vendidas por uma peça de prata".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9727, 'nvi', '2 Reis', '2rs', 7, 19, 'O oficial tinha contestado o homem de Deus perguntando: "Ainda que o Senhor abrisse as comportas do céu, será que isso poderia acontecer? " O homem de Deus havia respondido: "Você verá com os próprios olhos, mas não comerá coisa alguma! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9728, 'nvi', '2 Reis', '2rs', 7, 20, 'E foi exatamente isso que lhe aconteceu, pois o povo o pisoteou junto à porta da cidade, e ele morreu.');
