INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9729, 'nvi', '2 Reis', '2rs', 8, 1, 'Eliseu tinha prevenido a mãe do menino que ele havia ressuscitado: "Saia do país com sua família e vá morar onde puder, pois o Senhor determinou uma fome nesta terra, que durará sete anos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9730, 'nvi', '2 Reis', '2rs', 8, 2, 'A mulher seguiu o conselho do homem de Deus, partiu com sua família e passou sete anos na terra dos filisteus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9731, 'nvi', '2 Reis', '2rs', 8, 3, 'Ao final dos sete anos ela voltou a Israel e foi fazer um apelo ao rei para readquirir sua casa e sua propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9732, 'nvi', '2 Reis', '2rs', 8, 4, 'O rei estava conversando com Geazi, servo do homem de Deus, e disse: "Conte-me todos os prodígios que Eliseu tem feito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9733, 'nvi', '2 Reis', '2rs', 8, 5, 'Enquanto Geazi contava ao rei como Eliseu havia ressuscitado o menino, a própria mãe do menino que Eliseu tinha ressuscitado, chegou para apresentar sua petição ao rei para readquirir sua casa e sua propriedade. Geazi exclamou: "Esta é a mulher, ó rei, meu senhor, e este é o filho dela, a quem Eliseu ressuscitou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9734, 'nvi', '2 Reis', '2rs', 8, 6, 'O rei pediu que ela contasse o ocorrido, e ela confirmou os fatos. Então ele designou um funcionário para cuidar do caso dela e lhe ordenou: "Devolva tudo o que lhe pertencia, inclusive toda a renda das colheitas, desde que ela saiu do país até hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9735, 'nvi', '2 Reis', '2rs', 8, 7, 'Certa ocasião, Eliseu foi a Damasco. Ben-Hadade, rei da Síria, estava doente. Quando disseram ao rei: "O homem de Deus está na cidade",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9736, 'nvi', '2 Reis', '2rs', 8, 8, 'ele ordenou a Hazael: "Vá encontrar-se com o homem de Deus e leve-lhe um presente. Consulte o Senhor por meio dele: pergunte-lhe se vou me recuperar desta doença".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9737, 'nvi', '2 Reis', '2rs', 8, 9, 'Hazael foi encontrar-se com Eliseu, levando consigo de tudo o que havia de melhor em Damasco, um presente carregado por quarenta camelos. Ao chegar diante dele Hazael disse: "Teu filho Ben-Hadade, rei da Síria, enviou-me para perguntar se ele vai recuperar-se de sua doença".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9738, 'nvi', '2 Reis', '2rs', 8, 10, 'Eliseu respondeu: "Vá e diga-lhe: ‘Com certeza te recuperarás’, no entanto o Senhor me revelou que de fato ele vai morrer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9739, 'nvi', '2 Reis', '2rs', 8, 11, 'Eliseu ficou olhando fixamente para Hazael até deixá-lo constrangido. Então o homem de Deus começou a chorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9740, 'nvi', '2 Reis', '2rs', 8, 12, 'E perguntou Hazael: "Por que meu senhor está chorando? " Ele respondeu: "Porque sei das coisas terríveis que você fará aos israelitas. Você incendiará suas fortalezas, matará seus jovens à espada, esmagará as crianças e rasgará o ventre de suas mulheres grávidas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9741, 'nvi', '2 Reis', '2rs', 8, 13, 'Hazael disse: "Como poderia teu servo, que não passa de um cão, realizar algo assim? " Respondeu Eliseu: "O Senhor me mostrou que você se tornará rei da Síria".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9742, 'nvi', '2 Reis', '2rs', 8, 14, 'Então Hazael saiu dali e voltou para seu senhor. Quando Ben-Hadade perguntou: "O que Eliseu lhe disse? ", Hazael respondeu: "Ele me falou que certamente te recuperarás".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9743, 'nvi', '2 Reis', '2rs', 8, 15, 'Mas, no dia seguinte, ele apanhou um cobertor, encharcou-o e com ele sufocou o rei, até matá-lo. E assim Hazael foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9744, 'nvi', '2 Reis', '2rs', 8, 16, 'No quinto ano de Jorão, filho de Acabe, rei de Israel, sendo Josafá rei de Judá, Jeorão, seu filho, começou a reinar em Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9745, 'nvi', '2 Reis', '2rs', 8, 17, 'Ele tinha trinta e dois anos de idade quando começou a reinar, e reinou oito anos em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9746, 'nvi', '2 Reis', '2rs', 8, 18, 'Andou nos caminhos dos reis de Israel, como a família de Acabe havia feito, pois se casou com uma filha de Acabe. E fez o que o Senhor reprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9747, 'nvi', '2 Reis', '2rs', 8, 19, 'Entretanto, por amor ao seu servo Davi, o Senhor não quis destruir Judá. Ele havia prometido manter para sempre um descendente de Davi no trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9748, 'nvi', '2 Reis', '2rs', 8, 20, 'Nos dias de Jeorão, os edomitas rebelaram-se contra o domínio de Judá, proclamando seu próprio rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9749, 'nvi', '2 Reis', '2rs', 8, 21, 'Por isso, Jeorão foi a Zair com todos os seus carros de guerra. Lá os edomitas cercaram a ele e aos chefes dos seus carros de guerra, mas ele os atacou de noite e rompeu o cerco inimigo, e seu exército conseguiu fugir para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9750, 'nvi', '2 Reis', '2rs', 8, 22, 'E até hoje Edom continua independente de Judá. Nessa mesma época, a cidade de Libna também tornou-se independente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9751, 'nvi', '2 Reis', '2rs', 8, 23, 'Os demais acontecimentos do reinado de Jeorão e todas as suas realizações, estão escritos nos registros históricos dos reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9752, 'nvi', '2 Reis', '2rs', 8, 24, 'Jeorão descansou com seus antepassados e foi sepultado com eles na cidade de Davi. E seu filho Acazias foi o seu sucessor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9753, 'nvi', '2 Reis', '2rs', 8, 25, 'No décimo segundo ano do reinado de Jorão, filho de Acabe, rei de Israel, Acazias, filho de Jeorão, rei de Judá, começou a reinar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9754, 'nvi', '2 Reis', '2rs', 8, 26, 'Ele tinha vinte e dois anos de idade quando começou a reinar, e reinou um ano em Jerusalém. O nome de sua mãe era Atalia, neta de Onri, rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9755, 'nvi', '2 Reis', '2rs', 8, 27, 'Ele andou nos caminhos da família de Acabe e fez o que o Senhor reprova, como a família de Acabe havia feito, pois casou-se com uma mulher da família de Acabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9756, 'nvi', '2 Reis', '2rs', 8, 28, 'Acazias aliou-se a Jorão, filho de Acabe, e saiu à guerra contra Hazael, rei da Síria, em Ramote-Gileade. Jorão foi ferido');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9757, 'nvi', '2 Reis', '2rs', 8, 29, 'e voltou a Jezreel para recuperar-se dos ferimentos sofridos em Ramote, na batalha contra Hazael, rei da Síria. Depois Acazias, rei de Judá, foi a Jezreel visitar Jorão, que se recuperava de seus ferimentos.');
