INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9758, 'nvi', '2 Reis', '2rs', 9, 1, 'Enquanto isso o profeta Eliseu chamou um dos discípulos dos profetas e lhe disse: "Ponha a capa por dentro do cinto, pegue este frasco de óleo e vá a Ramote-Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9759, 'nvi', '2 Reis', '2rs', 9, 2, 'Quando lá chegar, procure Jeú, filho de Josafá e neto de Ninsi. Dirija-se a ele e leve-o para uma sala longe dos seus companheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9760, 'nvi', '2 Reis', '2rs', 9, 3, 'Depois pegue o frasco, derrame o óleo sobre a cabeça dele e declare: ‘Assim diz o Senhor: Eu o estou ungindo rei sobre Israel’. Então abra a porta e fuja sem demora! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9761, 'nvi', '2 Reis', '2rs', 9, 4, 'Então o jovem profeta foi a Ramote-Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9762, 'nvi', '2 Reis', '2rs', 9, 5, 'Ao chegar, encontrou os comandantes do exército reunidos e disse: "Trago uma mensagem para ti, comandante". "Para qual de nós? ", perguntou Jeú. Ele respondeu: "Para ti, comandante".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9763, 'nvi', '2 Reis', '2rs', 9, 6, 'Jeú levantou-se e entrou na casa. Então o jovem profeta derramou o óleo na cabeça de Jeú e declarou-lhe: "Assim diz o Senhor, o Deus de Israel: ‘Eu o estou ungindo rei de Israel, o povo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9764, 'nvi', '2 Reis', '2rs', 9, 7, 'Você dará fim à família de Acabe, seu senhor, e assim eu vingarei o sangue de meus servos, os profetas, e o sangue de todos os servos do Senhor, derramado por Jezabel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9765, 'nvi', '2 Reis', '2rs', 9, 8, 'Toda a família de Acabe perecerá. Eliminarei todos os de sexo masculino de sua família em Israel, seja escravo seja livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9766, 'nvi', '2 Reis', '2rs', 9, 9, 'Tratarei a família de Acabe como tratei a de Jeroboão, filho de Nebate, e a de Baasa, filho de Aías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9767, 'nvi', '2 Reis', '2rs', 9, 10, 'E Jezabel será devorada por cães num terreno em Jezreel, e ninguém a sepultará’ ". Então ele abriu a porta e saiu correndo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9768, 'nvi', '2 Reis', '2rs', 9, 11, 'Quando Jeú voltou para junto dos outros oficiais do rei, um deles lhe perguntou: "Está tudo bem? O que esse louco queria com você? " Jeú respondeu: "Vocês conhecem essa gente e as coisas que dizem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9769, 'nvi', '2 Reis', '2rs', 9, 12, 'Mas insistiram: "Não nos engane! Conte-nos o que ele disse". Então Jeú contou: "Ele me disse o seguinte: ‘Assim diz o Senhor: Eu o estou ungindo rei sobre Israel’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9770, 'nvi', '2 Reis', '2rs', 9, 13, 'Imediatamente eles pegaram os seus mantos e os estenderam sobre os degraus diante dele. Em seguida tocaram a trombeta e gritaram: "Jeú é rei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9771, 'nvi', '2 Reis', '2rs', 9, 14, 'Então Jeú, filho de Josafá e neto de Ninsi, começou uma conspiração contra o rei Jorão, na época em que este defendeu, com todo Israel, Ramote-Gileade contra Hazael, rei da Síria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9772, 'nvi', '2 Reis', '2rs', 9, 15, 'O rei Jorão tinha voltado a Jezreel para recuperar-se dos ferimentos sofridos na batalha contra Hazael, rei da Síria. Jeú propôs: "Se vocês me apóiam, não deixem ninguém sair escondido da cidade para nos denunciar em Jezreel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9773, 'nvi', '2 Reis', '2rs', 9, 16, 'Então ele subiu em seu carro e foi para Jezreel, porque Jorão estava lá se recuperando; e Acazias, rei de Judá, tinha ido visitá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9774, 'nvi', '2 Reis', '2rs', 9, 17, 'Quando a sentinela que estava na torre de vigia de Jezreel percebeu a tropa de Jeú se aproximando, gritou: "Estou vendo uma tropa! " Jorão ordenou: "Chame um cavaleiro e envie-o ao encontro deles para perguntar se eles vêm em paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9775, 'nvi', '2 Reis', '2rs', 9, 18, 'O cavaleiro foi ao encontro de Jeú e disse: "O rei pergunta: ‘Vocês vêm em paz? ’ " Jeú respondeu: "Não me venha falar em paz. Saia da minha frente". A sentinela relatou: "O mensageiro chegou a eles, mas não está voltando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9776, 'nvi', '2 Reis', '2rs', 9, 19, 'Então o rei enviou um segundo cavaleiro. Quando chegou a eles disse: "O rei pergunta: ‘Vocês vêm em paz? ’ " Jeú respondeu: "Não me venha falar em paz. Saia da minha frente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9777, 'nvi', '2 Reis', '2rs', 9, 20, 'A sentinela relatou: "Ele chegou a eles, mas também não está voltando". E acrescentou: "O jeito do chefe da tropa guiar o carro é como o de Jeú, neto de Ninsi; dirige como louco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9778, 'nvi', '2 Reis', '2rs', 9, 21, 'Jorão ordenou que preparassem seu carro de guerra. Assim que ficou pronto, Jorão, rei de Israel, e Acazias, rei de Judá, saíram, cada um em seu carro, ao encontro de Jeú. Eles o encontraram na propriedade que havia pertencido a Nabote, de Jezreel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9779, 'nvi', '2 Reis', '2rs', 9, 22, 'Quando Jorão viu Jeú, perguntou: "Você vem em paz, Jeú? " Jeú respondeu: "Como pode haver paz, enquanto continua toda a idolatria e as feitiçarias de sua mãe Jezabel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9780, 'nvi', '2 Reis', '2rs', 9, 23, 'Jorão deu meia-volta e fugiu, gritando para Acazias: "Traição, Acazias! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9781, 'nvi', '2 Reis', '2rs', 9, 24, 'Então Jeú disparou seu arco com toda força e atingiu Jorão nas costas. A flecha atravessou-lhe o coração e ele caiu morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9782, 'nvi', '2 Reis', '2rs', 9, 25, 'Jeú disse a Bidcar, seu oficial: "Pegue o cadáver e jogue-o nesta propriedade que pertencia a Nabote, de Jezreel. Lembre-se da advertência que o Senhor proferiu contra Acabe, pai dele, quando juntos acompanhávamos sua comitiva. Ele disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9783, 'nvi', '2 Reis', '2rs', 9, 26, '‘Ontem, vi o sangue de Nabote e o sangue dos seus filhos, declara o Senhor, e com certeza farei você pagar por isso nesta mesma propriedade, declara o Senhor’. Agora, então, pegue o cadáver e jogue-o nesta propriedade, conforme a palavra do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9784, 'nvi', '2 Reis', '2rs', 9, 27, 'Vendo isso, Acazias, rei de Judá, fugiu na direção de Bete-Hagã. Mas Jeú o perseguiu, gritando: "Matem-no também! " Eles o atingiram em seu carro de guerra na subida para Gur, perto de Ibleã, mas ele conseguiu refugiar-se em Megido, onde morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9785, 'nvi', '2 Reis', '2rs', 9, 28, 'Seus oficiais o levaram até Jerusalém e o sepultaram com seus antepassados em seu túmulo, na cidade de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9786, 'nvi', '2 Reis', '2rs', 9, 29, 'Acazias havia se tornado rei de Judá no décimo primeiro ano de Jorão, filho de Acabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9787, 'nvi', '2 Reis', '2rs', 9, 30, 'Em seguida Jeú entrou em Jezreel. Ao saber disso, Jezabel pintou os olhos, arrumou o cabelo e ficou olhando de uma janela do palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9788, 'nvi', '2 Reis', '2rs', 9, 31, 'Quando Jeú passou pelo portão, ela gritou: "Como vai, Zinri, assassino do seu senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9789, 'nvi', '2 Reis', '2rs', 9, 32, 'Ele ergueu os olhos para a janela e gritou: "Quem de vocês está do meu lado? " Dois ou três funcionários olharam para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9790, 'nvi', '2 Reis', '2rs', 9, 33, 'Então Jeú ordenou: "Joguem essa mulher para baixo! " Eles a jogaram e o sangue dela espirrou na parede e nos cavalos, e Jeú a atropelou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9791, 'nvi', '2 Reis', '2rs', 9, 34, 'Jeú entrou, comeu, bebeu e ordenou: "Peguem aquela maldita e sepultem-na, afinal era filha de rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9792, 'nvi', '2 Reis', '2rs', 9, 35, 'Mas, quando foram sepultá-la, só encontraram o crânio, os pés e as mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9793, 'nvi', '2 Reis', '2rs', 9, 36, 'Então voltaram e contaram isso a Jeú, que disse: "Cumpriu-se a palavra do Senhor, anunciada por meio do seu servo Elias, o tesbita: Num terreno em Jezreel cães devorarão a carne de Jezabel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (9794, 'nvi', '2 Reis', '2rs', 9, 37, 'os seus restos mortais serão espalhados num terreno em Jezreel, como esterco no campo, de modo que ninguém será capaz de dizer: ‘Esta é Jezabel’ ".');
