INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8024, 'nvi', '2 Samuel', '2sm', 1, 1, 'Depois da morte de Saul, Davi retornou de sua vitória sobre os amalequitas. Fazia dois dias que ele estava em Ziclague');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8025, 'nvi', '2 Samuel', '2sm', 1, 2, 'quando, no terceiro dia, chegou um homem que vinha do acampamento de Saul, com as roupas rasgadas e terra na cabeça. Ao aproximar-se de Davi, prostrou-se em terra, em sinal de respeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8026, 'nvi', '2 Samuel', '2sm', 1, 3, 'Davi então lhe perguntou: "De onde você vem? " Ele respondeu: "Fugi do acampamento israelita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8027, 'nvi', '2 Samuel', '2sm', 1, 4, 'Disse Davi: "Conte-me o que aconteceu? " E o homem contou: "O nosso exército fugiu da batalha, e muitos morreram. Saul e Jônatas também estão mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8028, 'nvi', '2 Samuel', '2sm', 1, 5, 'Então Davi perguntou ao jovem que lhe trouxera as notícias: "Como você sabe que Saul e Jônatas estão mortos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8029, 'nvi', '2 Samuel', '2sm', 1, 6, 'O jovem respondeu: "Cheguei por acaso ao monte Gilboa, e lá estava Saul, apoiado em sua lança. Os carros de guerra e os oficiais da cavalaria estavam a ponto de alcançá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8030, 'nvi', '2 Samuel', '2sm', 1, 7, 'Quando ele se virou e me viu, chamou-me, gritando, e eu disse: ‘Estou aqui’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8031, 'nvi', '2 Samuel', '2sm', 1, 8, '"Ele me perguntou: ‘Quem é você? ’ " ‘Sou um amalequita’, respondi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8032, 'nvi', '2 Samuel', '2sm', 1, 9, '"Então ele me ordenou: ‘Venha aqui e mate-me! Estou na angústia da morte, mas ainda vivo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8033, 'nvi', '2 Samuel', '2sm', 1, 10, '"Por isso aproximei-me dele e o matei, pois sabia que ele não sobreviveria ao ferimento. Peguei a coroa e o bracelete dele e trouxe-os para ti, meu senhor. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8034, 'nvi', '2 Samuel', '2sm', 1, 11, 'Então Davi rasgou suas vestes; e os homens que estavam com ele fizeram o mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8035, 'nvi', '2 Samuel', '2sm', 1, 12, 'E lamentaram, chorando e jejuando até o fim da tarde, por Saul e por seu filho Jônatas, pelo exército do Senhor e pelo povo de Israel, porque muitos haviam sido mortos à espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8036, 'nvi', '2 Samuel', '2sm', 1, 13, 'E Davi perguntou ao jovem que lhe trouxera as notícias: "De onde você é? " E ele respondeu: "Sou filho de um estrangeiro, sou amalequita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8037, 'nvi', '2 Samuel', '2sm', 1, 14, 'Davi lhe perguntou: "Como você não temeu levantar a mão para matar o ungido do Senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8038, 'nvi', '2 Samuel', '2sm', 1, 15, 'Então Davi chamou um dos seus soldados e disse-lhe: "Venha aqui e mate-o! " O servo o feriu, e o homem morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8039, 'nvi', '2 Samuel', '2sm', 1, 16, 'Davi tinha dito ao jovem: "Você é responsável por sua própria morte. Sua boca testemunhou contra você, quando disse: ‘Matei o ungido do Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8040, 'nvi', '2 Samuel', '2sm', 1, 17, 'Davi cantou este lamento sobre Saul e seu filho Jônatas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8041, 'nvi', '2 Samuel', '2sm', 1, 18, 'e ordenou que se ensinasse aos homens de Judá este Lamento do Arco, que foi registrado no Livro de Jasar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8042, 'nvi', '2 Samuel', '2sm', 1, 19, '"O seu esplendor, ó Israel, está morto sobre os seus montes. Como caíram os guerreiros!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8043, 'nvi', '2 Samuel', '2sm', 1, 20, '"Não conte isso em Gate, não o proclame nas ruas de Ascalom, para que não se alegrem as filhas dos filisteus nem exultem as filhas dos incircuncisos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8044, 'nvi', '2 Samuel', '2sm', 1, 21, '"Ó colinas de Gilboa, nunca mais haja orvalho nem chuva sobre vocês, nem campos que produzam trigo para as ofertas. Porque ali foi profanado o escudo dos guerreiros, o escudo de Saul, que nunca mais será polido com óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8045, 'nvi', '2 Samuel', '2sm', 1, 22, 'Do sangue dos mortos, da carne dos guerreiros, o arco de Jônatas nunca recuou, a espada de Saul sempre cumpriu a sua tarefa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8046, 'nvi', '2 Samuel', '2sm', 1, 23, '"Saul e Jônatas, mui amados, nem na vida nem na morte foram separados. Eram mais ágeis que as águias, mais fortes que os leões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8047, 'nvi', '2 Samuel', '2sm', 1, 24, '"Chorem por Saul, ó filhas de Israel! Chorem aquele que as vestia de rubros ornamentos, e suas roupas enfeitava com adornos de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8048, 'nvi', '2 Samuel', '2sm', 1, 25, '"Como caíram os guerreiros no meio da batalha! Jônatas está morto sobre os montes de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8049, 'nvi', '2 Samuel', '2sm', 1, 26, 'Como estou triste por você, Jônatas, meu irmão! Como eu lhe queria bem! Sua amizade me era mais preciosa que o amor das mulheres!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8050, 'nvi', '2 Samuel', '2sm', 1, 27, '"Caíram os guerreiros! As armas de guerra foram destruídas! "');
