INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8288, 'nvi', '2 Samuel', '2sm', 12, 1, 'E o Senhor enviou a Davi o profeta Natã. Ao chegar, ele disse a Davi: "Dois homens viviam numa cidade, um era rico e o outro, pobre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8289, 'nvi', '2 Samuel', '2sm', 12, 2, 'O rico possuía muitas ovelhas e bois,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8290, 'nvi', '2 Samuel', '2sm', 12, 3, 'mas o pobre nada tinha, senão uma cordeirinha que havia comprado. Ele a criou, e ela cresceu com ele e com seus filhos. Ela comia junto dele, bebia do seu copo e até dormia em seus braços. Era como uma filha para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8291, 'nvi', '2 Samuel', '2sm', 12, 4, '"Certo dia, um viajante chegou à casa do rico, e este não quis pegar uma de suas próprias ovelhas ou do seus bois para preparar-lhe uma refeição. Em vez disso, preparou para o visitante a cordeira que pertencia ao pobre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8292, 'nvi', '2 Samuel', '2sm', 12, 5, 'Então, Davi encheu-se de ira contra o homem e disse a Natã: "Juro pelo nome do Senhor que o homem que fez isso merece a morte!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8293, 'nvi', '2 Samuel', '2sm', 12, 6, 'Deverá pagar quatro vezes o preço da cordeira, porquanto agiu sem misericórdia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8294, 'nvi', '2 Samuel', '2sm', 12, 7, 'Então Natã disse a Davi: "Você é esse homem! Assim diz o Senhor, o Deus de Israel: ‘Eu o ungi rei de Israel, e livrei-o das mãos de Saul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8295, 'nvi', '2 Samuel', '2sm', 12, 8, 'Dei-lhe a casa e as mulheres do seu senhor. Dei-lhe a nação de Israel e Judá. E, se tudo isso não fosse suficiente, eu lhe teria dado mais ainda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8296, 'nvi', '2 Samuel', '2sm', 12, 9, 'Por que você desprezou a palavra do Senhor, fazendo o que ele reprova? Você matou Urias, o hitita, com a espada dos amonitas e ficou com a mulher dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8297, 'nvi', '2 Samuel', '2sm', 12, 10, 'Por isso, a espada nunca se afastará de sua família, pois você me desprezou e tomou a mulher de Urias, o hitita, para ser sua mulher’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8298, 'nvi', '2 Samuel', '2sm', 12, 11, '"Assim diz o Senhor: ‘De sua própria família trarei desgraça sobre você. Tomarei as suas mulheres diante dos seus próprios olhos e as darei a outro; e ele se deitará com elas em plena luz do dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8299, 'nvi', '2 Samuel', '2sm', 12, 12, 'Você fez isso às escondidas, mas eu o farei diante de todo o Israel, em plena luz do dia’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8300, 'nvi', '2 Samuel', '2sm', 12, 13, 'Então Davi disse a Natã: "Pequei contra o Senhor! " E Natã respondeu: "O Senhor perdoou o seu pecado. Você não morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8301, 'nvi', '2 Samuel', '2sm', 12, 14, 'Entretanto, uma vez que você insultou o Senhor, o menino morrerá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8302, 'nvi', '2 Samuel', '2sm', 12, 15, 'Depois que Natã foi para casa, o Senhor fez adoecer o filho que a mulher de Urias dera a Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8303, 'nvi', '2 Samuel', '2sm', 12, 16, 'E Davi implorou a Deus em favor da criança. Ele jejuou e, entrando em casa, passou a noite deitado no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8304, 'nvi', '2 Samuel', '2sm', 12, 17, 'Os oficiais do palácio tentaram fazê-lo levantar-se do chão, mas ele não quis, e recusou comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8305, 'nvi', '2 Samuel', '2sm', 12, 18, 'Sete dias depois a criança morreu. Os conselheiros de Davi estavam com medo de dizer-lhe que a criança estava morta, e comentavam: "Enquanto a criança ainda estava viva, falamos com ele, e ele não quis escutar-nos. Como vamos dizer-lhe que a criança morreu? Ele poderá cometer alguma loucura! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8306, 'nvi', '2 Samuel', '2sm', 12, 19, 'Davi, percebendo que seus conselheiros cochichavam entre si, compreendeu que a criança estava morta e perguntou: "A criança morreu? " "Sim, morreu", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8307, 'nvi', '2 Samuel', '2sm', 12, 20, 'Então Davi levantou-se do chão, lavou-se, perfumou-se e trocou de roupa. Depois entrou no santuário do Senhor e adorou. E voltando ao palácio, pediu que lhe preparassem uma refeição e comeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8308, 'nvi', '2 Samuel', '2sm', 12, 21, 'Seus conselheiros lhe perguntaram: "Por que ages assim? Enquanto a criança estava viva, jejuaste e choraste; mas, agora que a criança está morta, te levantas e comes! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8309, 'nvi', '2 Samuel', '2sm', 12, 22, 'Ele respondeu: "Enquanto a criança ainda estava viva, jejuei e chorei. Eu pensava: ‘Quem sabe? Talvez o Senhor tenha misericórdia de mim e deixe a criança viver’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8310, 'nvi', '2 Samuel', '2sm', 12, 23, 'Mas agora que ela morreu, por que deveria jejuar? Poderia eu trazê-la de volta à vida? Eu irei até ela, mas ela não voltará para mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8311, 'nvi', '2 Samuel', '2sm', 12, 24, 'Depois Davi consolou sua mulher Bate-Seba e deitou-se com ela, e ela teve um menino, a quem Davi deu o nome de Salomão. E o Senhor o amou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8312, 'nvi', '2 Samuel', '2sm', 12, 25, 'e, por isso, enviou o profeta Natã para dizer a Davi que, o menino deveria chamar-se Jedidias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8313, 'nvi', '2 Samuel', '2sm', 12, 26, 'Enquanto isso, Joabe atacou Rabá dos amonitas e conquistou a fortaleza real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8314, 'nvi', '2 Samuel', '2sm', 12, 27, 'Então mandou mensageiros a Davi, dizendo: "Lutei contra Rabá e apoderei-me dos seus reservatórios de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8315, 'nvi', '2 Samuel', '2sm', 12, 28, 'Agora, convoca o restante do exército, cerca a cidade e conquista-a. Se não, eu terei a fama de havê-la conquistado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8316, 'nvi', '2 Samuel', '2sm', 12, 29, 'Então, Davi convocou todo o exército, foi a Rabá, atacou a cidade e a conquistou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8317, 'nvi', '2 Samuel', '2sm', 12, 30, 'A seguir tirou a coroa da cabeça de Milcom, uma coroa de ouro de trinta e cinco quilos; ornamentada com pedras preciosas. E ela foi colocada na cabeça de Davi. Ele levou uma grande quantidade de bens da cidade');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8318, 'nvi', '2 Samuel', '2sm', 12, 31, 'e levou também os seus habitantes, designando-lhes trabalhos com serras, picaretas e machados, além da fabricação de tijolos. Davi fez assim com todas as cidades amonitas. Depois voltou com todo o seu exército para Jerusalém.');
