INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8319, 'nvi', '2 Samuel', '2sm', 13, 1, 'Depois de algum tempo, Amnom, filho de Davi, apaixonou-se por Tamar; ela era muito bonita e era irmã de Absalão, outro filho de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8320, 'nvi', '2 Samuel', '2sm', 13, 2, 'Amnom ficou angustiado a ponto de adoecer por causa de sua meio-irmã Tamar, pois ela era virgem, e parecia-lhe impossível aproximar-se dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8321, 'nvi', '2 Samuel', '2sm', 13, 3, 'Amnom tinha um amigo muito astuto chamado Jonadabe, filho de Siméia, irmão de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8322, 'nvi', '2 Samuel', '2sm', 13, 4, 'Ele perguntou a Amnom: "Filho do rei, por que todo dia você está abatido? Quer me contar o que se passa? " Amnom lhe disse: "Estou apaixonado por Tamar, irmã de meu irmão Absalão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8323, 'nvi', '2 Samuel', '2sm', 13, 5, 'Então disse Jonadabe: "Vá para a cama e finja estar doente". "Quando seu pai vier visitá-lo, diga-lhe: Permite que minha irmã Tamar venha dar-me de comer. Gostaria que ela preparasse a comida aqui mesmo e me servisse. Assim poderei vê-la. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8324, 'nvi', '2 Samuel', '2sm', 13, 6, 'Amnom atendeu e deitou-se na cama, fingindo-se doente. Quando o rei foi visitá-lo, Amnom lhe disse: "Eu gostaria que minha irmã Tamar viesse e preparasse dois bolos aqui mesmo e me servisse".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8325, 'nvi', '2 Samuel', '2sm', 13, 7, 'Davi mandou dizer a Tamar no palácio: "Vá à casa de seu irmão Amnom e prepare algo para ele comer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8326, 'nvi', '2 Samuel', '2sm', 13, 8, 'Assim, Tamar foi à casa de seu irmão, que estava deitado. Ela amassou a farinha, preparou os bolos na presença dele e os assou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8327, 'nvi', '2 Samuel', '2sm', 13, 9, 'Depois pegou a assadeira e lhe serviu os bolos, mas ele não quis comer. Então Amnom deu ordem para que todos saíssem; depois que todos saíram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8328, 'nvi', '2 Samuel', '2sm', 13, 10, 'Amnom disse a Tamar: "Traga os bolos e sirva-me aqui no meu quarto". Tamar levou os bolos que havia preparado ao quarto de seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8329, 'nvi', '2 Samuel', '2sm', 13, 11, 'Mas quando ela se aproximou para servi-lo, ele a agarrou e disse: "Deite-se comigo, minha irmã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8330, 'nvi', '2 Samuel', '2sm', 13, 12, 'Mas ela lhe disse: "Não, meu irmão! Não me faça essa violência. Não se faz uma coisa dessas em Israel! Não cometa essa loucura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8331, 'nvi', '2 Samuel', '2sm', 13, 13, 'O que seria de mim? Como eu poderia livrar-me da minha desonra? E o que seria de você? Você cairia em desgraça em Israel. Fale com o rei; ele deixará que eu me case com você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8332, 'nvi', '2 Samuel', '2sm', 13, 14, 'Mas Amnom não quis ouvir e, sendo mais forte que ela, violentou-a.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8333, 'nvi', '2 Samuel', '2sm', 13, 15, 'Logo depois Amnom sentiu uma forte aversão por ela, mais forte que a paixão que sentira. E disse a ela: "Levante-se e saia! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8334, 'nvi', '2 Samuel', '2sm', 13, 16, 'Mas ela lhe disse: "Não, meu irmão, mandar-me embora seria pior do que o mal que você já me fez". Ele, porém, não quis ouvi-la,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8335, 'nvi', '2 Samuel', '2sm', 13, 17, 'e chamando seu servo, disse-lhe: "Ponha esta mulher para fora daqui e tranque a porta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8336, 'nvi', '2 Samuel', '2sm', 13, 18, 'Então o servo a pôs para fora e trancou a porta. Ela estava vestindo uma túnica longa, pois esse era o tipo de roupa que as filhas virgens do rei usavam desde a puberdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8337, 'nvi', '2 Samuel', '2sm', 13, 19, 'Tamar pôs cinza na cabeça, rasgou a túnica longa que estava usando e se pôs a caminho, com as mãos sobre a cabeça e chorando em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8338, 'nvi', '2 Samuel', '2sm', 13, 20, 'Absalão, seu irmão, lhe perguntou: "Seu irmão, Amnom, lhe fez algum mal? Agora, acalme-se, minha irmã; ele é seu irmão! Não se deixe dominar pela angústia". E Tamar, muito triste, ficou na casa de seu irmão Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8339, 'nvi', '2 Samuel', '2sm', 13, 21, 'Ao saber de tudo isso, o rei Davi ficou furioso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8340, 'nvi', '2 Samuel', '2sm', 13, 22, 'E Absalão não falou nada com Amnom, nem bem, nem mal, embora o odiasse por ter violentado sua irmã Tamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8341, 'nvi', '2 Samuel', '2sm', 13, 23, 'Dois anos depois, quando os tosquiadores de ovelhas de Absalão estavam em Baal-Hazor, perto da fronteira de Efraim, Absalão convidou todos os filhos do rei para se reunirem com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8342, 'nvi', '2 Samuel', '2sm', 13, 24, 'Absalão foi ao rei e lhe disse: "Eu, teu servo, estou tosquiando as ovelhas e gostaria que o rei e os seus conselheiros estivessem comigo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8343, 'nvi', '2 Samuel', '2sm', 13, 25, 'Respondeu o rei: "Não, meu filho. Não iremos todos, pois isso seria um peso para você". Embora Absalão insistisse, ele se recusou a ir, mas o abençoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8344, 'nvi', '2 Samuel', '2sm', 13, 26, 'Então Absalão lhe disse: "Se não queres ir, permite, por favor, que o meu irmão Amnom vá conosco". O rei perguntou: "Mas, por que ele iria com você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8345, 'nvi', '2 Samuel', '2sm', 13, 27, 'Mas Absalão insistiu tanto que o rei acabou deixando que Amnom e os seus outros filhos fossem com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8346, 'nvi', '2 Samuel', '2sm', 13, 28, 'Absalão ordenou aos seus homens: "Ouçam! Quando Amnom estiver embriagado de vinho e eu disser: ‘Matem Amnom! ’, vocês o matarão. Não tenham medo; eu assumo a responsabilidade. Sejam fortes e corajosos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8347, 'nvi', '2 Samuel', '2sm', 13, 29, 'Assim os homens de Absalão mataram Amnom, obedecendo às suas ordens. Então todos os filhos do rei montaram em suas mulas e fugiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8348, 'nvi', '2 Samuel', '2sm', 13, 30, 'Estando eles ainda a caminho, chegou a seguinte notícia ao rei: "Absalão matou todos os teus filhos; nenhum deles escapou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8349, 'nvi', '2 Samuel', '2sm', 13, 31, 'O rei levantou-se, rasgou as suas vestes, prostrou-se, e todos os conselheiros que estavam com ele também rasgaram as vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8350, 'nvi', '2 Samuel', '2sm', 13, 32, 'Mas, Jonadabe, filho de Siméia, irmão de Davi, disse: "Não pense o meu senhor que mataram todos os teus filhos. Somente Amnom foi morto. Essa era a intenção de Absalão desde o dia em que Amnom violentou Tamar, irmã dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8351, 'nvi', '2 Samuel', '2sm', 13, 33, 'O rei, meu senhor, não deve acreditar que todos os seus filhos estão mortos. Apenas Amnom morreu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8352, 'nvi', '2 Samuel', '2sm', 13, 34, 'Enquanto isso, Absalão fugiu. Nesse meio tempo o sentinela viu muita gente que vinha pela estrada de Horonaim, descendo pela encosta da colina, e disse ao rei: "Vejo homens vindo pela estrada de Horonaim, na encosta da colina".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8353, 'nvi', '2 Samuel', '2sm', 13, 35, 'E Jonadabe disse ao rei: "São os filhos do rei! Aconteceu como o teu servo disse".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8354, 'nvi', '2 Samuel', '2sm', 13, 36, 'Acabando de falar, os filhos do rei chegaram, chorando em alta voz. Também o rei e todos os seus conselheiros choraram muito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8355, 'nvi', '2 Samuel', '2sm', 13, 37, 'Absalão fugiu para o território de Talmai, filho de Amiúde, rei de Gesur. E o rei Davi pranteava por seu filho todos os dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8356, 'nvi', '2 Samuel', '2sm', 13, 38, 'Depois que Absalão fugiu para Gesur, e lá permaneceu três anos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8357, 'nvi', '2 Samuel', '2sm', 13, 39, 'a ira do rei contra Absalão cessou, pois ele se sentia consolado da morte de Amnom.');
