INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8391, 'nvi', '2 Samuel', '2sm', 15, 1, 'Algum tempo depois, Absalão adquiriu uma carruagem, cavalos e uma escolta de cinqüenta homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8392, 'nvi', '2 Samuel', '2sm', 15, 2, 'Ele se levantava cedo e ficava junto ao caminho que levava à porta da cidade. Sempre que alguém trazia uma causa para ser decidida pelo rei, Absalão o chamava e perguntava de que cidade vinha. A pessoa respondia que era de uma das tribos de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8393, 'nvi', '2 Samuel', '2sm', 15, 3, 'e Absalão dizia: "A sua causa é válida e legítima, mas não há nenhum representante do rei para ouvi-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8394, 'nvi', '2 Samuel', '2sm', 15, 4, 'E Absalão acrescentava: "Quem me dera ser designado juiz desta terra! Todos os que tivessem uma causa ou uma questão legal viriam a mim, e eu lhe faria justiça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8395, 'nvi', '2 Samuel', '2sm', 15, 5, 'E sempre que alguém se aproximava dele para prostrar-se em sinal de respeito, Absalão estendia a mão, abraçava-o e beijava-o.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8396, 'nvi', '2 Samuel', '2sm', 15, 6, 'Absalão agia assim com todos os israelitas que vinham pedir que o rei lhes fizesse justiça. Assim ele foi conquistando a lealdade dos homens de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8397, 'nvi', '2 Samuel', '2sm', 15, 7, 'Ao final de quatro anos, Absalão disse ao rei: "Deixa-me ir a Hebrom para cumprir um voto que fiz ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8398, 'nvi', '2 Samuel', '2sm', 15, 8, 'Quando o teu servo estava em Gesur, na Síria, fez este voto: Se o Senhor me permitir voltar a Jerusalém, prestarei culto a ele em Hebrom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8399, 'nvi', '2 Samuel', '2sm', 15, 9, '"Vá em paz! ", disse o rei. E ele foi para Hebrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8400, 'nvi', '2 Samuel', '2sm', 15, 10, 'Absalão enviou secretamente mensageiros a todas as tribos de Israel, dizendo: "Assim que vocês ouvirem o som das trombetas, digam: Absalão é rei em Hebrom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8401, 'nvi', '2 Samuel', '2sm', 15, 11, 'Absalão levou duzentos homens de Jerusalém. Eles tinham sido convidados e nada sabiam nem suspeitavam do que estava acontecendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8402, 'nvi', '2 Samuel', '2sm', 15, 12, 'Depois de oferecer sacrifícios, Absalão mandou chamar da cidade de Gilo Aitofel, que era de Gilo, conselheiro de Davi. A conspiração ganhou força, e cresceu o número dos que seguiam Absalão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8403, 'nvi', '2 Samuel', '2sm', 15, 13, 'Então um mensageiro chegou e disse a Davi: "Os israelitas estão com Absalão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8404, 'nvi', '2 Samuel', '2sm', 15, 14, 'Então Davi disse aos conselheiros que estavam com ele em Jerusalém: "Vamos fugir; caso contrário não escaparemos de Absalão. Se não partirmos imediatamente ele nos alcançará, causará a nossa ruína e matará o povo à espada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8405, 'nvi', '2 Samuel', '2sm', 15, 15, 'Os conselheiros do rei lhe responderam: "Teus servos estão dispostos a fazer tudo o que o rei, nosso senhor, decidir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8406, 'nvi', '2 Samuel', '2sm', 15, 16, 'O rei partiu, seguido por todos os de sua família; deixou, porém, dez concubinas para tomarem conta do palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8407, 'nvi', '2 Samuel', '2sm', 15, 17, 'Assim, o rei partiu com todo o povo. Pararam na última casa da cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8408, 'nvi', '2 Samuel', '2sm', 15, 18, 'e todos os seus soldados marcharam, passando por ele: todos os queretitas e peletitas, e os seiscentos giteus que o acompanhavam desde Gate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8409, 'nvi', '2 Samuel', '2sm', 15, 19, 'O rei disse então a Itai, de Gate: "Por que você está indo conosco? Volte e fique com o novo rei, pois você é estrangeiro, um exilado de sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8410, 'nvi', '2 Samuel', '2sm', 15, 20, 'Faz pouco tempo que você chegou. Como eu poderia fazê-lo acompanhar-me? Volte e leve consigo os seus irmãos. Que o Senhor o trate com bondade e fidelidade! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8411, 'nvi', '2 Samuel', '2sm', 15, 21, 'Itai, contudo, respondeu ao rei: "Juro pelo nome do Senhor e por tua vida que onde quer que o rei, meu senhor, esteja, ali estará o seu servo, para viver ou para morrer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8412, 'nvi', '2 Samuel', '2sm', 15, 22, 'Então Davi disse a Itai: "Está bem, pode ir adiante". E Itai, o giteu, marchou, com todos os seus soldados e com as famílias que estavam com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8413, 'nvi', '2 Samuel', '2sm', 15, 23, 'Todo o povo do lugar chorava em alta voz enquanto o exército passava. O rei atravessou o vale do Cedrom e todo o povo foi com ele em direção ao deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8414, 'nvi', '2 Samuel', '2sm', 15, 24, 'Zadoque também estava lá, e com ele todos os levitas que carregavam a arca da aliança de Deus; Abiatar também estava lá. Puseram no chão a arca de Deus até que todo o povo saísse da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8415, 'nvi', '2 Samuel', '2sm', 15, 25, 'Então o rei disse a Zadoque: "Leve a arca de Deus de volta para a cidade. Se o Senhor mostrar benevolência a mim, ele me trará de volta e me deixará ver a arca e o lugar onde ela deve permanecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8416, 'nvi', '2 Samuel', '2sm', 15, 26, 'Mas, se ele disser que já não sou do seu agrado, aqui estou! Faça ele comigo o que for de sua vontade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8417, 'nvi', '2 Samuel', '2sm', 15, 27, 'Disse ainda o rei ao sacerdote Zadoque: "Fique alerta! Volte em paz para a cidade, você, Aimaás, seu filho, e Jônatas, filho de Abiatar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8418, 'nvi', '2 Samuel', '2sm', 15, 28, 'Junto às torrentes do deserto ficarei esperando notícias de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8419, 'nvi', '2 Samuel', '2sm', 15, 29, 'Então Zadoque e Abiatar levaram a arca de Deus de volta para Jerusalém, e lá permaneceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8420, 'nvi', '2 Samuel', '2sm', 15, 30, 'Davi, porém, continuou subindo o monte das Oliveiras, caminhando e chorando, e com a cabeça coberta e os pés descalços. E todos os que iam com ele também tinham a cabeça coberta e subiam chorando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8421, 'nvi', '2 Samuel', '2sm', 15, 31, 'Quando informaram a Davi que Aitofel era um dos conspiradores que apoiavam Absalão, Davi orou: "Ó Senhor, transforma em loucura os conselhos de Aitofel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8422, 'nvi', '2 Samuel', '2sm', 15, 32, 'Quando Davi chegou ao alto do monte, ao lugar onde o povo costumava adorar a Deus, veio ao seu encontro o arquita Husai, com a roupa rasgada e com terra sobre a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8423, 'nvi', '2 Samuel', '2sm', 15, 33, 'E Davi lhe disse: "Não adianta você vir comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8424, 'nvi', '2 Samuel', '2sm', 15, 34, 'Mas se voltar à cidade, poderá dizer a Absalão: ‘Estarei a teu serviço, ó rei. No passado estive a serviço de teu pai, mas agora estarei a teu serviço’. Assim você me ajudará, frustrando o conselho de Aitofel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8425, 'nvi', '2 Samuel', '2sm', 15, 35, 'Os sacerdotes Zadoque e Abiatar estarão lá com você. Informe-os do que você souber no palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8426, 'nvi', '2 Samuel', '2sm', 15, 36, 'Também estão lá os dois filhos deles: Aimaás e Jônatas. Por meio deles me informe de tudo que você ouvir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8427, 'nvi', '2 Samuel', '2sm', 15, 37, 'Então Husai, amigo de Davi, chegou a Jerusalém quando Absalão estava entrando na cidade.');
