INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8428, 'nvi', '2 Samuel', '2sm', 16, 1, 'Mal Davi tinha passado pelo alto do monte, lá estava à sua espera Ziba, criado de Mefibosete. Ele trazia dois jumentos carregando duzentos pães, cem bolos de uvas passas, cem frutas da estação e uma vasilha de couro cheia de vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8429, 'nvi', '2 Samuel', '2sm', 16, 2, 'O rei perguntou a Ziba: "Por que você trouxe essas coisas? " Ziba respondeu: "Os jumentos servirão de montaria para a família do rei, os pães e as frutas são para os homens comerem, e o vinho servirá para reanimar os que ficarem exaustos no deserto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8430, 'nvi', '2 Samuel', '2sm', 16, 3, 'O rei então perguntou: "Onde está Mefibosete, neto de seu senhor? " Respondeu-lhe Ziba: "Ele ficou em Jerusalém, pois acredita que os israelitas lhe restituirão o reino de seu avô ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8431, 'nvi', '2 Samuel', '2sm', 16, 4, 'Então o rei disse a Ziba: "Tudo o que pertencia a Mefibosete agora é seu". "Humildemente me prostro", disse Ziba. "Que o rei, meu senhor, agrade-se de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8432, 'nvi', '2 Samuel', '2sm', 16, 5, 'Chegando o rei Davi a Baurim, um homem do clã da família de Saul chamado Simei, filho de Gera, saiu da cidade proferindo maldições contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8433, 'nvi', '2 Samuel', '2sm', 16, 6, 'Ele atirava pedras em Davi e em todos os conselheiros do rei, embora todo o exército e a guarda de elite estivessem à direita e à esquerda de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8434, 'nvi', '2 Samuel', '2sm', 16, 7, 'Enquanto amaldiçoava, Simei dizia: "Saia daqui, saia daqui! Assassino! Bandido!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8435, 'nvi', '2 Samuel', '2sm', 16, 8, 'O Senhor retribuiu a você todo o sangue derramado na família de Saul, em cujo lugar você reinou. O Senhor entregou o reino nas mãos de seu filho Absalão. Você está arruinado porque é um assassino! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8436, 'nvi', '2 Samuel', '2sm', 16, 9, 'Então Abisai, filho de Zeruia, disse ao rei: "Por que esse cão morto amaldiçoa o rei meu senhor? Permite que eu lhe corte a cabeça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8437, 'nvi', '2 Samuel', '2sm', 16, 10, 'Mas o rei disse: "Que é que vocês têm com isso, filhos de Zeruia? Ele me amaldiçoa porque o Senhor lhe disse que amaldiçoasse Davi. Portanto, quem poderá questioná-lo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8438, 'nvi', '2 Samuel', '2sm', 16, 11, 'Disse então Davi a Abisai e a todos os seus conselheiros: "Até meu filho, sangue do meu sangue, procura matar-me. Quanto mais este benjamita! Deixem-no em paz! Que amaldiçoe, pois foi o que o Senhor lhe mandou fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8439, 'nvi', '2 Samuel', '2sm', 16, 12, 'Talvez o Senhor considere a minha aflição e me retribua com o bem a maldição que hoje recebo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8440, 'nvi', '2 Samuel', '2sm', 16, 13, 'Assim, Davi e os seus soldados prosseguiram pela estrada, enquanto Simei ia pela encosta do monte, no lado oposto, amaldiçoando, jogando pedras e terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8441, 'nvi', '2 Samuel', '2sm', 16, 14, 'O rei e todo o povo que estava com ele chegaram exaustos a seu destino. E lá descansaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8442, 'nvi', '2 Samuel', '2sm', 16, 15, 'Enquanto isso, Absalão e todos os homens de Israel entraram em Jerusalém, e Aitofel estava com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8443, 'nvi', '2 Samuel', '2sm', 16, 16, 'Então Husai, o arquita, amigo de Davi, aproximou-se de Absalão e exclamou: "Viva o rei! Viva o rei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8444, 'nvi', '2 Samuel', '2sm', 16, 17, 'Mas Absalão disse a Husai: "É essa a lealdade que você tem para com o seu amigo? Por que você não foi com ele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8445, 'nvi', '2 Samuel', '2sm', 16, 18, 'Respondeu Husai: "Não! Sou do escolhido do Senhor, deste povo e de todos os israelitas; e com ele permanecerei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8446, 'nvi', '2 Samuel', '2sm', 16, 19, 'Além disso, a quem devo servir? Não deveria eu servir o filho? Assim como servi a teu pai, também te servirei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8447, 'nvi', '2 Samuel', '2sm', 16, 20, 'Então Absalão disse a Aitofel: "Dê-nos o seu conselho. Que devemos fazer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8448, 'nvi', '2 Samuel', '2sm', 16, 21, 'Aitofel respondeu: "Tenha relações com as concubinas de teu pai, que ele deixou para tomar conta do palácio. Então todo o Israel ficará sabendo que te tornaste repugnante para teu pai, e todos os que estão contigo se encherão de coragem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8449, 'nvi', '2 Samuel', '2sm', 16, 22, 'E assim armaram uma tenda no terraço do palácio para Absalão, e ele teve relações com as concubinas de seu pai à vista de todo o Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8450, 'nvi', '2 Samuel', '2sm', 16, 23, 'Naquela época, tanto Davi como Absalão consideravam os conselhos de Aitofel como se fossem a palavra do próprio Deus.');
