INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8051, 'nvi', '2 Samuel', '2sm', 2, 1, 'Passado algum tempo, Davi perguntou ao Senhor: "Devo ir para uma das cidades de Judá? " O Senhor respondeu que sim, e Davi perguntou para qual delas. "Para Hebrom", respondeu o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8052, 'nvi', '2 Samuel', '2sm', 2, 2, 'Então Davi foi para Hebrom com suas duas mulheres, Ainoã, de Jezreel e Abigail, viúva de Nabal, o carmelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8053, 'nvi', '2 Samuel', '2sm', 2, 3, 'Davi também levou os homens que o acompanhavam, cada um com sua família, e estabeleceram-se em Hebrom e nos povoados vizinhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8054, 'nvi', '2 Samuel', '2sm', 2, 4, 'Então os homens de Judá foram a Hebrom e ali ungiram Davi rei da tribo de Judá. Informado de que os habitantes de Jabes-Gileade tinham sepultado Saul,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8055, 'nvi', '2 Samuel', '2sm', 2, 5, 'Davi enviou-lhes mensageiros que lhes disseram: "O Senhor os abençoe pelo seu ato de lealdade, ao sepultar Saul, seu rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8056, 'nvi', '2 Samuel', '2sm', 2, 6, 'Seja o Senhor leal e fiel para com vocês. Também eu firmarei minha amizade com vocês, por terem feito essa boa ação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8057, 'nvi', '2 Samuel', '2sm', 2, 7, 'Mas, agora, sejam fortes e corajosos, pois Saul, seu senhor, está morto, e já fui ungido rei pela tribo de Judá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8058, 'nvi', '2 Samuel', '2sm', 2, 8, 'Enquanto isso, Abner, filho de Ner, comandante do exército de Saul, levou Is-Bosete, filho de Saul, a Maanaim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8059, 'nvi', '2 Samuel', '2sm', 2, 9, 'onde o proclamou rei sobre Gileade, Assuri, Jezreel, Efraim, Benjamim e sobre todo o Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8060, 'nvi', '2 Samuel', '2sm', 2, 10, 'Is-Bosete, filho de Saul, tinha quarenta anos de idade quando começou a reinar em Israel, e reinou dois anos. A tribo de Judá, entretanto, seguia Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8061, 'nvi', '2 Samuel', '2sm', 2, 11, 'que a governou por sete anos e seis meses, em Hebrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8062, 'nvi', '2 Samuel', '2sm', 2, 12, 'Abner, filho de Ner, e os soldados de Is-Bosete, filho de Saul, partiram de Maanaim e marcharam para Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8063, 'nvi', '2 Samuel', '2sm', 2, 13, 'Joabe, filho de Zeruia, e os soldados de Davi foram ao encontro deles no açude de Gibeom. Um grupo posicionou-se num lado do açude, o outro grupo, no lado oposto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8064, 'nvi', '2 Samuel', '2sm', 2, 14, 'Então Abner disse a Joabe: "Vamos fazer alguns soldados lutarem diante de nós". Joabe respondeu: "De acordo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8065, 'nvi', '2 Samuel', '2sm', 2, 15, 'Então doze soldados aliados de Benjamim e Is-Bosete, filho de Saul, atravessaram o açude para enfrentar doze dos soldados aliados de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8066, 'nvi', '2 Samuel', '2sm', 2, 16, 'Cada soldado pegou o adversário pela cabeça e fincou-lhe o punhal no lado, e juntos caíram mortos. Por isso aquele lugar, situado em Gibeom, foi chamado Helcate-Hazurim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8067, 'nvi', '2 Samuel', '2sm', 2, 17, 'Houve uma violenta batalha naquele dia, e Abner e os soldados de Israel foram derrotados pelos soldados de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8068, 'nvi', '2 Samuel', '2sm', 2, 18, 'Estavam lá Joabe, Abisai e Asael, os três filhos de Zeruia. E Asael, que corria como uma gazela em terreno plano,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8069, 'nvi', '2 Samuel', '2sm', 2, 19, 'perseguiu Abner, sem se desviar nem para a direita nem para a esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8070, 'nvi', '2 Samuel', '2sm', 2, 20, 'Abner olhou para trás e perguntou: "É você, Asael? " "Sou eu", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8071, 'nvi', '2 Samuel', '2sm', 2, 21, 'Disse-lhe então Abner: "É melhor você sair para a direita ou para a esquerda, capturar um dos soldados e ficar com as armas dele". Mas Asael não quis parar de persegui-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8072, 'nvi', '2 Samuel', '2sm', 2, 22, 'Então Abner advertiu Asael mais uma vez: "Pare de me perseguir! Não quero matá-lo. Como poderia olhar seu irmão Joabe nos olhos de novo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8073, 'nvi', '2 Samuel', '2sm', 2, 23, 'Como, porém, Asael, não desistiu de persegui-lo, Abner cravou no estômago dele a ponta da lança, que saiu pelas costas. E ele caiu, morrendo ali mesmo. E paravam todos os que chegavam ao lugar onde Asael estava caído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8074, 'nvi', '2 Samuel', '2sm', 2, 24, 'Então Joabe e Abisai perseguiram Abner. Ao pôr-do-sol, chegaram à colina de Amá, defronte de Gia, no caminho para o deserto de Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8075, 'nvi', '2 Samuel', '2sm', 2, 25, 'Os soldados de Benjamim, seguindo Abner, reuniram-se formando um só grupo e ocuparam o alto de uma colina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8076, 'nvi', '2 Samuel', '2sm', 2, 26, 'Então Abner gritou para Joabe: "O derramamento de sangue vai continuar? Não vê que isso vai trazer amargura? Quando é que você vai mandar o seu exército parar de perseguir os seus irmãos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8077, 'nvi', '2 Samuel', '2sm', 2, 27, 'Respondeu Joabe: "Juro pelo nome de Deus, que se você não tivesse falado, o meu exército perseguiria os seus irmãos até de manhã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8078, 'nvi', '2 Samuel', '2sm', 2, 28, 'Então Joabe tocou a trombeta, e o exército parou de perseguir Israel e de lutar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8079, 'nvi', '2 Samuel', '2sm', 2, 29, 'Abner e seus soldados marcharam pela Arabá durante toda a noite. Atravessaram o Jordão, marcharam durante a manhã inteira e chegaram a Maanaim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8080, 'nvi', '2 Samuel', '2sm', 2, 30, 'Quando Joabe voltou da perseguição a Abner, reuniu todo o exército. E viram que faltavam dezenove soldados, além de Asael.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8081, 'nvi', '2 Samuel', '2sm', 2, 31, 'Mas os soldados de Davi tinham matado trezentos e sessenta benjamitas que estavam com Abner.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8082, 'nvi', '2 Samuel', '2sm', 2, 32, 'Levaram Asael e o sepultaram no túmulo de seu pai, em Belém. Depois disso, Joabe e seus soldados marcharam durante toda a noite e chegaram a Hebrom ao amanhecer.');
