INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8556, 'nvi', '2 Samuel', '2sm', 20, 1, 'Também estava lá um desordeiro chamado Seba, filho de Bicri, de Benjamim. Ele tocou a trombeta e gritou: "Não temos parte alguma com Davi, nenhuma herança com o filho de Jessé! Para casa todos, ó Israel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8557, 'nvi', '2 Samuel', '2sm', 20, 2, 'Então todos os de Israel abandonaram Davi para seguir Seba, filho de Bicri. Mas os de Judá permaneceram com seu rei e o acompanharam desde o Jordão até Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8558, 'nvi', '2 Samuel', '2sm', 20, 3, 'Quando Davi voltou ao palácio, em Jerusalém, mandou confinar numa casa, sob guarda, as dez concubinas que tinha deixado tomando conta do palácio. Ele as sustentou, mas nunca mais as possuiu. Ficaram confinadas, vivendo como viúvas até à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8559, 'nvi', '2 Samuel', '2sm', 20, 4, 'E o rei disse a Amasa: "Convoque os homens de Judá, e dentro de três dias apresente-se aqui com eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8560, 'nvi', '2 Samuel', '2sm', 20, 5, 'Mas Amasa levou mais tempo para convocar Judá do que o prazo estabelecido pelo rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8561, 'nvi', '2 Samuel', '2sm', 20, 6, 'Disse então Davi a Abisai: "Agora Seba, filho de Bicri, será pior para nós do que Absalão. Chame os meus soldados e persiga-o, antes que ele encontre alguma cidade fortificada e, depois, nos arranque os olhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8562, 'nvi', '2 Samuel', '2sm', 20, 7, 'Assim, os soldados de Joabe, os queretitas, os peletitas e todos os guerreiros saíram de Jerusalém para perseguir Seba, filho de Bicri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8563, 'nvi', '2 Samuel', '2sm', 20, 8, 'Quando estavam junto à grande rocha de Gibeom, Amasa encontrou-se com eles. Joabe vestia seu traje militar e tinha um cinto com um punhal na bainha. Ao aproximar-se de Amasa, deixou cair a adaga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8564, 'nvi', '2 Samuel', '2sm', 20, 9, '"Como vai, meu irmão? ", disse Joabe, pegando Amasa pela barba com a mão direita, para beijá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8565, 'nvi', '2 Samuel', '2sm', 20, 10, 'E Amasa, não percebendo o punhal na mão esquerda de Joabe, foi por ele golpeado no estômago. Suas entranhas se derramaram no chão, e ele morreu, sem necessidade de um segundo golpe. Então Joabe e, Abisai, seu irmão, perseguiram Seba, filho de Bicri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8566, 'nvi', '2 Samuel', '2sm', 20, 11, 'Um dos soldados de Joabe ficou ao lado do corpo de Amasa e disse: "Quem estiver do lado de Joabe e de Davi, que siga a Joabe! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8567, 'nvi', '2 Samuel', '2sm', 20, 12, 'Amasa jazia numa poça de sangue no meio da estrada. Quando o homem viu que todos os que se aproximavam do corpo de Amasa paravam, arrastou-o para fora da estrada e o cobriu com uma coberta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8568, 'nvi', '2 Samuel', '2sm', 20, 13, 'Depois que o corpo de Amasa foi retirado da estrada, todos os homens seguiram com Joabe em perseguição a Seba, filho de Bicri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8569, 'nvi', '2 Samuel', '2sm', 20, 14, 'Seba atravessou todas as tribos de Israel e chegou até Abel-Bete-Maaca, e todos os bicritas se reuniram para segui-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8570, 'nvi', '2 Samuel', '2sm', 20, 15, 'O exército de Joabe veio e cercou Seba em Abel-Bete-Maaca. Eles construíram contra a cidade uma rampa que chegou até à muralha externa. Quando o exército de Joabe estava para derrubar a muralha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8571, 'nvi', '2 Samuel', '2sm', 20, 16, 'uma mulher sábia gritou da cidade: "Ouçam! Ouçam! Digam a Joabe que venha aqui para que eu fale com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8572, 'nvi', '2 Samuel', '2sm', 20, 17, 'Quando ele se aproximou a mulher perguntou: "Tu és Joabe? " Ele respondeu: "Sim". Ela disse: "Ouve o que a tua serva tem para dizer-te". "Estou ouvindo", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8573, 'nvi', '2 Samuel', '2sm', 20, 18, 'E ela prosseguiu: "Antigamente se dizia: ‘Peça conselho na cidade de Abel’, e isso resolvia a questão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8574, 'nvi', '2 Samuel', '2sm', 20, 19, 'Nós somos pacíficos e fiéis em Israel. Tu procuras destruir uma cidade que é mãe em Israel. Por que queres arruinar a herança do Senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8575, 'nvi', '2 Samuel', '2sm', 20, 20, 'Respondeu Joabe: "Longe de mim uma coisa dessas! Longe de mim arruinar e destruir esta cidade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8576, 'nvi', '2 Samuel', '2sm', 20, 21, 'Não é esse o problema. Mas um homem chamado Seba, filho de Bicri, dos montes de Efraim, rebelou-se contra o rei Davi. Entreguem-me esse homem, e iremos embora". A mulher disse a Joabe: "A cabeça dele te será jogada do alto da muralha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8577, 'nvi', '2 Samuel', '2sm', 20, 22, 'Então a mulher foi falar com todo o povo, dando o seu sábio conselho, e eles cortaram a cabeça de Seba, filho de Bicri, e a jogaram para Joabe. Ele tocou a trombeta, e seus homens se dispersaram, abandonaram o cerco da cidade e cada um voltou para sua casa. E Joabe voltou ao rei, em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8578, 'nvi', '2 Samuel', '2sm', 20, 23, 'Joabe comandava todo o exército de Israel; Benaia, filho de Joiada, comandava os queretitas e os peletitas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8579, 'nvi', '2 Samuel', '2sm', 20, 24, 'Adonirão era responsável pelos trabalhos forçados; Josafá, filho de Ailude, era arquivista real;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8580, 'nvi', '2 Samuel', '2sm', 20, 25, 'Seva era secretário; Zadoque e Abiatar eram sacerdotes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8581, 'nvi', '2 Samuel', '2sm', 20, 26, 'e Ira, de Jair, era sacerdote de Davi.');
