INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8083, 'nvi', '2 Samuel', '2sm', 3, 1, 'A guerra entre as famílias de Saul e Davi durou muito tempo. Davi tornava-se cada vez mais forte, enquanto que a família de Saul se enfraquecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8084, 'nvi', '2 Samuel', '2sm', 3, 2, 'Estes foram os filhos de Davi nascidos em Hebrom: O seu filho mais velho era Amnom, filho de Ainoã, de Jezreel;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8085, 'nvi', '2 Samuel', '2sm', 3, 3, 'o segundo, Quileabe, de Abigail, viúva de Nabal, de Carmelo; o terceiro, Absalão, de Maaca, filha de Talmai, rei de Gesur;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8086, 'nvi', '2 Samuel', '2sm', 3, 4, 'o quarto, Adonias, de Hagite; o quinto, Sefatias, de Abital;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8087, 'nvi', '2 Samuel', '2sm', 3, 5, 'e o sexto, Itreão, de sua mulher Eglá. Esses foram os filhos de Davi que lhe nasceram em Hebrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8088, 'nvi', '2 Samuel', '2sm', 3, 6, 'Enquanto transcorria a guerra entre as famílias de Saul e de Davi, Abner foi se tornando poderoso na família de Saul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8089, 'nvi', '2 Samuel', '2sm', 3, 7, 'Saul tivera uma concubina chamada Rispa, filha de Aiá. Certa vez Is-Bosete perguntou a Abner: "Por que você se deitou com a concubina de meu pai? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8090, 'nvi', '2 Samuel', '2sm', 3, 8, 'Abner ficou furioso com a pergunta de Is-Bosete e exclamou: "Por acaso eu sou um cão a serviço de Judá? Até agora tenho sido leal à família de Saul, seu pai, e aos parentes e amigos dele, e não deixei que você caísse nas mãos de Davi; e agora você me acusa de um delito envolvendo esta mulher!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8091, 'nvi', '2 Samuel', '2sm', 3, 9, 'Que Deus me castigue com todo rigor, se eu não fizer por Davi o que o Senhor lhe prometeu sob juramento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8092, 'nvi', '2 Samuel', '2sm', 3, 10, 'tirar o reino da família de Saul e estabelecer o trono de Davi sobre Israel e Judá, de Dã a Berseba".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8093, 'nvi', '2 Samuel', '2sm', 3, 11, 'Is-Bosete não respondeu nada a Abner, pois tinha medo dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8094, 'nvi', '2 Samuel', '2sm', 3, 12, 'Então Abner enviou mensageiros a Davi com esta proposta: "A quem pertence esta terra? Faze um acordo comigo e eu te ajudarei a conseguir o apoio de todo o Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8095, 'nvi', '2 Samuel', '2sm', 3, 13, '"Está bem", disse Davi. "Farei um acordo com você, mas com uma condição: não compareça à minha presença sem trazer-me Mical, filha de Saul, quando você vier me ver. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8096, 'nvi', '2 Samuel', '2sm', 3, 14, 'E Davi enviou mensageiros a Is-Bosete, filho de Saul, exigindo: "Entregue-me minha mulher Mical, com quem me casei pelo preço de cem prepúcios de filisteus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8097, 'nvi', '2 Samuel', '2sm', 3, 15, 'Diante disso, Is-Bosete mandou que a tirassem do seu marido Paltiel, filho de Laís.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8098, 'nvi', '2 Samuel', '2sm', 3, 16, 'Mas Paltiel foi atrás dela, e a seguiu chorando até Baurim. Então Abner ordenou-lhe que voltasse para casa; e ele voltou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8099, 'nvi', '2 Samuel', '2sm', 3, 17, 'Nesse meio tempo, Abner enviou esta mensagem às autoridades de Israel: "Já faz algum tempo que vocês querem Davi como rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8100, 'nvi', '2 Samuel', '2sm', 3, 18, 'Agora é o momento de agir! Porque o Senhor prometeu a Davi: ‘Por meio de Davi, meu servo, livrarei Israel do poder dos filisteus e de todos os seus inimigos’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8101, 'nvi', '2 Samuel', '2sm', 3, 19, 'Abner também falou pessoalmente com os benjamitas. Depois foi a Hebrom dizer a Davi tudo o que Israel e a tribo de Benjamim haviam aprovado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8102, 'nvi', '2 Samuel', '2sm', 3, 20, 'Quando Abner, acompanhado de vinte homens, apresentou-se a Davi em Hebrom, este ofereceu um banquete para ele e para os homens que o acompanhavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8103, 'nvi', '2 Samuel', '2sm', 3, 21, 'Disse então Abner a Davi: "Deixa que eu me vá e reúna todo o Israel, meu senhor, para que façam um acordo contigo, ó rei, e reines sobre tudo o que desejares". Davi o deixou ir, e ele se foi em paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8104, 'nvi', '2 Samuel', '2sm', 3, 22, 'Naquele momento, os soldados de Davi e Joabe voltavam de um ataque, trazendo muitos bens. Abner, porém, já não estava com Davi em Hebrom, porque Davi o tinha deixado partir em paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8105, 'nvi', '2 Samuel', '2sm', 3, 23, 'Quando Joabe chegou com todo o seu exército, contaram-lhe que Abner, filho de Ner, se apresentara ao rei, que o tinha deixado ir em paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8106, 'nvi', '2 Samuel', '2sm', 3, 24, 'Então Joabe foi falar com o rei e lhe disse: "Que foi que fizeste? Abner veio à tua presença e o deixaste ir?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8107, 'nvi', '2 Samuel', '2sm', 3, 25, 'Conheces Abner, filho de Ner; ele veio para enganá-lo, observar os teus movimentos e descobrir tudo o que estás fazendo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8108, 'nvi', '2 Samuel', '2sm', 3, 26, 'Saindo da presença de Davi, Joabe enviou mensageiros atrás de Abner, e eles o trouxeram de volta, desde a cisterna de Sirá. Mas Davi não ficou sabendo disso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8109, 'nvi', '2 Samuel', '2sm', 3, 27, 'Quando Abner retornou a Hebrom, Joabe o chamou à parte, na porta da cidade, sob o pretexto de falar-lhe em particular, e ali mesmo o feriu no estômago. E Abner morreu por ter derramado o sangue de Asael, irmão de Joabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8110, 'nvi', '2 Samuel', '2sm', 3, 28, 'Mais tarde, quando Davi soube o que tinha acontecido, disse: "Eu e o meu reino, perante o Senhor, somos para sempre inocentes do sangue de Abner, filho de Ner.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8111, 'nvi', '2 Samuel', '2sm', 3, 29, 'Caia a responsabilidade pela morte dele sobre a cabeça de Joabe e de toda a sua família! Jamais falte entre os seus descendentes quem sofra fluxo ou lepra, quem use muletas, quem morra pela espada, ou quem passe fome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8112, 'nvi', '2 Samuel', '2sm', 3, 30, 'Assim, Joabe e seu irmão Abisai mataram Abner porque ele havia matado Asael, irmão deles, na batalha de Gibeom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8113, 'nvi', '2 Samuel', '2sm', 3, 31, 'Então Davi disse a Joabe e a todo o exército que o acompanhava: "Rasguem suas vestes, vistam roupas de luto e vão chorando à frente de Abner". E o rei Davi seguiu atrás da maca que levava o corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8114, 'nvi', '2 Samuel', '2sm', 3, 32, 'Enterraram-no em Hebrom, e o rei chorou em alta voz junto ao túmulo de Abner, como também todo o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8115, 'nvi', '2 Samuel', '2sm', 3, 33, 'Então o rei cantou este lamento por Abner: "Por que morreu Abner como morrem os insensatos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8116, 'nvi', '2 Samuel', '2sm', 3, 34, 'Suas mãos não estavam algemadas, nem seus pés acorrentados. Você caiu como quem cai perante homens perversos". E todo o povo chorou ainda mais por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8117, 'nvi', '2 Samuel', '2sm', 3, 35, 'Depois, quando o povo insistiu com Davi que comesse alguma coisa enquanto ainda era dia, Davi fez este juramento: "Deus me castigue com todo rigor, caso eu prove pão ou qualquer outra coisa antes do pôr-do-sol! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8118, 'nvi', '2 Samuel', '2sm', 3, 36, 'Todo o povo ouviu isso e aprovou; de fato, tudo o que o rei fazia o povo aprovava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8119, 'nvi', '2 Samuel', '2sm', 3, 37, 'Assim, naquele dia, todo o povo e todo o Israel reconheceram que o rei não tivera participação no assassinato de Abner, filho de Ner.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8120, 'nvi', '2 Samuel', '2sm', 3, 38, 'Então o rei disse aos seus conselheiros: "Não percebem que caiu hoje em Israel um líder, um grande homem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (8121, 'nvi', '2 Samuel', '2sm', 3, 39, 'Embora rei ungido, ainda sou fraco, e esses filhos de Zeruia são mais fortes do que eu. Que o Senhor retribua ao malfeitor de acordo com as suas más obras! "');
